INSERT INTO readings (reading_year, reading_month, reading_day, firstreading, firstreading_short, firstreading_link, secondreading, secondreading_short, secondreading_link, created_by) VALUES ('2024', '01', '1', '„Légy óvatos, maradj nyugton! Ne félj, ne lágyuljon meg a szíved […]!” (Ézs 7,4)', 'Ézs 7,4', 'https://szentiras.hu/RUF/Ézs 7,4', '„Az Úrnak Lelke van énrajtam, mert felkent engem, hogy […] hirdessem az Úr kedves esztendejét. Ma teljesedett be ez az írás a fületek hallatára.” (Lk 4,18–19.21)', 'Lk 4,18–19.21', 'https://szentiras.hu/RUF/Lk 4,18–19.21', 'admin');
INSERT INTO readings (reading_year, reading_month, reading_day, firstreading, firstreading_short, firstreading_link, secondreading, secondreading_short, secondreading_link, created_by) VALUES ('2024', '01', '2', '„Készségesen áldozok neked, magasztalom neved, Uram, mert jó vagy.” (Zsolt 54,8)', 'Zsolt 54,8', 'https://szentiras.hu/RUF/Zsolt 54,8', '„Egy szegény özvegyasszony pedig odamenve beledobott két fillért, azaz a legkisebb pénzt.” (Mk 12,42)', 'Mk 12,42', 'https://szentiras.hu/RUF/Mk 12,42', 'admin');
INSERT INTO readings (reading_year, reading_month, reading_day, firstreading, firstreading_short, firstreading_link, secondreading, secondreading_short, secondreading_link, created_by) VALUES ('2024', '01', '3', '„Bálám ezt felelte: Ha nekem adja Bálák a házát tele ezüsttel és arannyal, akkor sem szeghetem meg Istenemnek, az Úrnak a parancsát, és nem tehetek semmit vele szemben.” (4Móz 22,18)', '4Móz 22,18', 'https://szentiras.hu/RUF/4Móz 22,18', '„Istennek kell inkább engedelmeskednünk, mint az embereknek.” (ApCsel 5,29b)', 'ApCsel 5,29b', 'https://szentiras.hu/RUF/ApCsel 5,29b', 'admin');
INSERT INTO readings (reading_year, reading_month, reading_day, firstreading, firstreading_short, firstreading_link, secondreading, secondreading_short, secondreading_link, created_by) VALUES ('2024', '01', '4', '„Világosságodhoz népek jönnek, és királyok a rád ragyogó fényhez.” (Ézs 60,3)', 'Ézs 60,3', 'https://szentiras.hu/RUF/Ézs 60,3', '„És a városnak nincs szüksége a napra, sem a holdra, hogy világítsanak neki, mert az Isten dicsősége világosította meg, és lámpása a Bárány: a népek az ő világosságában fognak járni, és a föld királyai oda viszik be dicsőségüket.” (Jel 21,23–24)', 'Jel 21,23–24', 'https://szentiras.hu/RUF/Jel 21,23–24', 'admin');
INSERT INTO readings (reading_year, reading_month, reading_day, firstreading, firstreading_short, firstreading_link, secondreading, secondreading_short, secondreading_link, created_by) VALUES ('2024', '01', '5', '„Gyertek, menjünk el, könyörögjünk az Úrhoz, folyamodjunk a Seregek Urához! Én is megyek!” (Zak 8,21b)', 'Zak 8,21b', 'https://szentiras.hu/RUF/Zak 8,21b', '„Amikor pedig [Lídia] háza népével együtt megkeresztelkedett, azt kérte: Ha úgy látjátok, hogy az Úr híve vagyok, jöjjetek, és szálljatok meg a házamban!” (ApCsel 16,15a)', 'ApCsel 16,15a', 'https://szentiras.hu/RUF/ApCsel 16,15a', 'admin');
INSERT INTO readings (reading_year, reading_month, reading_day, firstreading, firstreading_short, firstreading_link, secondreading, secondreading_short, secondreading_link, created_by) VALUES ('2024', '01', '6', '„Azt az embert, aki féli az Urat, oktatja ő, hogy melyik utat válassza.” (Zsolt 25,12)', 'Zsolt 25,12', 'https://szentiras.hu/RUF/Zsolt 25,12', '„Mivel azonban [a bölcsek] kijelentést kaptak álomban, hogy ne menjenek vissza Heródeshez, más úton tértek vissza hazájukba.” (Mt 2,12)', 'Mt 2,12', 'https://szentiras.hu/RUF/Mt 2,12', 'admin');
INSERT INTO readings (reading_year, reading_month, reading_day, firstreading, firstreading_short, firstreading_link, secondreading, secondreading_short, secondreading_link, created_by) VALUES ('2024', '01', '7', '„Ne féljetek! Álljatok helyt, és meglátjátok, hogyan szabadít meg benneteket ma az Úr!” (2Móz 14,13a)', '2Móz 14,13a', 'https://szentiras.hu/RUF/2Móz 14,13a', '„Pál pedig így válaszolt: Kérem az Istentől, hogy előbb vagy utóbb nemcsak te, hanem azok is, akik ma hallgatnak engem, olyanná legyenek, amilyen én is vagyok e bilincsek nélkül.” (ApCsel 26,29)', 'ApCsel 26,29', 'https://szentiras.hu/RUF/ApCsel 26,29', 'admin');
INSERT INTO readings (reading_year, reading_month, reading_day, firstreading, firstreading_short, firstreading_link, secondreading, secondreading_short, secondreading_link, created_by) VALUES ('2024', '01', '8', '„Úgy örülnek színed előtt, ahogyan aratáskor szoktak örülni […].” (Ézs 9,2b)', 'Ézs 9,2b', 'https://szentiras.hu/RUF/Ézs 9,2b', '„Mert az a föld, amely beissza a gyakran ráhulló esőt, és hasznos növényt terem azoknak, akik számára művelik, áldást nyer Istentől.” (Zsid 6,7)', 'Zsid 6,7', 'https://szentiras.hu/RUF/Zsid 6,7', 'admin');
INSERT INTO readings (reading_year, reading_month, reading_day, firstreading, firstreading_short, firstreading_link, secondreading, secondreading_short, secondreading_link, created_by) VALUES ('2024', '01', '9', '„Megítéli az Úr az egész földet, de királyát megerősíti, felkentjének hatalmat ad.” (1Sám 2,10b)', '1Sám 2,10b', 'https://szentiras.hu/RUF/1Sám 2,10b', '„Íme, az Isten Báránya, aki elveszi a világ bűnét!” (Jn 1,29b)', 'Jn 1,29b', 'https://szentiras.hu/RUF/Jn 1,29b', 'admin');
INSERT INTO readings (reading_year, reading_month, reading_day, firstreading, firstreading_short, firstreading_link, secondreading, secondreading_short, secondreading_link, created_by) VALUES ('2024', '01', '10', '„Hagyj fel a rosszal, és cselekedj jót, törekedj békességre, és kövesd azt!” (Zsolt 34,15)', 'Zsolt 34,15', 'https://szentiras.hu/RUF/Zsolt 34,15', '„Arra törekedjünk tehát, ami a békességet és az épülést szolgálja.” (Róm 14,19)', 'Róm 14,19', 'https://szentiras.hu/RUF/Róm 14,19', 'admin');
INSERT INTO readings (reading_year, reading_month, reading_day, firstreading, firstreading_short, firstreading_link, secondreading, secondreading_short, secondreading_link, created_by) VALUES ('2024', '01', '11', '„A nyomorúság kohójában próbára tettelek.” (Ézs 48,10b)', 'Ézs 48,10b', 'https://szentiras.hu/RUF/Ézs 48,10b', '„Az imádkozásban és a hálaadásban legyetek kitartóak!” (Kol 4,2)', 'Kol 4,2', 'https://szentiras.hu/RUF/Kol 4,2', 'admin');
INSERT INTO readings (reading_year, reading_month, reading_day, firstreading, firstreading_short, firstreading_link, secondreading, secondreading_short, secondreading_link, created_by) VALUES ('2024', '01', '12', '„Jövevény vagyok a földön, ne rejtsd el előlem parancsolataidat!” (Zsolt 119,19)', 'Zsolt 119,19', 'https://szentiras.hu/RUF/Zsolt 119,19', '„Tudjuk pedig, hogy ha földi sátorunk összeomlik, van Istentől készített hajlékunk, nem kézzel csinált, hanem örökkévaló mennyei házunk.” (2Kor 5,1)', '2Kor 5,1', 'https://szentiras.hu/RUF/2Kor 5,1', 'admin');
INSERT INTO readings (reading_year, reading_month, reading_day, firstreading, firstreading_short, firstreading_link, secondreading, secondreading_short, secondreading_link, created_by) VALUES ('2024', '01', '13', '„Íme, arasznyivá tetted napjaimat, életem ideje semmiség előtted. Mint egy lehelet, annyit ér minden ember, aki él.” (Zsolt 39,6)', 'Zsolt 39,6', 'https://szentiras.hu/RUF/Zsolt 39,6', 'Pál írja: „Ezért tehát nem csüggedünk. Sőt ha a külső emberünk megromlik is, a belső emberünk mégis megújul napról napra.” (2Kor 4,16)', '2Kor 4,16', 'https://szentiras.hu/RUF/2Kor 4,16', 'admin');
INSERT INTO readings (reading_year, reading_month, reading_day, firstreading, firstreading_short, firstreading_link, secondreading, secondreading_short, secondreading_link, created_by) VALUES ('2024', '01', '14', '„Ezt mondja az Úr: Tartsátok meg a törvényt, cselekedjetek igazságosan, mert hamarosan eljön szabadításom, és nyilvánvalóvá lesz igazságom.” (Ézs 56,1)', 'Ézs 56,1', 'https://szentiras.hu/RUF/Ézs 56,1', '„János azonban megpróbálta visszatartani őt [Jézust]: Nekem volna szükségem arra, hogy megkeresztelj, és te jössz hozzám? Jézus ezt válaszolta: Engedj most, mert így illik minden igazságot betöltenünk. Akkor engedett neki.” (Mt 3,14–15)', 'Mt 3,14–15', 'https://szentiras.hu/RUF/Mt 3,14–15', 'admin');
INSERT INTO readings (reading_year, reading_month, reading_day, firstreading, firstreading_short, firstreading_link, secondreading, secondreading_short, secondreading_link, created_by) VALUES ('2024', '01', '15', '„Az istenfélőkben gyönyörködik az Úr, azokban, akik az ő szeretetében bíznak.” (Zsolt 147,11)', 'Zsolt 147,11', 'https://szentiras.hu/RUF/Zsolt 147,11', '„Mert nem a szolgaság lelkét kaptátok, hogy ismét féljetek, hanem a fiúság Lelkét kaptátok, aki által kiáltjuk: »Abbá, Atyám!«” (Róm 8,15)', 'Róm 8,15', 'https://szentiras.hu/RUF/Róm 8,15', 'admin');
INSERT INTO readings (reading_year, reading_month, reading_day, firstreading, firstreading_short, firstreading_link, secondreading, secondreading_short, secondreading_link, created_by) VALUES ('2024', '01', '16', '„Ne félj, mert megváltottalak, neveden szólítottalak, enyém vagy!” (Ézs 43,1b)', 'Ézs 43,1b', 'https://szentiras.hu/RUF/Ézs 43,1b', '„Amikor Jézus odaért, felnézett, és így szólt hozzá: Zákeus, jöjj le hamar, mert ma a te házadban kell megszállnom.” (Lk 19,5)', 'Lk 19,5', 'https://szentiras.hu/RUF/Lk 19,5', 'admin');
INSERT INTO readings (reading_year, reading_month, reading_day, firstreading, firstreading_short, firstreading_link, secondreading, secondreading_short, secondreading_link, created_by) VALUES ('2024', '01', '17', '„Pedig én tanítottam járni Efraimot, kézen fogtam őket, de ők nem ismerték fel, hogy én viselem gondjukat.” (Hós 11,3)', 'Hós 11,3', 'https://szentiras.hu/RUF/Hós 11,3', '„Nem hagylak titeket árván, eljövök hozzátok.” (Jn 14,18)', 'Jn 14,18', 'https://szentiras.hu/RUF/Jn 14,18', 'admin');
INSERT INTO readings (reading_year, reading_month, reading_day, firstreading, firstreading_short, firstreading_link, secondreading, secondreading_short, secondreading_link, created_by) VALUES ('2024', '01', '18', '„Illés […] így szólt: Meddig sántikáltok még kétfelé? Ha az Úr az Isten, kövessétek őt, ha pedig Baal, akkor őt kövessétek!” (1Kir 18,21a)', '1Kir 18,21a', 'https://szentiras.hu/RUF/1Kir 18,21a', 'Jézus mondja: „Nem szolgálhattok Istennek és a mammonnak.” (Mt 6,24b)', 'Mt 6,24b', 'https://szentiras.hu/RUF/Mt 6,24b', 'admin');
INSERT INTO readings (reading_year, reading_month, reading_day, firstreading, firstreading_short, firstreading_link, secondreading, secondreading_short, secondreading_link, created_by) VALUES ('2024', '01', '19', '„Se szegénységet, se gazdagságot ne adj nekem! Adj annyi eledelt, amennyi szükséges.” (Péld 30,8b)', 'Péld 30,8b', 'https://szentiras.hu/RUF/Péld 30,8b', '„Mert semmit sem hoztunk a világba, nem is vihetünk ki semmit belőle. De ha van élelmünk és ruházatunk, elégedjünk meg vele.” (1Tim 6,7–8)', '1Tim 6,7–8', 'https://szentiras.hu/RUF/1Tim 6,7–8', 'admin');
INSERT INTO readings (reading_year, reading_month, reading_day, firstreading, firstreading_short, firstreading_link, secondreading, secondreading_short, secondreading_link, created_by) VALUES ('2024', '01', '20', '„Az Úr ereje alkotta a földet, az ő bölcsessége szilárdította meg a világot, az ő értelme feszítette ki az eget.” (Jer 10,12)', 'Jer 10,12', 'https://szentiras.hu/RUF/Jer 10,12', '„Ő [Krisztus] előbb volt mindennél, és minden őbenne áll fenn.” (Kol 1,17)', 'Kol 1,17', 'https://szentiras.hu/RUF/Kol 1,17', 'admin');
INSERT INTO readings (reading_year, reading_month, reading_day, firstreading, firstreading_short, firstreading_link, secondreading, secondreading_short, secondreading_link, created_by) VALUES ('2024', '01', '21', '„Ha a jót elfogadtuk Istentől, a rosszat is el kell fogadnunk.” (Jób 2,10b)', 'Jób 2,10b', 'https://szentiras.hu/RUF/Jób 2,10b', '„Azt pedig tudjuk, hogy akik Istent szeretik, azoknak minden javukra szolgál, azoknak, akiket örök elhatározása szerint elhívott.” (Róm 8,28)', 'Róm 8,28', 'https://szentiras.hu/RUF/Róm 8,28', 'admin');
INSERT INTO readings (reading_year, reading_month, reading_day, firstreading, firstreading_short, firstreading_link, secondreading, secondreading_short, secondreading_link, created_by) VALUES ('2024', '01', '22', '„Egek, harmatozzatok a magasból, hulljon igazság a fellegekből! Táruljon fel a föld, és teremjen szabadságot, sarjadjon igazság is vele! Én, az Úr teremtettem mindezt.” (Ézs 45,8)', 'Ézs 45,8', 'https://szentiras.hu/RUF/Ézs 45,8', '„De az ő ígérete szerint új eget és új földet várunk, amelyben igazság lakik.” (2Pt 3,13)', '2Pt 3,13', 'https://szentiras.hu/RUF/2Pt 3,13', 'admin');
INSERT INTO readings (reading_year, reading_month, reading_day, firstreading, firstreading_short, firstreading_link, secondreading, secondreading_short, secondreading_link, created_by) VALUES ('2024', '01', '23', '„A te nevedért, ó, Uram, bocsásd meg bűneimet, mert sok van!” (Zsolt 25,11)', 'Zsolt 25,11', 'https://szentiras.hu/RUF/Zsolt 25,11', '„Jézus pedig látva a hitüket, így szólt a bénához: Fiam, megbocsáttattak a te bűneid.” (Mk 2,5)', 'Mk 2,5', 'https://szentiras.hu/RUF/Mk 2,5', 'admin');
INSERT INTO readings (reading_year, reading_month, reading_day, firstreading, firstreading_short, firstreading_link, secondreading, secondreading_short, secondreading_link, created_by) VALUES ('2024', '01', '24', '„Azok [az ég és a föld] elpusztulnak, de te megmaradsz.” (Zsolt 102,27)', 'Zsolt 102,27', 'https://szentiras.hu/RUF/Zsolt 102,27', 'Jézus mondja: „És íme, én veletek vagyok minden napon a világ végezetéig.” (Mt 28,20b)', 'Mt 28,20b', 'https://szentiras.hu/RUF/Mt 28,20b', 'admin');
INSERT INTO readings (reading_year, reading_month, reading_day, firstreading, firstreading_short, firstreading_link, secondreading, secondreading_short, secondreading_link, created_by) VALUES ('2024', '01', '25', '„Boldog nép az, amely tud neked ujjongani, amely orcád világosságában járhat, Uram!” (Zsolt 89,16)', 'Zsolt 89,16', 'https://szentiras.hu/RUF/Zsolt 89,16', 'Jézus mondja: „Én vagyok a világ világossága: aki engem követ, nem jár sötétségben, hanem övé lesz az élet világossága.” (Jn 8,12)', 'Jn 8,12', 'https://szentiras.hu/RUF/Jn 8,12', 'admin');
INSERT INTO readings (reading_year, reading_month, reading_day, firstreading, firstreading_short, firstreading_link, secondreading, secondreading_short, secondreading_link, created_by) VALUES ('2024', '01', '26', '„Megismerteted velem az élet útját […].” (Zsolt 16,11a)', 'Zsolt 16,11a', 'https://szentiras.hu/RUF/Zsolt 16,11a', 'Jézus ezt mondta tanítványainak: „Mert aki meg akarja menteni az életét, elveszti azt, aki pedig elveszti az életét énértem, megtalálja azt.” (Mt 16,25)', 'Mt 16,25', 'https://szentiras.hu/RUF/Mt 16,25', 'admin');
INSERT INTO readings (reading_year, reading_month, reading_day, firstreading, firstreading_short, firstreading_link, secondreading, secondreading_short, secondreading_link, created_by) VALUES ('2024', '01', '27', '„Ezt mondta Isten [Salamonnak]: Mivel ezt kérted, és nem kértél magadnak hosszú életet, nem kértél gazdagságot, és nem kérted ellenségeid életét, hanem értelmet kértél, hogy törvényemnek engedelmeskedve tudj kormányozni, ezért teljesítem kérésedet.” (1Kir 3,11–12a)', '1Kir 3,11–12a', 'https://szentiras.hu/RUF/1Kir 3,11–12a', 'Pál írja: „És ne igazodjatok e világhoz, hanem változzatok meg értelmetek megújulásával, hogy megítélhessétek, mi az Isten akarata, mi az, ami jó, ami neki tetsző és tökéletes.” (Róm 12,1)', 'Róm 12,1', 'https://szentiras.hu/RUF/Róm 12,1', 'admin');
INSERT INTO readings (reading_year, reading_month, reading_day, firstreading, firstreading_short, firstreading_link, secondreading, secondreading_short, secondreading_link, created_by) VALUES ('2024', '01', '28', '„Eljön minden nép, amelyet alkottál, leborulnak előtted, Uram, és tiszteletet adnak nevednek.” (Zsolt 86,9)', 'Zsolt 86,9', 'https://szentiras.hu/RUF/Zsolt 86,9', '„Hallottam, hogy egy hatalmas hang szól a trón felől: Íme, Isten sátora az emberekkel van, és ő velük fog lakni, ők pedig népei lesznek, és maga Isten lesz velük.” (Jel 21,3)', 'Jel 21,3', 'https://szentiras.hu/RUF/Jel 21,3', 'admin');
INSERT INTO readings (reading_year, reading_month, reading_day, firstreading, firstreading_short, firstreading_link, secondreading, secondreading_short, secondreading_link, created_by) VALUES ('2024', '01', '29', '„Hadd tudja meg a föld minden népe, hogy az Úr az Isten, nincs más.” (1Kir 8,60)', '1Kir 8,60', 'https://szentiras.hu/RUF/1Kir 8,60', '„Menjetek el tehát, tegyetek tanítvánnyá minden népet, megkeresztelve őket az Atyának, a Fiúnak és a Szentléleknek nevében, tanítva őket, hogy megtartsák mindazt, amit én parancsoltam nektek.” (Mt 28,19–20a)', 'Mt 28,19–20a', 'https://szentiras.hu/RUF/Mt 28,19–20a', 'admin');
INSERT INTO readings (reading_year, reading_month, reading_day, firstreading, firstreading_short, firstreading_link, secondreading, secondreading_short, secondreading_link, created_by) VALUES ('2024', '01', '30', '„Ezt mondta Isai a fiának, Dávidnak: […] Tudd meg, hogy jól vannak-e a testvéreid!” (1Sám 17, 17a.18b)', '1Sám 17, 17a.18b', 'https://szentiras.hu/RUF/1Sám 17, 17a.18b', '„És íme, férfiak ágyon hoztak egy béna embert, és igyekeztek bevinni és letenni [Jézus] elé.” (Lk 5,18)', 'Lk 5,18', 'https://szentiras.hu/RUF/Lk 5,18', 'admin');
INSERT INTO readings (reading_year, reading_month, reading_day, firstreading, firstreading_short, firstreading_link, secondreading, secondreading_short, secondreading_link, created_by) VALUES ('2024', '01', '31', '„Bizony, csak Istenünk, az Úr szabadítja meg Izráelt.” (Jer 3,23b)', 'Jer 3,23b', 'https://szentiras.hu/RUF/Jer 3,23b', '„Mária pedig ezt mondta: Magasztalja lelkem az Urat, és ujjong az én lelkem megtartó Istenemben.” (Lk 1,46–47)', 'Lk 1,46–47', 'https://szentiras.hu/RUF/Lk 1,46–47', 'admin');
INSERT INTO readings (reading_year, reading_month, reading_day, firstreading, firstreading_short, firstreading_link, secondreading, secondreading_short, secondreading_link, created_by) VALUES ('2024', '02', '1', '„Az Úr letekint a mennyből az emberekre, hogy lássa, van-e köztük értelmes, aki keresi az Istent.” (Zsolt 14,2)', 'Zsolt 14,2', 'https://szentiras.hu/RUF/Zsolt 14,2', '„Inkább növekedjetek a kegyelemben és a mi Urunk, üdvözítő Jézus Krisztusunk ismeretében.” (2Pt 3,18a)', '2Pt 3,18a', 'https://szentiras.hu/RUF/2Pt 3,18a', 'admin');
INSERT INTO readings (reading_year, reading_month, reading_day, firstreading, firstreading_short, firstreading_link, secondreading, secondreading_short, secondreading_link, created_by) VALUES ('2024', '02', '2', '„Eljön a sok nép, és ezt mondják: Jöjjetek, menjünk föl az Úr hegyére, Jákób Istenének házához! Tanítson minket útjaira, hogy az ő ösvényein járjunk.” (Ézs 2,3a–b)', 'Ézs 2,3a–b', 'https://szentiras.hu/RUF/Ézs 2,3a–b', '„Amikor Jézus meglátta a sokaságot, felment a hegyre, és miután leült, odamentek hozzá tanítványai. Ő pedig megszólalt, és […] tanította őket.” (Mt 5,1–2)', 'Mt 5,1–2', 'https://szentiras.hu/RUF/Mt 5,1–2', 'admin');
INSERT INTO readings (reading_year, reading_month, reading_day, firstreading, firstreading_short, firstreading_link, secondreading, secondreading_short, secondreading_link, created_by) VALUES ('2024', '02', '3', '„Tanácsot adok, rajtad lesz a szemem.” (Zsolt 32,8b)', 'Zsolt 32,8b', 'https://szentiras.hu/RUF/Zsolt 32,8b', '„[Jézus] így szólt hozzájuk [Péterhez és Andráshoz]: Jöjjetek utánam, és én emberhalászokká teszlek titeket.” (Mt 4,19)', 'Mt 4,19', 'https://szentiras.hu/RUF/Mt 4,19', 'admin');
INSERT INTO readings (reading_year, reading_month, reading_day, firstreading, firstreading_short, firstreading_link, secondreading, secondreading_short, secondreading_link, created_by) VALUES ('2024', '02', '4', '„Közel van, aki igazságot szolgáltat nekem. Ki mer perbe szállni velem?!” (Ézs 50,8a–b)', 'Ézs 50,8a–b', 'https://szentiras.hu/RUF/Ézs 50,8a–b', '„[Jézus] így szólt hozzá: Asszony, hol vannak a vádlóid? Senki sem ítélt el téged? Ő így felelt: Senki, Uram. Jézus pedig ezt mondta neki: Én sem ítéllek el téged, menj el, és mostantól fogva többé ne vétkezz!” (Jn 8,10–11)', 'Jn 8,10–11', 'https://szentiras.hu/RUF/Jn 8,10–11', 'admin');
INSERT INTO readings (reading_year, reading_month, reading_day, firstreading, firstreading_short, firstreading_link, secondreading, secondreading_short, secondreading_link, created_by) VALUES ('2024', '02', '5', '„De még vár az Úr, hogy megkegyelmezhessen, készen áll arra, hogy irgalmazhasson.” (Ézs 30,18a)', 'Ézs 30,18a', 'https://szentiras.hu/RUF/Ézs 30,18a', '„Ellenben mi abban hiszünk, hogy az Úr Jézus kegyelme által üdvözülünk.” (ApCsel 15,11a)', 'ApCsel 15,11a', 'https://szentiras.hu/RUF/ApCsel 15,11a', 'admin');
INSERT INTO readings (reading_year, reading_month, reading_day, firstreading, firstreading_short, firstreading_link, secondreading, secondreading_short, secondreading_link, created_by) VALUES ('2024', '02', '6', 'Így szól az Úr: „Papok királysága és szent népem lesztek.” (2Móz 19,6a)', '2Móz 19,6a', 'https://szentiras.hu/RUF/2Móz 19,6a', '„Végül eltétetett nekem az igazság koronája, amelyet megad nekem az Úr, az igaz bíró azon a napon; de nemcsak nekem, hanem mindazoknak is, akik várva várják az ő megjelenését.” (2Tim 4,8)', '2Tim 4,8', 'https://szentiras.hu/RUF/2Tim 4,8', 'admin');
INSERT INTO readings (reading_year, reading_month, reading_day, firstreading, firstreading_short, firstreading_link, secondreading, secondreading_short, secondreading_link, created_by) VALUES ('2024', '02', '7', '„Keményen megfeddett engem az Úr, de nem adott át a halálnak.” (Zsolt 118,18)', 'Zsolt 118,18', 'https://szentiras.hu/RUF/Zsolt 118,18', '„Volt ott egy ember, aki harmincnyolc éve szenvedett betegségében. Jézus ezt mondta neki: Kelj fel, vedd az ágyadat, és járj! És azonnal meggyógyult ez az ember, felvette az ágyát, és járt.” (Jn 5,5.8–9a)', 'Jn 5,5.8–9a', 'https://szentiras.hu/RUF/Jn 5,5.8–9a', 'admin');
INSERT INTO readings (reading_year, reading_month, reading_day, firstreading, firstreading_short, firstreading_link, secondreading, secondreading_short, secondreading_link, created_by) VALUES ('2024', '02', '8', '„Jó csendben várni az Úr szabadítására.” (JSir 3,26)', 'JSir 3,26', 'https://szentiras.hu/RUF/JSir 3,26', '„Állhatatossággal fussuk meg az előttünk levő pályát. Nézzünk fel Jézusra, a hit szerzőjére és beteljesítőjére […].” (Zsid 12,1b–2a)', 'Zsid 12,1b–2a', 'https://szentiras.hu/RUF/Zsid 12,1b–2a', 'admin');
INSERT INTO readings (reading_year, reading_month, reading_day, firstreading, firstreading_short, firstreading_link, secondreading, secondreading_short, secondreading_link, created_by) VALUES ('2024', '02', '9', '„Beszéljetek szívhez szólóan Jeruzsálemmel, és hirdessétek neki, hogy letelt rabsága, megbűnhődött bűnéért […].” (Ézs 40,2a)', 'Ézs 40,2a', 'https://szentiras.hu/RUF/Ézs 40,2a', '„…és hirdetni kell az ő [Krisztus] nevében a megtérést és a bűnbocsánatot minden nép között, Jeruzsálemtől kezdve.” (Lk 24,47)', 'Lk 24,47', 'https://szentiras.hu/RUF/Lk 24,47', 'admin');
INSERT INTO readings (reading_year, reading_month, reading_day, firstreading, firstreading_short, firstreading_link, secondreading, secondreading_short, secondreading_link, created_by) VALUES ('2024', '02', '10', '„Igazságodról beszél a szám és szabadító tetteidről mindennap, bár nem tudom felsorolni őket.” (Zsolt 71,15)', 'Zsolt 71,15', 'https://szentiras.hu/RUF/Zsolt 71,15', 'Mária ezt mondta: „Mert nagy dolgokat tett velem a Hatalmas, és szent az ő neve.” (Lk 1,49)', 'Lk 1,49', 'https://szentiras.hu/RUF/Lk 1,49', 'admin');
INSERT INTO readings (reading_year, reading_month, reading_day, firstreading, firstreading_short, firstreading_link, secondreading, secondreading_short, secondreading_link, created_by) VALUES ('2024', '02', '11', '„És lelkem köztetek marad! Ne féljetek!” (Hag 2,5)', 'Hag 2,5', 'https://szentiras.hu/RUF/Hag 2,5', '„A világon nyomorúságotok van, de bízzatok: én legyőztem a világot.” (Jn 16,33b)', 'Jn 16,33b', 'https://szentiras.hu/RUF/Jn 16,33b', 'admin');
INSERT INTO readings (reading_year, reading_month, reading_day, firstreading, firstreading_short, firstreading_link, secondreading, secondreading_short, secondreading_link, created_by) VALUES ('2024', '02', '12', '„Fáradozzatok annak a városnak a jólétén, ahová fogságba vitettelek titeket, és imádkozzatok érte az Úrhoz, mert annak a jólététől függ a ti jólétetek is!” (Jer 29,7)', 'Jer 29,7', 'https://szentiras.hu/RUF/Jer 29,7', '„Amint beért Jeruzsálembe, felbolydult az egész város, és ezt kérdezgették: Kicsoda ez?” (Mt 21,10)', 'Mt 21,10', 'https://szentiras.hu/RUF/Mt 21,10', 'admin');
INSERT INTO readings (reading_year, reading_month, reading_day, firstreading, firstreading_short, firstreading_link, secondreading, secondreading_short, secondreading_link, created_by) VALUES ('2024', '02', '13', 'Így szól az Úr: „Ne gyalázzátok meg szent nevemet, hogy megszentelt legyek Izráel fiai között.” (3Móz 22,32a)', '3Móz 22,32a', 'https://szentiras.hu/RUF/3Móz 22,32a', 'Jézus mondja: „Miért mondjátok nekem: Uram, Uram – ha nem teszitek, amit mondok?” (Lk 6,46)', 'Lk 6,46', 'https://szentiras.hu/RUF/Lk 6,46', 'admin');
INSERT INTO readings (reading_year, reading_month, reading_day, firstreading, firstreading_short, firstreading_link, secondreading, secondreading_short, secondreading_link, created_by) VALUES ('2024', '02', '14', '„Mert ki vagyok én, és mi az én népem, honnan volt erőnk ennyit adakozni? Bizony, tőled van mindez, és csak azt adtuk neked, amit a te kezedből kaptunk!” (1Krón 29,14)', '1Krón 29,14', 'https://szentiras.hu/RUF/1Krón 29,14', '„Gyógyítsatok betegeket, támasszatok fel halottakat, tisztítsatok meg leprásokat, űzzetek ki ördögöket! Ingyen kaptátok, ingyen adjátok!” (Mt 10,8)', 'Mt 10,8', 'https://szentiras.hu/RUF/Mt 10,8', 'admin');
INSERT INTO readings (reading_year, reading_month, reading_day, firstreading, firstreading_short, firstreading_link, secondreading, secondreading_short, secondreading_link, created_by) VALUES ('2024', '02', '15', '„Ekkor odament a nép Mózeshez, és ezt mondta: Vétkeztünk, mert az Úr ellen és ellened szóltunk. Imádkozz az Úrhoz, hogy távolítsa el rólunk a kígyókat! És imádkozott Mózes a népért.” (4Móz 21,7)', '4Móz 21,7', 'https://szentiras.hu/RUF/4Móz 21,7', '„Krisztusért kérünk, béküljetek meg Istennel!” (2Kor 5,20b)', '2Kor 5,20b', 'https://szentiras.hu/RUF/2Kor 5,20b', 'admin');
INSERT INTO readings (reading_year, reading_month, reading_day, firstreading, firstreading_short, firstreading_link, secondreading, secondreading_short, secondreading_link, created_by) VALUES ('2024', '02', '16', '„Taníts úgy számlálni napjainkat, hogy bölcs szívhez jussunk!” (Zsolt 90,12)', 'Zsolt 90,12', 'https://szentiras.hu/RUF/Zsolt 90,12', '„Mert ha élünk, az Úrnak élünk, ha meghalunk, az Úrnak halunk meg. Tehát akár éljünk, akár haljunk, az Úréi vagyunk.” (Róm 14,8)', 'Róm 14,8', 'https://szentiras.hu/RUF/Róm 14,8', 'admin');
INSERT INTO readings (reading_year, reading_month, reading_day, firstreading, firstreading_short, firstreading_link, secondreading, secondreading_short, secondreading_link, created_by) VALUES ('2024', '02', '17', '„[Izráel hegyei,] én mellettetek vagyok, és felétek fordulok: megművelnek és bevetnek titeket.” (Ez 36,9)', 'Ez 36,9', 'https://szentiras.hu/RUF/Ez 36,9', '„Legyetek tehát türelemmel, testvéreim, az Úr eljöveteléig. Íme, a földművelő várja a föld drága gyümölcsét, és türelmesen várja, amíg az korai és késői esőt kap.” (Jak 5,7)', 'Jak 5,7', 'https://szentiras.hu/RUF/Jak 5,7', 'admin');
INSERT INTO readings (reading_year, reading_month, reading_day, firstreading, firstreading_short, firstreading_link, secondreading, secondreading_short, secondreading_link, created_by) VALUES ('2024', '02', '18', '„De az igazak örülnek, vigadnak, Isten színe előtt vígan örvendeznek.” (Zsolt 68,4)', 'Zsolt 68,4', 'https://szentiras.hu/RUF/Zsolt 68,4', '„Ura így szólt hozzá: Jól van, jó és hű szolgám, a kevésen hű voltál, sokat bízok rád ezután, jöjj, és osztozz urad örömében!” (Mt 25,21)', 'Mt 25,21', 'https://szentiras.hu/RUF/Mt 25,21', 'admin');
INSERT INTO readings (reading_year, reading_month, reading_day, firstreading, firstreading_short, firstreading_link, secondreading, secondreading_short, secondreading_link, created_by) VALUES ('2024', '02', '19', '„Mily nagyok alkotásaid, Uram! Igen mélyek gondolataid!” (Zsolt 92,6)', 'Zsolt 92,6', 'https://szentiras.hu/RUF/Zsolt 92,6', '„Nagyok és csodálatosak a te műveid, mindenható Úr Isten, igazságosak és igazak a te utaid, népek királya.” (Jel 15,3b)', 'Jel 15,3b', 'https://szentiras.hu/RUF/Jel 15,3b', 'admin');
INSERT INTO readings (reading_year, reading_month, reading_day, firstreading, firstreading_short, firstreading_link, secondreading, secondreading_short, secondreading_link, created_by) VALUES ('2024', '02', '20', 'Ezt mondta az Úr Illésnek: „De meghagyok Izráelben hétezer embert: minden térdet, amely nem hajolt meg Baal előtt…” (1Kir 19,18)', '1Kir 19,18', 'https://szentiras.hu/RUF/1Kir 19,18', '„Ti azonban, szeretteim, épüljetek szentséges hitetekben, imádkozzatok a Szentlélek által, tartsátok meg magatokat Isten szeretetében, várva a mi Urunk Jézus Krisztusnak irgalmát az örök életre.” (Júd 20–21)', 'Júd 20–21', 'https://szentiras.hu/RUF/Júd 20–21', 'admin');
INSERT INTO readings (reading_year, reading_month, reading_day, firstreading, firstreading_short, firstreading_link, secondreading, secondreading_short, secondreading_link, created_by) VALUES ('2024', '02', '21', '„Ki mondhatja: Tisztán tartottam szívemet, tiszta vagyok, nincs vétkem?” (Péld 20,9)', 'Péld 20,9', 'https://szentiras.hu/RUF/Péld 20,9', '„Jézus pedig ismerve gondolataikat, megkérdezte tőlük: Miről tanakodtok szívetekben?” (Lk 5,22)', 'Lk 5,22', 'https://szentiras.hu/RUF/Lk 5,22', 'admin');
INSERT INTO readings (reading_year, reading_month, reading_day, firstreading, firstreading_short, firstreading_link, secondreading, secondreading_short, secondreading_link, created_by) VALUES ('2024', '02', '22', '„Szolgáljatok az Úrnak örömmel, vigadozva járuljatok színe elé!” (Zsolt 100,2)', 'Zsolt 100,2', 'https://szentiras.hu/RUF/Zsolt 100,2', '„A jó cselekvésében pedig ne fáradjunk el, mert a maga idejében aratunk majd, ha meg nem lankadunk.” (Gal 6,9)', 'Gal 6,9', 'https://szentiras.hu/RUF/Gal 6,9', 'admin');
INSERT INTO readings (reading_year, reading_month, reading_day, firstreading, firstreading_short, firstreading_link, secondreading, secondreading_short, secondreading_link, created_by) VALUES ('2024', '02', '23', '„Én Istenem, én Istenem, miért hagytál el engem? Távol van tőlem a segítség, pedig jajgatva kiáltok!” (Zsolt 22,2)', 'Zsolt 22,2', 'https://szentiras.hu/RUF/Zsolt 22,2', '„Ekkor a templom kárpitja felülről az aljáig kettéhasadt.” (Mk 15,38)', 'Mk 15,38', 'https://szentiras.hu/RUF/Mk 15,38', 'admin');
INSERT INTO readings (reading_year, reading_month, reading_day, firstreading, firstreading_short, firstreading_link, secondreading, secondreading_short, secondreading_link, created_by) VALUES ('2024', '02', '24', '„Több jót teszek veletek, mint azelőtt. Akkor majd megtudjátok, hogy én vagyok az Úr!” (Ez 36,11)', 'Ez 36,11', 'https://szentiras.hu/RUF/Ez 36,11', 'Pál írja: „Azt pedig tudom, hogy amikor hozzátok megyek, Krisztus áldásának teljességével megyek majd.” (Róm 15,29)', 'Róm 15,29', 'https://szentiras.hu/RUF/Róm 15,29', 'admin');
INSERT INTO readings (reading_year, reading_month, reading_day, firstreading, firstreading_short, firstreading_link, secondreading, secondreading_short, secondreading_link, created_by) VALUES ('2024', '02', '25', '„Maradjanak a szívedben azok az igék, amelyeket ma parancsolok neked. Ismételgesd azokat fiaid előtt, és beszélj azokról…” (5Móz 6,6–7)', '5Móz 6,6–7', 'https://szentiras.hu/RUF/5Móz 6,6–7', 'Jézus mondja: „A Pártfogó pedig, a Szentlélek, akit az én nevemben küld az Atya, ő megtanít majd titeket mindenre, és eszetekbe juttat mindent, amit én mondtam nektek.” (Jn 14,26)', 'Jn 14,26', 'https://szentiras.hu/RUF/Jn 14,26', 'admin');
INSERT INTO readings (reading_year, reading_month, reading_day, firstreading, firstreading_short, firstreading_link, secondreading, secondreading_short, secondreading_link, created_by) VALUES ('2024', '02', '26', '„Mert az Úr szemei áttekintik az egész földet, és ő megmutatja erejét azoknak, akik tiszta szívvel az övéi.” (2Krón 16,9a)', '2Krón 16,9a', 'https://szentiras.hu/RUF/2Krón 16,9a', '„…és világosítsa meg lelki szemeteket, hogy meglássátok, milyen reménységre hívott el titeket […], és milyen mérhetetlenül nagy az ő hatalma rajtunk, hívőkön.” (Ef 1,18–19)', 'Ef 1,18–19', 'https://szentiras.hu/RUF/Ef 1,18–19', 'admin');
INSERT INTO readings (reading_year, reading_month, reading_day, firstreading, firstreading_short, firstreading_link, secondreading, secondreading_short, secondreading_link, created_by) VALUES ('2024', '02', '27', '„Teljesen mosd le rólam bűnömet, és vétkemtől tisztíts meg engem!” (Zsolt 51,4)', 'Zsolt 51,4', 'https://szentiras.hu/RUF/Zsolt 51,4', 'Jézus mondja: „Ugyanígy egyetlen megtérő bűnös miatt nagyobb öröm lesz a mennyben, mint kilencvenkilenc igaz miatt, akiknek nincs szüksége megtérésre.” (Lk 15,7)', 'Lk 15,7', 'https://szentiras.hu/RUF/Lk 15,7', 'admin');
INSERT INTO readings (reading_year, reading_month, reading_day, firstreading, firstreading_short, firstreading_link, secondreading, secondreading_short, secondreading_link, created_by) VALUES ('2024', '02', '28', '„Uram, Istenem, hozzád menekülök! Szabadíts meg üldözőimtől, és ments meg engem.” (Zsolt 7,2)', 'Zsolt 7,2', 'https://szentiras.hu/RUF/Zsolt 7,2', '„Amikor azonban átadnak titeket, ne aggódjatok amiatt, hogyan szóljatok, vagy mit mondjatok, mert megadatik nektek abban az órában, hogy mit mondjatok.” (Mt 10,19)', 'Mt 10,19', 'https://szentiras.hu/RUF/Mt 10,19', 'admin');
INSERT INTO readings (reading_year, reading_month, reading_day, firstreading, firstreading_short, firstreading_link, secondreading, secondreading_short, secondreading_link, created_by) VALUES ('2024', '02', '29', '„Az Úrhoz menekülök. Hogyan mondhatjátok nekem: Menekülj a hegyre, mint a madár?!” (Zsolt 11,1)', 'Zsolt 11,1', 'https://szentiras.hu/RUF/Zsolt 11,1', '„Ne veszítsétek el tehát bizalmatokat, amelynek nagy jutalma van.” (Zsid 10,35)', 'Zsid 10,35', 'https://szentiras.hu/RUF/Zsid 10,35', 'admin');
INSERT INTO readings (reading_year, reading_month, reading_day, firstreading, firstreading_short, firstreading_link, secondreading, secondreading_short, secondreading_link, created_by) VALUES ('2024', '03', '1', '„Mert te voltál a segítségem, szárnyad árnyékában ujjongok.” (Zsolt 63,8)', 'Zsolt 63,8', 'https://szentiras.hu/RUF/Zsolt 63,8', '„Ha Isten velünk, ki lehet ellenünk?” (Róm 8,31b)', 'Róm 8,31b', 'https://szentiras.hu/RUF/Róm 8,31b', 'admin');
INSERT INTO readings (reading_year, reading_month, reading_day, firstreading, firstreading_short, firstreading_link, secondreading, secondreading_short, secondreading_link, created_by) VALUES ('2024', '03', '2', '„Meddig tart ez, Uram? Végképp megfeledkeztél rólam? Meddig rejted el orcádat előlem?” (Zsolt 13,2)', 'Zsolt 13,2', 'https://szentiras.hu/RUF/Zsolt 13,2', '„Jézus hangosan felkiáltott: […] Én Istenem, én Istenem, miért hagytál el engem?” (Mt 27,46)', 'Mt 27,46', 'https://szentiras.hu/RUF/Mt 27,46', 'admin');
INSERT INTO readings (reading_year, reading_month, reading_day, firstreading, firstreading_short, firstreading_link, secondreading, secondreading_short, secondreading_link, created_by) VALUES ('2024', '03', '3', '„Aki takargatja vétkeit, annak nem lesz jó vége, aki pedig megvallja és elhagyja, az irgalmat nyer.” (Péld 28,13)', 'Péld 28,13', 'https://szentiras.hu/RUF/Péld 28,13', '„Zákeus pedig odaállt az Úr elé, és ezt mondta: Uram, íme, vagyonom felét a szegényeknek adom, és ha valakitől valamit törvénytelenül vettem el, a négyszeresét adom vissza annak. Jézus így felelt neki: Ma lett üdvössége ennek a háznak […].” (Lk 19,8–9)', 'Lk 19,8–9', 'https://szentiras.hu/RUF/Lk 19,8–9', 'admin');
INSERT INTO readings (reading_year, reading_month, reading_day, firstreading, firstreading_short, firstreading_link, secondreading, secondreading_short, secondreading_link, created_by) VALUES ('2024', '03', '4', '„De népét megoltalmazza az Úr, és erőssége lesz Izráel fiainak.” (Joel 4,16b)', 'Joel 4,16b', 'https://szentiras.hu/RUF/Joel 4,16b', '„[A vak] annál inkább kiáltozott: Dávid Fia, könyörülj rajtam! Jézus megállt, és ezt mondta: Hívjátok ide!” (Mk 10,48–49a)', 'Mk 10,48–49a', 'https://szentiras.hu/RUF/Mk 10,48–49a', 'admin');
INSERT INTO readings (reading_year, reading_month, reading_day, firstreading, firstreading_short, firstreading_link, secondreading, secondreading_short, secondreading_link, created_by) VALUES ('2024', '03', '5', '„Ki tudja, talán felénk fordul és megszán az Isten, megfékezi izzó haragját, és nem veszünk el!” (Jón 3,9)', 'Jón 3,9', 'https://szentiras.hu/RUF/Jón 3,9', 'Jézus mondja: „Higgyétek, hogy mindazt, amit imádságotokban kértek, megkapjátok, és megadatik nektek.” (Mk 11,24)', 'Mk 11,24', 'https://szentiras.hu/RUF/Mk 11,24', 'admin');
INSERT INTO readings (reading_year, reading_month, reading_day, firstreading, firstreading_short, firstreading_link, secondreading, secondreading_short, secondreading_link, created_by) VALUES ('2024', '03', '6', '„Az Úr megőriz téged minden bajtól, megőrzi életedet.” (Zsolt 121,7)', 'Zsolt 121,7', 'https://szentiras.hu/RUF/Zsolt 121,7', 'Jézus így imádkozott: „Nem azt kérem, hogy vedd ki őket a világból, hanem hogy őrizd meg őket a gonosztól.” (Jn 17,15)', 'Jn 17,15', 'https://szentiras.hu/RUF/Jn 17,15', 'admin');
INSERT INTO readings (reading_year, reading_month, reading_day, firstreading, firstreading_short, firstreading_link, secondreading, secondreading_short, secondreading_link, created_by) VALUES ('2024', '03', '7', '„Fenséges az Úr, magasságban lakik, betölti a Siont törvénnyel és igazsággal. Nyugodt lesz az életed, bölcsesség és ismeret teszi szabaddá és gazdaggá; az Úr félelme lesz a kincse.” (Ézs 33,5–6)', 'Ézs 33,5–6', 'https://szentiras.hu/RUF/Ézs 33,5–6', '„Az egyháznak tehát egész Júdeában, Galileában és Samáriában békessége volt: eközben épült, az Úr félelmében járt, és a Szentlélek segítségével egyre gyarapodott.” (ApCsel 9,31)', 'ApCsel 9,31', 'https://szentiras.hu/RUF/ApCsel 9,31', 'admin');
INSERT INTO readings (reading_year, reading_month, reading_day, firstreading, firstreading_short, firstreading_link, secondreading, secondreading_short, secondreading_link, created_by) VALUES ('2024', '03', '8', '„De megkegyelmezett nekik [Izráelnek] az Úr, megkönyörült rajtuk, és feléjük fordult az Ábrahámmal, Izsákkal és Jákóbbal kötött szövetségért. Nem akarta őket elpusztítani, és nem vetette el őket maga elől mindeddig.” (2Kir 13,23)', '2Kir 13,23', 'https://szentiras.hu/RUF/2Kir 13,23', '„Mert megbánhatatlanok az Isten ajándékai és az ő elhívása.” (Róm 11,29)', 'Róm 11,29', 'https://szentiras.hu/RUF/Róm 11,29', 'admin');
INSERT INTO readings (reading_year, reading_month, reading_day, firstreading, firstreading_short, firstreading_link, secondreading, secondreading_short, secondreading_link, created_by) VALUES ('2024', '03', '9', '„Miért tart fájdalmam szüntelen, miért nincs gyógyulás súlyos sebemre? Olyan vagy hozzám, mint a csalóka patak, amelynek nincsen állandóan vize.” (Jer 15,18)', 'Jer 15,18', 'https://szentiras.hu/RUF/Jer 15,18', 'Pál írja: „Őbenne [Krisztusban] van bátorságunk és szabad utunk bizodalommal a benne való hit által. Kérlek tehát titeket, ne csüggedjetek el az értetek szenvedett megpróbáltatásaim miatt, hiszen számotokra dicsőség ez.” (Ef 3,12–13)', 'Ef 3,12–13', 'https://szentiras.hu/RUF/Ef 3,12–13', 'admin');
INSERT INTO readings (reading_year, reading_month, reading_day, firstreading, firstreading_short, firstreading_link, secondreading, secondreading_short, secondreading_link, created_by) VALUES ('2024', '03', '10', '„Örvendezni [fogok] népemmel. Nem hallatszik ott többé sírás és jajkiáltás hangja.” (Ézs 65,19)', 'Ézs 65,19', 'https://szentiras.hu/RUF/Ézs 65,19', '„Mert a Bárány, aki középen a trónnál van, legelteti őket, elvezeti őket az élet vizének forrásaihoz, és Isten letöröl szemükről minden könnyet.” (Jel 7,17)', 'Jel 7,17', 'https://szentiras.hu/RUF/Jel 7,17', 'admin');
INSERT INTO readings (reading_year, reading_month, reading_day, firstreading, firstreading_short, firstreading_link, secondreading, secondreading_short, secondreading_link, created_by) VALUES ('2024', '03', '11', '„Amikor Jákób fölébredt álmából, ezt mondta: Bizonyára az Úr van ezen a helyen, és én nem tudtam! Félelem fogta el, és így szólt: Milyen félelmetes ez a hely! Nem más ez, mint Isten háza és a menny kapuja.” (1Móz 28,16–17)', '1Móz 28,16–17', 'https://szentiras.hu/RUF/1Móz 28,16–17', '„Mert minden, ami nyilvánvalóvá lett, az világosság. Ezért mondja: »Ébredj fel, aki alszol, támadj fel a halálból, és felragyog neked a Krisztus.«” (Ef 5,14)', 'Ef 5,14', 'https://szentiras.hu/RUF/Ef 5,14', 'admin');
INSERT INTO readings (reading_year, reading_month, reading_day, firstreading, firstreading_short, firstreading_link, secondreading, secondreading_short, secondreading_link, created_by) VALUES ('2024', '03', '12', '„Ne koholj rosszat felebarátod ellen, aki melletted lakik gyanútlanul!” (Péld 3,29)', 'Péld 3,29', 'https://szentiras.hu/RUF/Péld 3,29', 'Pál írja: „Mivel így vonzódtunk hozzátok, készek voltunk odaadni nektek nemcsak Isten evangéliumát, hanem a saját lelkünket is, mert annyira megszerettünk titeket.” (1Thessz 2,8)', '1Thessz 2,8', 'https://szentiras.hu/RUF/1Thessz 2,8', 'admin');
INSERT INTO readings (reading_year, reading_month, reading_day, firstreading, firstreading_short, firstreading_link, secondreading, secondreading_short, secondreading_link, created_by) VALUES ('2024', '03', '13', 'Az Úr ezt mondta: „Mert őt [Ábrahámot] választottam ki arra, hogy megparancsolja maga után fiainak és háza népének is, hogy őrizzék meg az Úr útját, cselekedjenek az igazságnak és jognak megfelelően, hogy az Úr is beteljesítse, amit megígért Ábrahámnak.” (1Móz 18,19)', '1Móz 18,19', 'https://szentiras.hu/RUF/1Móz 18,19', '„Mi azonban tudjuk, hogy a törvény jó, ha valaki a törvénynek megfelelően él azzal.” (1Tim 1,8)', '1Tim 1,8', 'https://szentiras.hu/RUF/1Tim 1,8', 'admin');
INSERT INTO readings (reading_year, reading_month, reading_day, firstreading, firstreading_short, firstreading_link, secondreading, secondreading_short, secondreading_link, created_by) VALUES ('2024', '03', '14', '„Bizonyosan megkegyelmez az Úr, ha hozzá kiáltasz. Mihelyt meghallja, válaszol neked.” (Ézs 30,19b)', 'Ézs 30,19b', 'https://szentiras.hu/RUF/Ézs 30,19b', '„Ekkor így szólt hozzá [a kánaáni asszonyhoz] Jézus: Asszony, nagy a te hited, legyen úgy, amint kívánod!” (Mt 15,28a)', 'Mt 15,28a', 'https://szentiras.hu/RUF/Mt 15,28a', 'admin');
INSERT INTO readings (reading_year, reading_month, reading_day, firstreading, firstreading_short, firstreading_link, secondreading, secondreading_short, secondreading_link, created_by) VALUES ('2024', '03', '15', '„Ti az Úrnak, a ti Isteneteknek a gyermekei vagytok.” (5Móz 14,1a)', '5Móz 14,1a', 'https://szentiras.hu/RUF/5Móz 14,1a', '„Mert mindnyájan Isten fiai vagytok a Krisztus Jézusban való hit által.” (Gal 3,26)', 'Gal 3,26', 'https://szentiras.hu/RUF/Gal 3,26', 'admin');
INSERT INTO readings (reading_year, reading_month, reading_day, firstreading, firstreading_short, firstreading_link, secondreading, secondreading_short, secondreading_link, created_by) VALUES ('2024', '03', '16', '„Adjatok hálát az Úrnak, hívjátok segítségül nevét, hirdessétek tetteit a népek közt!” (Zsolt 105,1)', 'Zsolt 105,1', 'https://szentiras.hu/RUF/Zsolt 105,1', '„A hit tehát hallásból van, a hallás pedig Krisztus beszéde által.” (Róm 10,17)', 'Róm 10,17', 'https://szentiras.hu/RUF/Róm 10,17', 'admin');
INSERT INTO readings (reading_year, reading_month, reading_day, firstreading, firstreading_short, firstreading_link, secondreading, secondreading_short, secondreading_link, created_by) VALUES ('2024', '03', '17', '„Mert vizet árasztok a szomjas földre, patakokat a száraz tájra. Kiárasztom lelkemet utódaidra, áldásomat sarjadékaidra.” (Ézs 44,3)', 'Ézs 44,3', 'https://szentiras.hu/RUF/Ézs 44,3', '„Jézus […] így kiáltott: Ha valaki szomjazik, jöjjön hozzám, és igyék!” (Jn 7,38)', 'Jn 7,38', 'https://szentiras.hu/RUF/Jn 7,38', 'admin');
INSERT INTO readings (reading_year, reading_month, reading_day, firstreading, firstreading_short, firstreading_link, secondreading, secondreading_short, secondreading_link, created_by) VALUES ('2024', '03', '18', '„Jaj azoknak, akik jogtalan rendeleteket hoznak, és irataik csak terheket rónak ki. Mert elnyújtják a nincstelenek perét, megfosztják jogaiktól népem szegényeit.” (Ézs 10,1–2)', 'Ézs 10,1–2', 'https://szentiras.hu/RUF/Ézs 10,1–2', '„Amit csak szeretnétek, hogy az emberek tegyenek veletek, mindenben ugyanúgy tegyetek ti is velük, mert ezt tanítja a törvény és a próféták.” (Mt 7,12)', 'Mt 7,12', 'https://szentiras.hu/RUF/Mt 7,12', 'admin');
INSERT INTO readings (reading_year, reading_month, reading_day, firstreading, firstreading_short, firstreading_link, secondreading, secondreading_short, secondreading_link, created_by) VALUES ('2024', '03', '19', '„Az örök Isten a te menedéked…” (5Móz 33,27)', '5Móz 33,27', 'https://szentiras.hu/RUF/5Móz 33,27', '„Ne nyugtalankodjék a ti szívetek: higgyetek Istenben, és higgyetek énbennem! Az én Atyám házában sok hajlék van.” (Jn 14,1–2a)', 'Jn 14,1–2a', 'https://szentiras.hu/RUF/Jn 14,1–2a', 'admin');
INSERT INTO readings (reading_year, reading_month, reading_day, firstreading, firstreading_short, firstreading_link, secondreading, secondreading_short, secondreading_link, created_by) VALUES ('2024', '03', '20', '„Mit fizessek az Úrnak a sok jóért, amit velem tett?” (Zsolt 116,12)', 'Zsolt 116,12', 'https://szentiras.hu/RUF/Zsolt 116,12', '„Egyikük pedig, amikor látta, hogy meggyógyult, visszatért, és fennhangon dicsőítette Istent. Arcra borult Jézus lábánál, és hálát adott neki. Ez pedig samáriai volt.” (Lk 17,15–16)', 'Lk 17,15–16', 'https://szentiras.hu/RUF/Lk 17,15–16', 'admin');
INSERT INTO readings (reading_year, reading_month, reading_day, firstreading, firstreading_short, firstreading_link, secondreading, secondreading_short, secondreading_link, created_by) VALUES ('2024', '03', '21', '„Ő [Isten] ad bölcsességet a bölcseknek és tudományt a nagy tudósoknak.” (Dán 2,21c)', 'Dán 2,21c', 'https://szentiras.hu/RUF/Dán 2,21c', '„Őt [Krisztust] tette nekünk Isten bölcsességgé, igazsággá, megszentelődéssé és megváltássá, hogy amint meg van írva: »Aki dicsekszik, az Úrral dicsekedjék.«” (1Kor 1,30b–31)', '1Kor 1,30b–31', 'https://szentiras.hu/RUF/1Kor 1,30b–31', 'admin');
INSERT INTO readings (reading_year, reading_month, reading_day, firstreading, firstreading_short, firstreading_link, secondreading, secondreading_short, secondreading_link, created_by) VALUES ('2024', '03', '22', '„Te vetettél hajdan alapot a földnek, az ég a te kezed alkotása.” (Zsolt 102,26)', 'Zsolt 102,26', 'https://szentiras.hu/RUF/Zsolt 102,26', '„Mert benne [Krisztusban] teremtetett minden a mennyen és a földön, a láthatók és a láthatatlanok.” (Kol 1,16)', 'Kol 1,16', 'https://szentiras.hu/RUF/Kol 1,16', 'admin');
INSERT INTO readings (reading_year, reading_month, reading_day, firstreading, firstreading_short, firstreading_link, secondreading, secondreading_short, secondreading_link, created_by) VALUES ('2024', '03', '23', '„Hiszen nem istenek azok, akiket istenként készít magának az ember!” (Jer 16,20)', 'Jer 16,20', 'https://szentiras.hu/RUF/Jer 16,20', '„Öljétek meg tehát tagjaitokban azt, ami földi: a paráznaságot, a tisztátalanságot, a szenvedélyt, a gonosz kívánságot és a kapzsiságot, ami bálványimádás.” (Kol 3,5)', 'Kol 3,5', 'https://szentiras.hu/RUF/Kol 3,5', 'admin');
INSERT INTO readings (reading_year, reading_month, reading_day, firstreading, firstreading_short, firstreading_link, secondreading, secondreading_short, secondreading_link, created_by) VALUES ('2024', '03', '24', '„Mert ha te, Uram, megáldod, örökre áldott lesz!” (1Krón 17,27b)', '1Krón 17,27b', 'https://szentiras.hu/RUF/1Krón 17,27b', '„Akkor így szól a király a jobb keze felől állókhoz: Jöjjetek, Atyám áldottai, örököljétek az országot, amely készen áll számotokra a világ kezdete óta.” (Mt 25,34)', 'Mt 25,34', 'https://szentiras.hu/RUF/Mt 25,34', 'admin');
INSERT INTO readings (reading_year, reading_month, reading_day, firstreading, firstreading_short, firstreading_link, secondreading, secondreading_short, secondreading_link, created_by) VALUES ('2024', '03', '25', '„Az élő Úrra esküszöm, hogy csak azt fogom mondani, amit az én Istenem mond.” (2Krón 18,13)', '2Krón 18,13', 'https://szentiras.hu/RUF/2Krón 18,13', '„Hirdesd az igét, állj elő vele, akár alkalmas, akár alkalmatlan az idő, feddj, ints, biztass teljes türelemmel és tanítással.” (2Tim 4,2)', '2Tim 4,2', 'https://szentiras.hu/RUF/2Tim 4,2', 'admin');
INSERT INTO readings (reading_year, reading_month, reading_day, firstreading, firstreading_short, firstreading_link, secondreading, secondreading_short, secondreading_link, created_by) VALUES ('2024', '03', '26', '„…az Úr […] utat készített a tengeren, ösvényt a hatalmas vízen át.” (Ézs 43,16)', 'Ézs 43,16', 'https://szentiras.hu/RUF/Ézs 43,16', 'Jézus mondja: „És ha majd elmentem, és helyet készítettem nektek, ismét eljövök, és magam mellé veszlek titeket, hogy ahol én vagyok, ti is ott legyetek. Ahova pedig én megyek, oda tudjátok az utat.” (Jn 14,3–4)', 'Jn 14,3–4', 'https://szentiras.hu/RUF/Jn 14,3–4', 'admin');
INSERT INTO readings (reading_year, reading_month, reading_day, firstreading, firstreading_short, firstreading_link, secondreading, secondreading_short, secondreading_link, created_by) VALUES ('2024', '03', '27', '„[Az Úr] pártját fogja a szegénynek, megszabadítja a halálos ítélettől.” (Zsolt 109,31)', 'Zsolt 109,31', 'https://szentiras.hu/RUF/Zsolt 109,31', '„Figyeljetek csak ide, szeretett testvéreim: vajon nem Isten választotta-e ki azokat, akik a világ szemében szegények, hogy hitben gazdagok legyenek, és örököljék azt az országot, amelyet Isten az őt szeretőknek ígért?” (Jak 2,5)', 'Jak 2,5', 'https://szentiras.hu/RUF/Jak 2,5', 'admin');
INSERT INTO readings (reading_year, reading_month, reading_day, firstreading, firstreading_short, firstreading_link, secondreading, secondreading_short, secondreading_link, created_by) VALUES ('2024', '03', '28', '„Az igazságosság felmagasztalja a népet, a bűn pedig gyalázatukra van a nemzeteknek.” (Péld 14,34)', 'Péld 14,34', 'https://szentiras.hu/RUF/Péld 14,34', '„Ezután [Jézus] eltávozott onnan, és szokása szerint az Olajfák hegyére ment. Követték a tanítványai is. Mikor pedig odaért, így szólt hozzájuk: Imádkozzatok, hogy kísértésbe ne essetek!” (Lk 22,39–40)', 'Lk 22,39–40', 'https://szentiras.hu/RUF/Lk 22,39–40', 'admin');
INSERT INTO readings (reading_year, reading_month, reading_day, firstreading, firstreading_short, firstreading_link, secondreading, secondreading_short, secondreading_link, created_by) VALUES ('2024', '03', '29', '„Jákób Bételnek nevezte el azt a helyet, ahol Isten beszélt vele.” (1Móz 35,15)', '1Móz 35,15', 'https://szentiras.hu/RUF/1Móz 35,15', '„Amikor pedig a százados és akik vele őrizték Jézust, látták a földrengést és a történteket, nagyon megrémültek, és így szóltak: Bizony, Isten Fia volt ez!” (Mt 27,54)', 'Mt 27,54', 'https://szentiras.hu/RUF/Mt 27,54', 'admin');
INSERT INTO readings (reading_year, reading_month, reading_day, firstreading, firstreading_short, firstreading_link, secondreading, secondreading_short, secondreading_link, created_by) VALUES ('2024', '03', '30', '„[Az Úr] készen áll arra, hogy irgalmazhasson.” (Ézs 30,18a)', 'Ézs 30,18a', 'https://szentiras.hu/RUF/Ézs 30,18a', '„Mert Isten nem azért küldte el a Fiút a világba, hogy elítélje a világot, hanem hogy üdvözüljön a világ általa.” (Jn 3,17)', 'Jn 3,17', 'https://szentiras.hu/RUF/Jn 3,17', 'admin');
INSERT INTO readings (reading_year, reading_month, reading_day, firstreading, firstreading_short, firstreading_link, secondreading, secondreading_short, secondreading_link, created_by) VALUES ('2024', '03', '31', '„A sötétséget világossággá változtatom előttük, a göröngyös utat egyenessé.” (Ézs 42,16b)', 'Ézs 42,16b', 'https://szentiras.hu/RUF/Ézs 42,16b', '„[Jézus] vette a kenyeret, megáldotta, megtörte és nekik adta. Ekkor megnyílt a szemük, és felismerték […].” (Lk 24,30–31)', 'Lk 24,30–31', 'https://szentiras.hu/RUF/Lk 24,30–31', 'admin');
INSERT INTO readings (reading_year, reading_month, reading_day, firstreading, firstreading_short, firstreading_link, secondreading, secondreading_short, secondreading_link, created_by) VALUES ('2024', '04', '1', '„Tanuljatok meg jót cselekedni! Törekedjetek igazságra, térítsétek jó útra az erőszakoskodót!” (Ézs 1,17a–b)', 'Ézs 1,17a–b', 'https://szentiras.hu/RUF/Ézs 1,17a–b', '„Agabosz […] megjövendölte, hogy nagy éhínség lesz az egész földön. Ez be is következett Klaudiusz idejében. A tanítványok pedig valamennyien elhatározták, hogy aszerint, amint kinek-kinek módjában áll, valami segítséget küldenek a Júdeában lakó testvéreknek.” (ApCsel 11,28–29)', 'ApCsel 11,28–29', 'https://szentiras.hu/RUF/ApCsel 11,28–29', 'admin');
INSERT INTO readings (reading_year, reading_month, reading_day, firstreading, firstreading_short, firstreading_link, secondreading, secondreading_short, secondreading_link, created_by) VALUES ('2024', '04', '2', '„Magasságban és szentségben lakom, de a megtörttel és alázatos lelkűvel is. Felüdítem az alázatosak lelkét, felüdítem a megtörtek szívét.” (Ézs 57,15b)', 'Ézs 57,15b', 'https://szentiras.hu/RUF/Ézs 57,15b', '„De azok [az emmausi tanítványok] unszolták és kérték [Jézust]: Maradj velünk, mert esteledik, a nap is lehanyatlott már! Bement hát, hogy velük maradjon.” (Lk 24,29)', 'Lk 24,29', 'https://szentiras.hu/RUF/Lk 24,29', 'admin');
INSERT INTO readings (reading_year, reading_month, reading_day, firstreading, firstreading_short, firstreading_link, secondreading, secondreading_short, secondreading_link, created_by) VALUES ('2024', '04', '3', '„Majd megtudják, hogy én vagyok az Úr, amikor darabokra töröm igájukat, és kiszabadítom őket azok kezéből, akiket szolgálnak.” (Ez 34,27b)', 'Ez 34,27b', 'https://szentiras.hu/RUF/Ez 34,27b', '„És [Isten] letöröl minden könnyet a szemükről, és halál sem lesz többé, sem gyász, sem jajkiáltás, sem fájdalom nem lesz többé, mert az elsők elmúltak.” (Jel 21,4)', 'Jel 21,4', 'https://szentiras.hu/RUF/Jel 21,4', 'admin');
INSERT INTO readings (reading_year, reading_month, reading_day, firstreading, firstreading_short, firstreading_link, secondreading, secondreading_short, secondreading_link, created_by) VALUES ('2024', '04', '4', '„Én vagyok az Úr, senki más! Én alkottam a világosságot, én teremtettem a sötétséget, én szerzek békességet, én teremtek bajt, én, az Úr cselekszem mindezt.” (Ézs 45,6b–7)', 'Ézs 45,6b–7', 'https://szentiras.hu/RUF/Ézs 45,6b–7', '„Halott voltam, de íme, élek örökkön-örökké, és nálam vannak a halál és a pokol kulcsai.” (Jel 1,18b)', 'Jel 1,18b', 'https://szentiras.hu/RUF/Jel 1,18b', 'admin');
INSERT INTO readings (reading_year, reading_month, reading_day, firstreading, firstreading_short, firstreading_link, secondreading, secondreading_short, secondreading_link, created_by) VALUES ('2024', '04', '5', '„De én visszaemlékezem szövetségemre, amelyet ifjúkorodban kötöttem veled, és örök szövetségre lépek veled.” (Ez 16,60)', 'Ez 16,60', 'https://szentiras.hu/RUF/Ez 16,60', '„Ő tett alkalmassá minket arra, hogy az új szövetség szolgái legyünk, nem a betűé, hanem a Léleké, mert a betű megöl, a Lélek pedig megelevenít.” (2Kor 3,6)', '2Kor 3,6', 'https://szentiras.hu/RUF/2Kor 3,6', 'admin');
INSERT INTO readings (reading_year, reading_month, reading_day, firstreading, firstreading_short, firstreading_link, secondreading, secondreading_short, secondreading_link, created_by) VALUES ('2024', '04', '6', '„Nyelvem hirdeti igazságodat és dicséretedet mindennap.” (Zsolt 35,28)', 'Zsolt 35,28', 'https://szentiras.hu/RUF/Zsolt 35,28', '„Krisztus beszéde lakjék bennetek gazdagon úgy, hogy tanítsátok egymást teljes bölcsességgel, és intsétek egymást zsoltárokkal, dicséretekkel, lelki énekekkel; hálaadással énekeljetek szívetekben Istennek.” (Kol 3,16)', 'Kol 3,16', 'https://szentiras.hu/RUF/Kol 3,16', 'admin');
INSERT INTO readings (reading_year, reading_month, reading_day, firstreading, firstreading_short, firstreading_link, secondreading, secondreading_short, secondreading_link, created_by) VALUES ('2024', '04', '7', '„Izráel fiai ekkor azt mondták az Úrnak: Vétkeztünk. Tégy velünk bármit, ahogy jónak látod, csak ments most meg bennünket!” (Bír 10,15)', 'Bír 10,15', 'https://szentiras.hu/RUF/Bír 10,15', '„A vámszedő pedig távol állva, még szemét sem akarta az égre emelni, hanem a mellét verve így szólt: Istenem, légy irgalmas nekem, bűnösnek!” (Lk 18,13)', 'Lk 18,13', 'https://szentiras.hu/RUF/Lk 18,13', 'admin');
INSERT INTO readings (reading_year, reading_month, reading_day, firstreading, firstreading_short, firstreading_link, secondreading, secondreading_short, secondreading_link, created_by) VALUES ('2024', '04', '8', '„Boldog ember az, aki az Urat féli, sok örömöt talál parancsolataiban.” (Zsolt 112,1b)', 'Zsolt 112,1b', 'https://szentiras.hu/RUF/Zsolt 112,1b', '„…Krisztus is szenvedett értetek, és példát adott nektek, hogy az ő nyomdokait kövessétek” (1Pt 2,21b)', '1Pt 2,21b', 'https://szentiras.hu/RUF/1Pt 2,21b', 'admin');
INSERT INTO readings (reading_year, reading_month, reading_day, firstreading, firstreading_short, firstreading_link, secondreading, secondreading_short, secondreading_link, created_by) VALUES ('2024', '04', '9', '„Hiszen nem kívánom én a bűnös ember halálát – így szól az én Uram, az Úr –, hanem azt, hogy megtérjen útjáról, és éljen.” (Ez 18,23)', 'Ez 18,23', 'https://szentiras.hu/RUF/Ez 18,23', '„Mert a bűn zsoldja a halál, Isten kegyelmi ajándéka pedig az örök élet Krisztus Jézusban, a mi Urunkban.” (Róm 6,23)', 'Róm 6,23', 'https://szentiras.hu/RUF/Róm 6,23', 'admin');
INSERT INTO readings (reading_year, reading_month, reading_day, firstreading, firstreading_short, firstreading_link, secondreading, secondreading_short, secondreading_link, created_by) VALUES ('2024', '04', '10', '„Mert én, az Úr vagyok a te Istened, Izráel Szentje, a te szabadítód!” (Ézs 43,3a)', 'Ézs 43,3a', 'https://szentiras.hu/RUF/Ézs 43,3a', '„És hang hallatszott a felhőből: Ez az én Fiam, akit kiválasztottam, reá hallgassatok! Amikor a hang hallatszott, Jézust egyedül találták.” (Lk 9,35–36a)', 'Lk 9,35–36a', 'https://szentiras.hu/RUF/Lk 9,35–36a', 'admin');
INSERT INTO readings (reading_year, reading_month, reading_day, firstreading, firstreading_short, firstreading_link, secondreading, secondreading_short, secondreading_link, created_by) VALUES ('2024', '04', '11', '„Én, én vagyok a vigasztalótok! Miért félsz a halandótól, az olyan embertől, aki a fű sorsára jut?” (Ézs 51,12)', 'Ézs 51,12', 'https://szentiras.hu/RUF/Ézs 51,12', '„…legyetek készen mindenkor számot adni mindenkinek, aki számonkéri tőletek a bennetek élő reménységet.” (1Pt 3,15b)', '1Pt 3,15b', 'https://szentiras.hu/RUF/1Pt 3,15b', 'admin');
INSERT INTO readings (reading_year, reading_month, reading_day, firstreading, firstreading_short, firstreading_link, secondreading, secondreading_short, secondreading_link, created_by) VALUES ('2024', '04', '12', '„Szövetségre lépek veled, és megtudod, hogy én vagyok az Úr.” (Ez 16,62)', 'Ez 16,62', 'https://szentiras.hu/RUF/Ez 16,62', '„Ezért tehát nem azé, aki akarja, sem nem azé, aki fut, hanem a könyörülő Istené.” (Róm 9,16)', 'Róm 9,16', 'https://szentiras.hu/RUF/Róm 9,16', 'admin');
INSERT INTO readings (reading_year, reading_month, reading_day, firstreading, firstreading_short, firstreading_link, secondreading, secondreading_short, secondreading_link, created_by) VALUES ('2024', '04', '13', '„Ő [Isten] megment, megszabadít, jeleket és csodákat tesz égen és földön.” (Dán 6,28a)', 'Dán 6,28a', 'https://szentiras.hu/RUF/Dán 6,28a', '„…többé tehát nem én élek, hanem Krisztus él bennem; azt az életet pedig, amelyet most testben élek, az Isten Fiában való hitben élem, aki szeretett engem, és önmagát adta értem.” (Gal 2,20b)', 'Gal 2,20b', 'https://szentiras.hu/RUF/Gal 2,20b', 'admin');
INSERT INTO readings (reading_year, reading_month, reading_day, firstreading, firstreading_short, firstreading_link, secondreading, secondreading_short, secondreading_link, created_by) VALUES ('2024', '04', '14', '„Isten ruház föl engem erővel, ő teszi tökéletessé utamat.” (Zsolt 18,33)', 'Zsolt 18,33', 'https://szentiras.hu/RUF/Zsolt 18,33', '„Isten »erőtlensége« erősebb az emberek erejénél.” (1Kor 1,25b)', '1Kor 1,25b', 'https://szentiras.hu/RUF/1Kor 1,25b', 'admin');
INSERT INTO readings (reading_year, reading_month, reading_day, firstreading, firstreading_short, firstreading_link, secondreading, secondreading_short, secondreading_link, created_by) VALUES ('2024', '04', '15', 'Ámósz ezt mondta: „Ó, Uram, Uram! Bocsáss meg! Mi lesz így Jákóbbal, hiszen oly kicsiny? Megbánta ezt az Úr: Nem történik meg! – mondta az Úr.” (Ám 7,2b–3)', 'Ám 7,2b–3', 'https://szentiras.hu/RUF/Ám 7,2b–3', 'Jézus így szólt: „Simon, Simon, íme, a Sátán kikért titeket, hogy megrostáljon, mint a búzát, de én könyörögtem érted, hogy el ne fogyatkozzék a hited […].” (Lk 22,31–32a)', 'Lk 22,31–32a', 'https://szentiras.hu/RUF/Lk 22,31–32a', 'admin');
INSERT INTO readings (reading_year, reading_month, reading_day, firstreading, firstreading_short, firstreading_link, secondreading, secondreading_short, secondreading_link, created_by) VALUES ('2024', '04', '16', '„Az én Istenem fénysugarat küld nekem a sötétbe.” (Zsolt 18,29b)', 'Zsolt 18,29b', 'https://szentiras.hu/RUF/Zsolt 18,29b', '„Istenünk könyörülő irgalmáért, amellyel meglátogat minket a felkelő nap a magasságból, hogy világítson azoknak, akik sötétségben és a halál árnyékában lakoznak, hogy lábunkat a békesség útjára igazítsa.” (Lk 1,78–79)', 'Lk 1,78–79', 'https://szentiras.hu/RUF/Lk 1,78–79', 'admin');
INSERT INTO readings (reading_year, reading_month, reading_day, firstreading, firstreading_short, firstreading_link, secondreading, secondreading_short, secondreading_link, created_by) VALUES ('2024', '04', '17', '„Áldjad, lelkem, az Urat, és egész bensőm az ő szent nevét!” (Zsolt 103,1)', 'Zsolt 103,1', 'https://szentiras.hu/RUF/Zsolt 103,1', '„Áldott a mi Urunk Jézus Krisztus Istene és Atyja, aki nagy irgalmából újjászült minket Jézus Krisztusnak a halottak közül való feltámadása által élő reménységre.” (1Pt 1,3)', '1Pt 1,3', 'https://szentiras.hu/RUF/1Pt 1,3', 'admin');
INSERT INTO readings (reading_year, reading_month, reading_day, firstreading, firstreading_short, firstreading_link, secondreading, secondreading_short, secondreading_link, created_by) VALUES ('2024', '04', '18', '„Kiálts és ujjongj, Sion lakója, mert nagy közöttetek Izráel Szentje!” (Ézs 12,6)', 'Ézs 12,6', 'https://szentiras.hu/RUF/Ézs 12,6', '„Az Ige testté lett, közöttünk lakott, és láttuk az ő dicsőségét, mint az Atya egyszülöttjének dicsőségét, telve kegyelemmel és igazsággal.” (Jn 1,14)', 'Jn 1,14', 'https://szentiras.hu/RUF/Jn 1,14', 'admin');
INSERT INTO readings (reading_year, reading_month, reading_day, firstreading, firstreading_short, firstreading_link, secondreading, secondreading_short, secondreading_link, created_by) VALUES ('2024', '04', '19', '„Nóé […] mindenben úgy járt el, ahogyan Isten megparancsolta neki.” (1Móz 6,22)', '1Móz 6,22', 'https://szentiras.hu/RUF/1Móz 6,22', 'Jézus mondja: „Aki tehát hallja tőlem ezeket a beszédeket, és cselekszi azokat, hasonló a bölcs emberhez, aki kősziklára építette a házát.” (Mt 7,24)', 'Mt 7,24', 'https://szentiras.hu/RUF/Mt 7,24', 'admin');
INSERT INTO readings (reading_year, reading_month, reading_day, firstreading, firstreading_short, firstreading_link, secondreading, secondreading_short, secondreading_link, created_by) VALUES ('2024', '04', '20', '„Mert én megszabadítalak, nem esel el fegyvertől, és életedet ajándékul kapod, mert bíztál bennem! – így szól az Úr.” (Jer 39,18)', 'Jer 39,18', 'https://szentiras.hu/RUF/Jer 39,18', '„Jézus így felelt neki [a gonosztevőnek]: Bizony mondom neked, ma velem leszel a paradicsomban.” (Lk 23,43)', 'Lk 23,43', 'https://szentiras.hu/RUF/Lk 23,43', 'admin');
INSERT INTO readings (reading_year, reading_month, reading_day, firstreading, firstreading_short, firstreading_link, secondreading, secondreading_short, secondreading_link, created_by) VALUES ('2024', '04', '21', '„A rágalmazó titkokat tár fel, de a hűséges lélek leplezi a dolgot.” (Péld 11,13)', 'Péld 11,13', 'https://szentiras.hu/RUF/Péld 11,13', '„Az igazsághoz ragaszkodva növekedjünk fel szeretetben mindenestől őhozzá, aki a fej, a Krisztus.” (Ef 4,15)', 'Ef 4,15', 'https://szentiras.hu/RUF/Ef 4,15', 'admin');
INSERT INTO readings (reading_year, reading_month, reading_day, firstreading, firstreading_short, firstreading_link, secondreading, secondreading_short, secondreading_link, created_by) VALUES ('2024', '04', '22', '„Áldott legyen […] Isten, mert elküldte az angyalát, és kiszabadította szolgáit, akik benne bíztak.” (Dán 3,28a)', 'Dán 3,28a', 'https://szentiras.hu/RUF/Dán 3,28a', '„[Jézus] negyven napig volt a pusztában, miközben kísértette a Sátán. Vadállatok között élt, és angyalok szolgáltak neki.” (Mk 1,13)', 'Mk 1,13', 'https://szentiras.hu/RUF/Mk 1,13', 'admin');
INSERT INTO readings (reading_year, reading_month, reading_day, firstreading, firstreading_short, firstreading_link, secondreading, secondreading_short, secondreading_link, created_by) VALUES ('2024', '04', '23', '„Isten útja tökéletes, az Úr beszéde színigaz. Pajzsa mindazoknak, akik hozzá menekülnek.” (Zsolt 18,31)', 'Zsolt 18,31', 'https://szentiras.hu/RUF/Zsolt 18,31', '„Jézus ezt is mondta: Úgy van az Isten országa, mint amikor az ember magot vet a földbe, azután alszik és felkel, éjjel és nappal: a mag sarjad és nő, az ember pedig nem tudja, hogyan. Magától terem a föld […].” (Mk 4,26–28a)', 'Mk 4,26–28a', 'https://szentiras.hu/RUF/Mk 4,26–28a', 'admin');
INSERT INTO readings (reading_year, reading_month, reading_day, firstreading, firstreading_short, firstreading_link, secondreading, secondreading_short, secondreading_link, created_by) VALUES ('2024', '04', '24', '„Ne félj, mert nem vallasz szégyent, ne pironkodj, mert nem ér gyalázat!” (Ézs 54,4a)', 'Ézs 54,4a', 'https://szentiras.hu/RUF/Ézs 54,4a', '„Az asszonyokat pedig így szólította meg az angyal: Ti ne féljetek! Mert tudom, hogy a megfeszített Jézust keresitek. Nincsen itt, mert feltámadt, amint megmondta.” (Mt 28,5–6a)', 'Mt 28,5–6a', 'https://szentiras.hu/RUF/Mt 28,5–6a', 'admin');
INSERT INTO readings (reading_year, reading_month, reading_day, firstreading, firstreading_short, firstreading_link, secondreading, secondreading_short, secondreading_link, created_by) VALUES ('2024', '04', '25', '„Az Úrtól jön a szabadítás. Legyen áldásod népeden!” (Zsolt 3,9)', 'Zsolt 3,9', 'https://szentiras.hu/RUF/Zsolt 3,9', '„[Jézus] felemelte a kezét, és megáldotta őket.” (Lk 24,50)', 'Lk 24,50', 'https://szentiras.hu/RUF/Lk 24,50', 'admin');
INSERT INTO readings (reading_year, reading_month, reading_day, firstreading, firstreading_short, firstreading_link, secondreading, secondreading_short, secondreading_link, created_by) VALUES ('2024', '04', '26', '„Féld Istent, és tartsd meg parancsolatait, mert ez minden embernek kötelessége!” (Préd 12,13b)', 'Préd 12,13b', 'https://szentiras.hu/RUF/Préd 12,13b', '„Jézus így válaszolt: A legfőbb ez: »Halld meg, Izráel: Az Úr a mi Istenünk, egyedül az Úr!« és: »Szeresd az Urat, a te Istenedet teljes szívedből, teljes lelkedből, teljes elmédből és teljes erődből!« A második ez: »Szeresd felebarátodat, mint magadat!« Nincsen más, ezeknél nagyobb parancsolat.” (Mk 12,29–31)', 'Mk 12,29–31', 'https://szentiras.hu/RUF/Mk 12,29–31', 'admin');
INSERT INTO readings (reading_year, reading_month, reading_day, firstreading, firstreading_short, firstreading_link, secondreading, secondreading_short, secondreading_link, created_by) VALUES ('2024', '04', '27', '„Ezt mondod: Ártatlan vagyok, hiszen [az Úr] haragja is elfordult rólam! Pedig ítéletet tartok fölötted, hiába mondod: Nem vétkeztem!” (Jer 2,35)', 'Jer 2,35', 'https://szentiras.hu/RUF/Jer 2,35', '„Jézus, emlékezzél meg rólam, amikor eljössz a te királyságodba!” (Lk 23,42)', 'Lk 23,42', 'https://szentiras.hu/RUF/Lk 23,42', 'admin');
INSERT INTO readings (reading_year, reading_month, reading_day, firstreading, firstreading_short, firstreading_link, secondreading, secondreading_short, secondreading_link, created_by) VALUES ('2024', '04', '28', '„Taníts engem utaidra, Uram, hogy igazságod szerint járjak, és teljes szívvel féljem nevedet.” (Zsolt 86,11)', 'Zsolt 86,11', 'https://szentiras.hu/RUF/Zsolt 86,11', '„Ezért fel is magasztalta őt Isten mindenek fölé, és azt a nevet adományozta neki, amely minden névnél nagyobb, hogy Jézus nevére minden térd meghajoljon, mennyeieké, földieké és föld alattiaké; és minden nyelv vallja, hogy Jézus Krisztus Úr az Atya Isten dicsőségére.” (Fil 2,9–11)', 'Fil 2,9–11', 'https://szentiras.hu/RUF/Fil 2,9–11', 'admin');
INSERT INTO readings (reading_year, reading_month, reading_day, firstreading, firstreading_short, firstreading_link, secondreading, secondreading_short, secondreading_link, created_by) VALUES ('2024', '04', '29', '„Igazságos velem az Úr, mert szembeszálltam parancsával.” (JSir 1,18a)', 'JSir 1,18a', 'https://szentiras.hu/RUF/JSir 1,18a', '„Erről pedig mit gondoltok? Egy embernek volt két fia. Az elsőhöz fordulva ezt mondta: Fiam, menj el, dolgozz ma a szőlőben! Ő így felelt: Nem akarok. Később azonban meggondolta magát, és elment.” (Mt 21,28–29)', 'Mt 21,28–29', 'https://szentiras.hu/RUF/Mt 21,28–29', 'admin');
INSERT INTO readings (reading_year, reading_month, reading_day, firstreading, firstreading_short, firstreading_link, secondreading, secondreading_short, secondreading_link, created_by) VALUES ('2024', '04', '30', '„Gondolj, Uram, irgalmadra és kegyelmedre, melyek öröktől fogva vannak.” (Zsolt 25,6)', 'Zsolt 25,6', 'https://szentiras.hu/RUF/Zsolt 25,6', '„De amikor megjelent a mi üdvözítő Istenünk jósága és emberszeretete, nem az általunk véghezvitt igaz cselekedetekért, hanem az ő irgalmából üdvözített minket újjászülő és megújító fürdője a Szentlélek által.” (Tit 3,4–5)', 'Tit 3,4–5', 'https://szentiras.hu/RUF/Tit 3,4–5', 'admin');
INSERT INTO readings (reading_year, reading_month, reading_day, firstreading, firstreading_short, firstreading_link, secondreading, secondreading_short, secondreading_link, created_by) VALUES ('2024', '05', '1', '„Én veled leszek – így szól az Úr –, és megszabadítalak.” (Jer 30,11a)', 'Jer 30,11a', 'https://szentiras.hu/RUF/Jer 30,11a', '„Mivel maga is kísértést szenvedett, segíteni tud azokon, akik kísértésbe esnek.” (Zsid 2,18)', 'Zsid 2,18', 'https://szentiras.hu/RUF/Zsid 2,18', 'admin');
INSERT INTO readings (reading_year, reading_month, reading_day, firstreading, firstreading_short, firstreading_link, secondreading, secondreading_short, secondreading_link, created_by) VALUES ('2024', '05', '2', 'Így szól az Úr: „Bárcsak rám hallgatna népem, az én utaimon járna Izráel!” (Zsolt 81,14)', 'Zsolt 81,14', 'https://szentiras.hu/RUF/Zsolt 81,14', '„[Maradjatok meg] a hitben szilárdan és egyenesen, el nem tántorodva az evangélium reménységétől, amelyet hallottatok […].” (Kol 1,23a)', 'Kol 1,23a', 'https://szentiras.hu/RUF/Kol 1,23a', 'admin');
INSERT INTO readings (reading_year, reading_month, reading_day, firstreading, firstreading_short, firstreading_link, secondreading, secondreading_short, secondreading_link, created_by) VALUES ('2024', '05', '3', '„A süketet ne szidalmazd, a vak elé ne vess gáncsot! Félj Istenedtől […].” (3Móz 19,14a)', '3Móz 19,14a', 'https://szentiras.hu/RUF/3Móz 19,14a', 'Jézus mondja: „Legyetek irgalmasok, amint a ti Atyátok is irgalmas!” (Lk 6,36)', 'Lk 6,36', 'https://szentiras.hu/RUF/Lk 6,36', 'admin');
INSERT INTO readings (reading_year, reading_month, reading_day, firstreading, firstreading_short, firstreading_link, secondreading, secondreading_short, secondreading_link, created_by) VALUES ('2024', '05', '4', '„Ehettek majd jóllakásig, és dicséritek az Úrnak, Isteneteknek a nevét, mert csodát tett veletek.” (Jóel 2,26a)', 'Jóel 2,26a', 'https://szentiras.hu/RUF/Jóel 2,26a', '„Jézus pedig vette az öt kenyeret és a két halat, feltekintett az égre, megáldotta és megtörte a kenyereket, majd átadta a tanítványoknak, hogy tegyék eléjük; és a két halat is elosztotta mindnyájuknak. […] mind ettek, és jóllaktak.” (Mk 6,41–42)', 'Mk 6,41–42', 'https://szentiras.hu/RUF/Mk 6,41–42', 'admin');
INSERT INTO readings (reading_year, reading_month, reading_day, firstreading, firstreading_short, firstreading_link, secondreading, secondreading_short, secondreading_link, created_by) VALUES ('2024', '05', '5', '„Ahogyan a szarvas kívánkozik a folyóvízhez, úgy kívánkozik a lelkem hozzád, Istenem!” (Zsolt 42,2)', 'Zsolt 42,2', 'https://szentiras.hu/RUF/Zsolt 42,2', '„Az apostolok így szóltak az Úrhoz: Növeld a hitünket!” (Lk 17,5)', 'Lk 17,5', 'https://szentiras.hu/RUF/Lk 17,5', 'admin');
INSERT INTO readings (reading_year, reading_month, reading_day, firstreading, firstreading_short, firstreading_link, secondreading, secondreading_short, secondreading_link, created_by) VALUES ('2024', '05', '6', '„[Az Úr] megóv engem sátrában a veszedelem napján. Elrejt sátra mélyén, magas kősziklára helyez engem.” (Zsolt 27,5)', 'Zsolt 27,5', 'https://szentiras.hu/RUF/Zsolt 27,5', '„De hűséges az Úr, aki megerősít titeket, és megőriz a gonosztól.” (2Thessz 3,3)', '2Thessz 3,3', 'https://szentiras.hu/RUF/2Thessz 3,3', 'admin');
INSERT INTO readings (reading_year, reading_month, reading_day, firstreading, firstreading_short, firstreading_link, secondreading, secondreading_short, secondreading_link, created_by) VALUES ('2024', '05', '7', '„A tökéletes szépségű Sionon ragyogva jelenik meg Isten. Jön a mi Istenünk, nem hallgat.” (Zsolt 50,2–3a)', 'Zsolt 50,2–3a', 'https://szentiras.hu/RUF/Zsolt 50,2–3a', '„Az írástudó ezt mondta neki [Jézusnak]: Úgy van, Mester, helyesen mondtad, hogy egy Isten van, és rajta kívül nincsen más.” (Mk 12,32)', 'Mk 12,32', 'https://szentiras.hu/RUF/Mk 12,32', 'admin');
INSERT INTO readings (reading_year, reading_month, reading_day, firstreading, firstreading_short, firstreading_link, secondreading, secondreading_short, secondreading_link, created_by) VALUES ('2024', '05', '8', '„[Az Úr] új éneket adott a számba, Istenünknek dicséretét.” (Zsolt 40,4a)', 'Zsolt 40,4a', 'https://szentiras.hu/RUF/Zsolt 40,4a', '„Újuljatok meg lelketekben és elmétekben.” (Ef 4,23)', 'Ef 4,23', 'https://szentiras.hu/RUF/Ef 4,23', 'admin');
INSERT INTO readings (reading_year, reading_month, reading_day, firstreading, firstreading_short, firstreading_link, secondreading, secondreading_short, secondreading_link, created_by) VALUES ('2024', '05', '9', '„Megtartották a kovásztalan kenyerek ünnepét is hét napon át örvendezve, mert megörvendeztette őket az Úr […].” (Ezsd 6,22a)', 'Ezsd 6,22a', 'https://szentiras.hu/RUF/Ezsd 6,22a', '„Mindenkor örüljetek.” (1Thessz 5,16)', '1Thessz 5,16', 'https://szentiras.hu/RUF/1Thessz 5,16', 'admin');
INSERT INTO readings (reading_year, reading_month, reading_day, firstreading, firstreading_short, firstreading_link, secondreading, secondreading_short, secondreading_link, created_by) VALUES ('2024', '05', '10', 'Így szól az Úr: „Hozzátok fordulok, megszaporítalak és megsokasítalak benneteket, és fenntartom veletek szövetségemet.” (3Móz 26,9)', '3Móz 26,9', 'https://szentiras.hu/RUF/3Móz 26,9', '„…az igazsághoz ragaszkodva növekedjünk fel szeretetben mindenestől őhozzá, aki a fej, a Krisztus, akiből az egész test egybeilleszkedik és összekapcsolódik a különféle ízületek segítségével úgy, hogy minden egyes tagja erejéhez mérten közösen munkálja a test növekedését, hogy épüljön szeretetben.” (Ef 4,15–16a)', 'Ef 4,15–16a', 'https://szentiras.hu/RUF/Ef 4,15–16a', 'admin');
INSERT INTO readings (reading_year, reading_month, reading_day, firstreading, firstreading_short, firstreading_link, secondreading, secondreading_short, secondreading_link, created_by) VALUES ('2024', '05', '11', '„Mert ő [Isten] megsebez, de be is kötöz, összezúz, de keze meg is gyógyít.” (Jób 5,18)', 'Jób 5,18', 'https://szentiras.hu/RUF/Jób 5,18', '„Bizony, őtőle, őáltala és őreá nézve van minden: övé a dicsőség mindörökké. Ámen.” (Róm 11,36)', 'Róm 11,36', 'https://szentiras.hu/RUF/Róm 11,36', 'admin');
INSERT INTO readings (reading_year, reading_month, reading_day, firstreading, firstreading_short, firstreading_link, secondreading, secondreading_short, secondreading_link, created_by) VALUES ('2024', '05', '12', '„Mert megpróbáltál minket, Istenünk, megtisztítottál, mint az ezüstöt.” (Zsolt 66,10)', 'Zsolt 66,10', 'https://szentiras.hu/RUF/Zsolt 66,10', '„Harmadszor is szólt hozzá [Jézus]: Simon, Jóna fia, szeretsz-e engem? Péter elszomorodott, hogy harmadszor is megkérdezte tőle: Szeretsz-e engem? Ezért ezt mondta neki: Uram, te mindent tudsz, te tudod, hogy szeretlek téged. Jézus ezt mondta neki: Legeltesd az én juhaimat!” (Jn 21,17)', 'Jn 21,17', 'https://szentiras.hu/RUF/Jn 21,17', 'admin');
INSERT INTO readings (reading_year, reading_month, reading_day, firstreading, firstreading_short, firstreading_link, secondreading, secondreading_short, secondreading_link, created_by) VALUES ('2024', '05', '13', '„Hagyd abba a hangos sírást, ne könnyezzenek szemeid! Mert meglesz szenvedésed jutalma – így szól az Úr […].” (Jer 31,16a)', 'Jer 31,16a', 'https://szentiras.hu/RUF/Jer 31,16a', 'Jézus mondja: „Így most ti is szomorúak vagytok, de ismét meglátlak majd titeket, és örülni fog a szívetek, és örömötöket senki sem veheti el tőletek.” (Jn 16,22)', 'Jn 16,22', 'https://szentiras.hu/RUF/Jn 16,22', 'admin');
INSERT INTO readings (reading_year, reading_month, reading_day, firstreading, firstreading_short, firstreading_link, secondreading, secondreading_short, secondreading_link, created_by) VALUES ('2024', '05', '14', '„Istenünk ereje mindazoknak a javára van, akik őt keresik, de hatalma és haragja fordul mindazok ellen, akik őt elhagyják.” (Ezsd 8,22b)', 'Ezsd 8,22b', 'https://szentiras.hu/RUF/Ezsd 8,22b', '„Jézus ekkor megkérdezte a tizenkettőtől: Vajon ti is el akartok menni?” (Jn 6,67)', 'Jn 6,67', 'https://szentiras.hu/RUF/Jn 6,67', 'admin');
INSERT INTO readings (reading_year, reading_month, reading_day, firstreading, firstreading_short, firstreading_link, secondreading, secondreading_short, secondreading_link, created_by) VALUES ('2024', '05', '15', '„[Uram,] légy segítségünk reggelenként, szabadítónk a nyomorúság idején!” (Ézs 33,2)', 'Ézs 33,2', 'https://szentiras.hu/RUF/Ézs 33,2', '„Kik ezek a fehér ruhába öltözöttek, és honnan jöttek? […] Ezek azok, akik a nagy nyomorúságból jöttek, és megmosták ruhájukat, és megfehérítették a Bárány vérében. […] Nem éheznek és nem szomjaznak többé […].” (Jel 7,13b.14b.16a)', 'Jel 7,13b.14b.16a', 'https://szentiras.hu/RUF/Jel 7,13b.14b.16a', 'admin');
INSERT INTO readings (reading_year, reading_month, reading_day, firstreading, firstreading_short, firstreading_link, secondreading, secondreading_short, secondreading_link, created_by) VALUES ('2024', '05', '16', '„Megöntözöd onnan fentről a hegyeket, alkotásaid gyümölcsével jól tartod a földet.” (Zsolt 104,13)', 'Zsolt 104,13', 'https://szentiras.hu/RUF/Zsolt 104,13', '„Amikor pedig jóllaktak, [Jézus] így szólt tanítványaihoz: Szedjétek össze a maradékot, hogy semmi se vesszen kárba! Összeszedték tehát, és tizenkét kosarat töltöttek meg az öt árpakenyér maradékával, amit meghagytak azok, akik ettek.” (Jn 6,12–13)', 'Jn 6,12–13', 'https://szentiras.hu/RUF/Jn 6,12–13', 'admin');
INSERT INTO readings (reading_year, reading_month, reading_day, firstreading, firstreading_short, firstreading_link, secondreading, secondreading_short, secondreading_link, created_by) VALUES ('2024', '05', '17', '„Ne sanyargasd a jövevényt, hiszen ti ismeritek a jövevény életét, mert ti is jövevények voltatok Egyiptomban.” (2Móz 23,9)', '2Móz 23,9', 'https://szentiras.hu/RUF/2Móz 23,9', '„Akkor így válaszolnak neki az igazak: […] Mikor láttunk jövevénynek, hogy befogadtunk volna? […] A király így felel majd nekik: Bizony mondom nektek, valahányszor megtettétek ezeket akár csak eggyel is az én legkisebb testvéreim közül, velem tettétek meg.” (Mt 25,37a.38a.40)', 'Mt 25,37a.38a.40', 'https://szentiras.hu/RUF/Mt 25,37a.38a.40', 'admin');
INSERT INTO readings (reading_year, reading_month, reading_day, firstreading, firstreading_short, firstreading_link, secondreading, secondreading_short, secondreading_link, created_by) VALUES ('2024', '05', '18', '„Az Úr pedig azt mondta: Megláttam népem nyomorúságát Egyiptomban, és meghallottam segélykiáltásukat a sanyargatók miatt, mert ismerem fájdalmukat.” (2Móz 3,7)', '2Móz 3,7', 'https://szentiras.hu/RUF/2Móz 3,7', '„Ha egy férfi- vagy nőtestvérünknek nincs ruhája, és nincs meg a mindennapi kenyere, valaki pedig ezt mondja nekik közületek: »Menjetek el békességgel, melegedjetek meg, és lakjatok jól«, de nem adjátok meg nekik, amire a testnek szüksége van, mit használ az?” (Jak 2,15–16)', 'Jak 2,15–16', 'https://szentiras.hu/RUF/Jak 2,15–16', 'admin');
INSERT INTO readings (reading_year, reading_month, reading_day, firstreading, firstreading_short, firstreading_link, secondreading, secondreading_short, secondreading_link, created_by) VALUES ('2024', '05', '19', '„Áradjon a törvény, mint a víz, és az igazság, mint a bővizű patak!” (Ám 5,24)', 'Ám 5,24', 'https://szentiras.hu/RUF/Ám 5,24', '„Amikor azonban eljön ő, az igazság Lelke, elvezet titeket a teljes igazságra; mert nem önmagától szól, hanem azokat mondja, amiket hall, és az eljövendő dolgokat is kijelenti nektek.” (Jn 16,13)', 'Jn 16,13', 'https://szentiras.hu/RUF/Jn 16,13', 'admin');
INSERT INTO readings (reading_year, reading_month, reading_day, firstreading, firstreading_short, firstreading_link, secondreading, secondreading_short, secondreading_link, created_by) VALUES ('2024', '05', '20', '„Ezt mondja az Úr: A menny az én trónusom, a föld pedig lábam zsámolya. Milyen házat akartok építeni nekem […]?” (Ézs 66,1a)', 'Ézs 66,1a', 'https://szentiras.hu/RUF/Ézs 66,1a', '„Nem tudjátok, hogy ti Isten temploma vagytok, és az Isten Lelke bennetek lakik?” (1Kor 3,16)', '1Kor 3,16', 'https://szentiras.hu/RUF/1Kor 3,16', 'admin');
INSERT INTO readings (reading_year, reading_month, reading_day, firstreading, firstreading_short, firstreading_link, secondreading, secondreading_short, secondreading_link, created_by) VALUES ('2024', '05', '21', '„Én, az Úr vagyok a védelmezője, minduntalan öntözgetem. Éjjel-nappal őrzöm, hogy senki se bánthassa.” (Ézs 27,3)', 'Ézs 27,3', 'https://szentiras.hu/RUF/Ézs 27,3', '„Isten pedig hűséges, és nem hagy titeket erőtökön felül kísérteni; sőt a kísértéssel együtt el fogja készíteni a szabadulás útját is, hogy el bírjátok azt viselni.” (1Kor 10,13b)', '1Kor 10,13b', 'https://szentiras.hu/RUF/1Kor 10,13b', 'admin');
INSERT INTO readings (reading_year, reading_month, reading_day, firstreading, firstreading_short, firstreading_link, secondreading, secondreading_short, secondreading_link, created_by) VALUES ('2024', '05', '22', '„Gondoskodott népe megváltásáról, örökre elrendelte szövetségét, szent és félelmetes az ő neve.” (Zsolt 111,9)', 'Zsolt 111,9', 'https://szentiras.hu/RUF/Zsolt 111,9', '„Mert ő [Jézus Krisztus] engesztelő áldozat a mi bűneinkért, de nemcsak a miénkért, hanem az egész világ bűnéért is.” (1Jn 2,2)', '1Jn 2,2', 'https://szentiras.hu/RUF/1Jn 2,2', 'admin');
INSERT INTO readings (reading_year, reading_month, reading_day, firstreading, firstreading_short, firstreading_link, secondreading, secondreading_short, secondreading_link, created_by) VALUES ('2024', '05', '23', '„A nép, amely sötétségben jár, nagy világosságot lát, a homály földjén lakókra világosság ragyog.” (Ézs 9,1)', 'Ézs 9,1', 'https://szentiras.hu/RUF/Ézs 9,1', '„Amikor ezt [a csillagot] látták [a bölcsek], igen nagy volt az örömük. Bementek a házba, meglátták a gyermeket anyjával, Máriával, és leborulva imádták őt.” (Mt 2,10–11)', 'Mt 2,10–11', 'https://szentiras.hu/RUF/Mt 2,10–11', 'admin');
INSERT INTO readings (reading_year, reading_month, reading_day, firstreading, firstreading_short, firstreading_link, secondreading, secondreading_short, secondreading_link, created_by) VALUES ('2024', '05', '24', '„Távol legyen tőlünk, hogy elhagyjuk az Urat, és más isteneket szolgáljunk!” (Józs 24,16b)', 'Józs 24,16b', 'https://szentiras.hu/RUF/Józs 24,16b', '„Simon Péter így felelt: Uram, kihez mennénk? Örök élet beszéde van nálad.” (Jn 6,68)', 'Jn 6,68', 'https://szentiras.hu/RUF/Jn 6,68', 'admin');
INSERT INTO readings (reading_year, reading_month, reading_day, firstreading, firstreading_short, firstreading_link, secondreading, secondreading_short, secondreading_link, created_by) VALUES ('2024', '05', '25', '„Uram, légy kegyelmes hozzám! Gyógyíts meg engem, mert vétkeztem ellened!” (Zsolt 41,5)', 'Zsolt 41,5', 'https://szentiras.hu/RUF/Zsolt 41,5', 'Pál írja: „Mert én a legkisebb vagyok az apostolok között, aki arra sem vagyok méltó, hogy apostolnak nevezzenek, mert üldöztem Isten egyházát. De Isten kegyelméből vagyok, ami vagyok, és hozzám való kegyelme nem lett hiábavaló […].” (1Kor 15,9–10)', '1Kor 15,9–10', 'https://szentiras.hu/RUF/1Kor 15,9–10', 'admin');
INSERT INTO readings (reading_year, reading_month, reading_day, firstreading, firstreading_short, firstreading_link, secondreading, secondreading_short, secondreading_link, created_by) VALUES ('2024', '05', '26', '„[Uram,] te látod a vészt és a bánatot, rátekintesz, és kezedben tartod.” (Zsolt 10,14a)', 'Zsolt 10,14a', 'https://szentiras.hu/RUF/Zsolt 10,14a', '„Amikor Jézus meglátta őt, előszólította, és ezt mondta neki: Asszony, megszabadultál betegségedből. És rátette a kezét, mire ő nyomban felegyenesedett, és dicsőítette az Istent.” (Lk 13,12–13)', 'Lk 13,12–13', 'https://szentiras.hu/RUF/Lk 13,12–13', 'admin');
INSERT INTO readings (reading_year, reading_month, reading_day, firstreading, firstreading_short, firstreading_link, secondreading, secondreading_short, secondreading_link, created_by) VALUES ('2024', '05', '27', '„Isten segítségével hatalmas dolgokat viszünk véghez […].” (Zsolt 60,14a)', 'Zsolt 60,14a', 'https://szentiras.hu/RUF/Zsolt 60,14a', '„Kérlek azért titeket, testvéreim, az Isten irgalmasságára, hogy okos istentiszteletként szánjátok oda magatokat élő, szent, Istennek tetsző áldozatul.” (Róm 12,1)', 'Róm 12,1', 'https://szentiras.hu/RUF/Róm 12,1', 'admin');
INSERT INTO readings (reading_year, reading_month, reading_day, firstreading, firstreading_short, firstreading_link, secondreading, secondreading_short, secondreading_link, created_by) VALUES ('2024', '05', '28', '„A halló fület és a látó szemet egyaránt az Úr alkotta.” (Péld 20,12)', 'Péld 20,12', 'https://szentiras.hu/RUF/Péld 20,12', '„Amikor Fülöp odafutott, hallotta, hogy Ézsaiás prófétát olvassa, és megkérdezte tőle: Érted is, amit olvasol? Erre az [etióp főember] így válaszolt: Hogyan érthetném, ha valaki meg nem magyarázza? És megkérte Fülöpöt, hogy szálljon fel, és üljön mellé.” (ApCsel 8,30–31)', 'ApCsel 8,30–31', 'https://szentiras.hu/RUF/ApCsel 8,30–31', 'admin');
INSERT INTO readings (reading_year, reading_month, reading_day, firstreading, firstreading_short, firstreading_link, secondreading, secondreading_short, secondreading_link, created_by) VALUES ('2024', '05', '29', '„[Mózes] így szólt hozzájuk: Szívleljétek meg mindazokat az igéket, amelyekkel ma intelek titeket […] Mert nem üres beszéd ez a számotokra, hanem ez jelenti számotokra az életet […].” (5Móz 32,46a.47a)', '5Móz 32,46a.47a', 'https://szentiras.hu/RUF/5Móz 32,46a.47a', '„A Lélek az, aki életre kelt, a test nem használ semmit: azok a beszédek, amelyeket én mondtam nektek: Lélek és élet.” (Jn 6,63)', 'Jn 6,63', 'https://szentiras.hu/RUF/Jn 6,63', 'admin');
INSERT INTO readings (reading_year, reading_month, reading_day, firstreading, firstreading_short, firstreading_link, secondreading, secondreading_short, secondreading_link, created_by) VALUES ('2024', '05', '30', '„Mikor jóra fordította Sion sorsát az Úr, olyanok voltunk, mint az álmodók.” (Zsolt 126,1b)', 'Zsolt 126,1b', 'https://szentiras.hu/RUF/Zsolt 126,1b', '„A szeretet soha el nem múlik. De legyen bár prófétálás: el fog töröltetni; legyen nyelveken szólás: meg fog szűnni; legyen ismeret: el fog töröltetni. Mert most tükör által homályosan látunk, akkor pedig színről színre […].” (1Kor 13,8.12a)', '1Kor 13,8.12a', 'https://szentiras.hu/RUF/1Kor 13,8.12a', 'admin');
INSERT INTO readings (reading_year, reading_month, reading_day, firstreading, firstreading_short, firstreading_link, secondreading, secondreading_short, secondreading_link, created_by) VALUES ('2024', '05', '31', '„Megszégyenülve mennek hozzá mind, akik szembeszálltak vele.” (Ézs 45,24b)', 'Ézs 45,24b', 'https://szentiras.hu/RUF/Ézs 45,24b', '„Ismét megkérdezte őt a főpap: Te vagy a Krisztus, az Áldottnak Fia? Jézus ezt mondta: Én vagyok, és meglátjátok az Emberfiát, amint a Hatalmas jobbján ül, és eljön az ég felhőin.” (Mk 14,61b–62)', 'Mk 14,61b–62', 'https://szentiras.hu/RUF/Mk 14,61b–62', 'admin');
INSERT INTO readings (reading_year, reading_month, reading_day, firstreading, firstreading_short, firstreading_link, secondreading, secondreading_short, secondreading_link, created_by) VALUES ('2024', '06', '1', '„A lelkemet öntöttem ki az Úr előtt.” (1Sám 1,15)', '1Sám 1,15', 'https://szentiras.hu/RUF/1Sám 1,15', '„[Az asszony] megállt [Jézus] mögött, a lábánál sírva, és könnyeivel kezdte öntözni a lábát, és hajával törölte meg; csókolgatta a lábát, és megkente olajjal.” (Lk 7,38)', 'Lk 7,38', 'https://szentiras.hu/RUF/Lk 7,38', 'admin');
INSERT INTO readings (reading_year, reading_month, reading_day, firstreading, firstreading_short, firstreading_link, secondreading, secondreading_short, secondreading_link, created_by) VALUES ('2024', '06', '2', '„Bizony, jóságod és szereteted kísér életem minden napján, és az Úr házában lakom egész életemben.” (Zsolt 23,6)', 'Zsolt 23,6', 'https://szentiras.hu/RUF/Zsolt 23,6', '„Mindennap állhatatosan, egy szívvel, egy lélekkel voltak a templomban, és amikor házanként megtörték a kenyeret, örömmel és tiszta szívvel részesültek az ételben; dicsérték Istent, és kedvelte őket az egész nép.” (ApCsel 2,46–47a)', 'ApCsel 2,46–47a', 'https://szentiras.hu/RUF/ApCsel 2,46–47a', 'admin');
INSERT INTO readings (reading_year, reading_month, reading_day, firstreading, firstreading_short, firstreading_link, secondreading, secondreading_short, secondreading_link, created_by) VALUES ('2024', '06', '3', '„Uram, […] egyedül te vagy a föld minden országának Istene, te alkottad az eget és a földet!” (2Kir 19,15b)', '2Kir 19,15b', 'https://szentiras.hu/RUF/2Kir 19,15b', '„Nincs különbség zsidók és görögök között, mert mindenkinek ugyanaz az Ura, és ő bőkezű mindazokhoz, akik segítségül hívják őt.” (Róm 10,12)', 'Róm 10,12', 'https://szentiras.hu/RUF/Róm 10,12', 'admin');
INSERT INTO readings (reading_year, reading_month, reading_day, firstreading, firstreading_short, firstreading_link, secondreading, secondreading_short, secondreading_link, created_by) VALUES ('2024', '06', '4', '„Az Úr így felelt: Megnyugtat téged, ha az orcám megy veletek?” (2Móz 33,14)', '2Móz 33,14', 'https://szentiras.hu/RUF/2Móz 33,14', '„A szombati nyugalom tehát még ezután vár Isten népére.” (Zsid 4,9)', 'Zsid 4,9', 'https://szentiras.hu/RUF/Zsid 4,9', 'admin');
INSERT INTO readings (reading_year, reading_month, reading_day, firstreading, firstreading_short, firstreading_link, secondreading, secondreading_short, secondreading_link, created_by) VALUES ('2024', '06', '5', '„Amikor elcsüggedt a lelkem, az Úrra gondoltam, és imádságom eljutott hozzád…” (Jón 2,8)', 'Jón 2,8', 'https://szentiras.hu/RUF/Jón 2,8', '„Semmiért se aggódjatok, hanem imádságban és könyörgésben mindenkor hálaadással tárjátok fel kéréseiteket Isten előtt.” (Fil 4,6)', 'Fil 4,6', 'https://szentiras.hu/RUF/Fil 4,6', 'admin');
INSERT INTO readings (reading_year, reading_month, reading_day, firstreading, firstreading_short, firstreading_link, secondreading, secondreading_short, secondreading_link, created_by) VALUES ('2024', '06', '6', '„Ne félj, mert én veled vagyok!” (Ézs 43,5a)', 'Ézs 43,5a', 'https://szentiras.hu/RUF/Ézs 43,5a', '„Az Úr egy éjjel látomásban ezt mondta Pálnak: Ne félj, hanem szólj, és ne hallgass: mert én veled vagyok, és senki sem fog rád támadni és ártani neked […].” (ApCsel 18,9–10a)', 'ApCsel 18,9–10a', 'https://szentiras.hu/RUF/ApCsel 18,9–10a', 'admin');
INSERT INTO readings (reading_year, reading_month, reading_day, firstreading, firstreading_short, firstreading_link, secondreading, secondreading_short, secondreading_link, created_by) VALUES ('2024', '06', '7', 'Így szól az Úr: „Íme, én angyalt küldök előtted, hogy megőrizzen az úton, és bevigyen arra a helyre, amelyet kijelöltem.” (2Móz 23,20)', '2Móz 23,20', 'https://szentiras.hu/RUF/2Móz 23,20', '„Miután elmentek [a bölcsek], íme, az Úr angyala megjelent Józsefnek álmában, és így szólt: Kelj fel, vedd a gyermeket és anyját, menekülj Egyiptomba, és maradj ott, amíg nem szólok neked, mert Heródes halálra fogja kerestetni a gyermeket.” (Mt 2,13)', 'Mt 2,13', 'https://szentiras.hu/RUF/Mt 2,13', 'admin');
INSERT INTO readings (reading_year, reading_month, reading_day, firstreading, firstreading_short, firstreading_link, secondreading, secondreading_short, secondreading_link, created_by) VALUES ('2024', '06', '8', '„Ha apám, anyám elhagyna is, az Úr magához fogad engem.” (Zsolt 27,10)', 'Zsolt 27,10', 'https://szentiras.hu/RUF/Zsolt 27,10', 'Pál írja: „És ti mégsem estetek abba a kísértésbe, hogy engem testi erőtlenségem miatt megvessetek vagy megutáljatok, hanem úgy fogadtatok, mint Isten angyalát, mint Krisztus Jézust.” (Gal 4,14)', 'Gal 4,14', 'https://szentiras.hu/RUF/Gal 4,14', 'admin');
INSERT INTO readings (reading_year, reading_month, reading_day, firstreading, firstreading_short, firstreading_link, secondreading, secondreading_short, secondreading_link, created_by) VALUES ('2024', '06', '9', '„Jól láthatja utaimat Isten, és számon tarthatja minden lépésemet.” (Jób 31,4)', 'Jób 31,4', 'https://szentiras.hu/RUF/Jób 31,4', '„Ugye, öt verebet adnak két fillérért, de Isten egyről sem feledkezik meg közülük.” (Lk 12,6)', 'Lk 12,6', 'https://szentiras.hu/RUF/Lk 12,6', 'admin');
INSERT INTO readings (reading_year, reading_month, reading_day, firstreading, firstreading_short, firstreading_link, secondreading, secondreading_short, secondreading_link, created_by) VALUES ('2024', '06', '10', '„Bement tehát Mózes és Áron a fáraóhoz, és ezt mondták neki: Így szól az Úr […]: Bocsásd el népemet, hogy áldozattal szolgáljon nekem!” (2Móz 10,3)', '2Móz 10,3', 'https://szentiras.hu/RUF/2Móz 10,3', '„Krisztus szabadságra szabadított meg minket, álljatok meg tehát szilárdan, és ne engedjétek magatokat újra a szolgaság igájába fogni.” (Gal 5,1)', 'Gal 5,1', 'https://szentiras.hu/RUF/Gal 5,1', 'admin');
INSERT INTO readings (reading_year, reading_month, reading_day, firstreading, firstreading_short, firstreading_link, secondreading, secondreading_short, secondreading_link, created_by) VALUES ('2024', '06', '11', '„Akik tanácsukkal békére intenek, azok szívében öröm [van].” (Péld 12,20b)', 'Péld 12,20b', 'https://szentiras.hu/RUF/Péld 12,20b', '„Ha lehetséges, amennyire tőletek telik, éljetek minden emberrel békességben.” (Róm 12,18)', 'Róm 12,18', 'https://szentiras.hu/RUF/Róm 12,18', 'admin');
INSERT INTO readings (reading_year, reading_month, reading_day, firstreading, firstreading_short, firstreading_link, secondreading, secondreading_short, secondreading_link, created_by) VALUES ('2024', '06', '12', '„Kevéssel tetted őt [az embert] kisebbé Istennél, dicsőséggel és méltósággal koronáztad meg.” (Zsolt 8,7)', 'Zsolt 8,7', 'https://szentiras.hu/RUF/Zsolt 8,7', '„Minden szabad nekem, de nem minden használ. Minden szabad nekem, de nem válok semminek a rabjává.” (1Kor 6,12)', '1Kor 6,12', 'https://szentiras.hu/RUF/1Kor 6,12', 'admin');
INSERT INTO readings (reading_year, reading_month, reading_day, firstreading, firstreading_short, firstreading_link, secondreading, secondreading_short, secondreading_link, created_by) VALUES ('2024', '06', '13', 'Mózes mondta: „Tartsátok meg és teljesítsétek mindazt, amit megparancsolok nektek! Semmit se tégy ahhoz hozzá, és semmit se végy el belőle!” (5Móz 13,1)', '5Móz 13,1', 'https://szentiras.hu/RUF/5Móz 13,1', 'Jézus mondja: „Az én tanításom nem az enyém, hanem azé, aki elküldött engem. Ha valaki kész cselekedni az ő akaratát, felismeri erről a tanításról, hogy vajon Istentől való-e, vagy én magamtól szólok.” (Jn 7,16–17)', 'Jn 7,16–17', 'https://szentiras.hu/RUF/Jn 7,16–17', 'admin');
INSERT INTO readings (reading_year, reading_month, reading_day, firstreading, firstreading_short, firstreading_link, secondreading, secondreading_short, secondreading_link, created_by) VALUES ('2024', '06', '14', '„Az egész nép pedig nagy örömujjongásban tört ki, dicsérve az Urat azért, hogy lerakhatták az Úr házának az alapját.” (Ezsd 3,11b)', 'Ezsd 3,11b', 'https://szentiras.hu/RUF/Ezsd 3,11b', '„Mert más alapot senki sem vethet a meglevőn kívül, aki Jézus Krisztus.” (1Kor 3,11)', '1Kor 3,11', 'https://szentiras.hu/RUF/1Kor 3,11', 'admin');
INSERT INTO readings (reading_year, reading_month, reading_day, firstreading, firstreading_short, firstreading_link, secondreading, secondreading_short, secondreading_link, created_by) VALUES ('2024', '06', '15', '„Vessetek magatoknak igazságot, akkor hűséget arathattok!” (Hós 10,12a)', 'Hós 10,12a', 'https://szentiras.hu/RUF/Hós 10,12a', '„Egy gazdag embernek bő termést hozott a földje. Így gondolkozott magában: Mit tegyek? Nincs hova betakarítanom a termésemet. […] Én lelkem, sok javad van sok évre félretéve, pihenj, egyél, igyál, vigadozzál! Isten azonban azt mondta neki: Bolond, még ez éjjel elkérik tőled a lelkedet!” (Lk 12,16b–17.19–20a)', 'Lk 12,16b–17.19–20a', 'https://szentiras.hu/RUF/Lk 12,16b–17.19–20a', 'admin');
INSERT INTO readings (reading_year, reading_month, reading_day, firstreading, firstreading_short, firstreading_link, secondreading, secondreading_short, secondreading_link, created_by) VALUES ('2024', '06', '16', '„Mindezek arra várnak, hogy idejében adj nekik eledelt. Ha adsz nekik, szedegetnek, ha bőkezű vagy, jóllaknak javaiddal.” (Zsolt 104,27–28)', 'Zsolt 104,27–28', 'https://szentiras.hu/RUF/Zsolt 104,27–28', '„Jézus azt mondta nekik: Én vagyok az élet kenyere: aki énhozzám jön, nem éhezik meg, és aki énbennem hisz, nem szomjazik meg soha.” (Jn 6,35)', 'Jn 6,35', 'https://szentiras.hu/RUF/Jn 6,35', 'admin');
INSERT INTO readings (reading_year, reading_month, reading_day, firstreading, firstreading_short, firstreading_link, secondreading, secondreading_short, secondreading_link, created_by) VALUES ('2024', '06', '17', '„Énekeljetek az Úrnak új éneket, mert csodákat tett!” (Zsolt 98,1a)', 'Zsolt 98,1a', 'https://szentiras.hu/RUF/Zsolt 98,1a', '„Mi pedig valamennyien az ő teljességéből kaptunk kegyelmet kegyelemre.” (Jn 1,16)', 'Jn 1,16', 'https://szentiras.hu/RUF/Jn 1,16', 'admin');
INSERT INTO readings (reading_year, reading_month, reading_day, firstreading, firstreading_short, firstreading_link, secondreading, secondreading_short, secondreading_link, created_by) VALUES ('2024', '06', '18', '„Eléjük tártam rendelkezéseimet, és megismertettem velük törvényeimet, hogy aki teljesíti azokat, életet nyerjen általuk.” (Ez 20,11)', 'Ez 20,11', 'https://szentiras.hu/RUF/Ez 20,11', '„Abban nyilvánult meg Isten irántunk való szeretete, hogy egyszülött Fiát küldte el Isten a világba, hogy éljünk őáltala.” (1Jn 4,9)', '1Jn 4,9', 'https://szentiras.hu/RUF/1Jn 4,9', 'admin');
INSERT INTO readings (reading_year, reading_month, reading_day, firstreading, firstreading_short, firstreading_link, secondreading, secondreading_short, secondreading_link, created_by) VALUES ('2024', '06', '19', '„Figyeljetek rám, ti, népek, hallgassatok rám, ti, nemzetek! Mert tanítás származik tőlem, és törvényemet a népek világosságává teszem hamarosan.” (Ézs 51,4)', 'Ézs 51,4', 'https://szentiras.hu/RUF/Ézs 51,4', '„[Simeon] áldotta az Istent, és ezt mondta: […] meglátta szemem üdvösségedet, amelyet elkészítettél minden nép szeme láttára, hogy megjelenjék világosságul a pogányoknak és dicsőségül népednek, Izráelnek.” (Lk 2,28b.30–32)', 'Lk 2,28b.30–32', 'https://szentiras.hu/RUF/Lk 2,28b.30–32', 'admin');
INSERT INTO readings (reading_year, reading_month, reading_day, firstreading, firstreading_short, firstreading_link, secondreading, secondreading_short, secondreading_link, created_by) VALUES ('2024', '06', '20', '„Legyen a szívetek teljesen az Úré, a mi Istenünké […]!” (1Kir 8,61a)', '1Kir 8,61a', 'https://szentiras.hu/RUF/1Kir 8,61a', 'Pál írja: „Ami mögöttem van, azt elfelejtve, ami pedig előttem van, annak nekifeszülve futok egyenest a cél felé, Isten mennyei elhívásának Krisztus Jézusban adott jutalmáért.” (Fil 3,14)', 'Fil 3,14', 'https://szentiras.hu/RUF/Fil 3,14', 'admin');
INSERT INTO readings (reading_year, reading_month, reading_day, firstreading, firstreading_short, firstreading_link, secondreading, secondreading_short, secondreading_link, created_by) VALUES ('2024', '06', '21', '„Ne ölj!” (2Móz 20,13)', '2Móz 20,13', 'https://szentiras.hu/RUF/2Móz 20,13', 'Jézus így szólt tanítványaihoz: „Az az én parancsolatom, hogy úgy szeressétek egymást, ahogyan én szerettelek titeket.” (Jn 15,12)', 'Jn 15,12', 'https://szentiras.hu/RUF/Jn 15,12', 'admin');
INSERT INTO readings (reading_year, reading_month, reading_day, firstreading, firstreading_short, firstreading_link, secondreading, secondreading_short, secondreading_link, created_by) VALUES ('2024', '06', '22', '„Kegyelmezz, Uram, mert elcsüggedtem, gyógyíts meg, Uram, mert reszketnek tagjaim!” (Zsolt 6,3–4)', 'Zsolt 6,3–4', 'https://szentiras.hu/RUF/Zsolt 6,3–4', 'Jézus mondja: „Jöjjetek énhozzám mindnyájan, akik megfáradtatok és meg vagytok terhelve, és én megnyugvást adok nektek.” (Mt 11,28)', 'Mt 11,28', 'https://szentiras.hu/RUF/Mt 11,28', 'admin');
INSERT INTO readings (reading_year, reading_month, reading_day, firstreading, firstreading_short, firstreading_link, secondreading, secondreading_short, secondreading_link, created_by) VALUES ('2024', '06', '23', '„Hirdetem nevedet testvéreimnek, dicsérlek a gyülekezetben.” (Zsolt 22,23)', 'Zsolt 22,23', 'https://szentiras.hu/RUF/Zsolt 22,23', 'Pál írja: „Tudtotokra adom, testvéreim, hogy az evangélium, amelyet én hirdettem, nem embertől való.” (Gal 1,11)', 'Gal 1,11', 'https://szentiras.hu/RUF/Gal 1,11', 'admin');
INSERT INTO readings (reading_year, reading_month, reading_day, firstreading, firstreading_short, firstreading_link, secondreading, secondreading_short, secondreading_link, created_by) VALUES ('2024', '06', '24', '„[Uram,] teljesedjék be rajtam irgalmad, hogy éljek, mert törvényedben gyönyörködöm.” (Zsolt 119,77)', 'Zsolt 119,77', 'https://szentiras.hu/RUF/Zsolt 119,77', '„Imádkozom azért, hogy a szeretet egyre inkább gazdagodjék bennetek ismerettel és igazi megértéssel.” (Fil 1,9)', 'Fil 1,9', 'https://szentiras.hu/RUF/Fil 1,9', 'admin');
INSERT INTO readings (reading_year, reading_month, reading_day, firstreading, firstreading_short, firstreading_link, secondreading, secondreading_short, secondreading_link, created_by) VALUES ('2024', '06', '25', '„Hallgassatok rám, ti konok szívűek, akik messze vagytok az igazságtól! Közel van igazságom, nincs már messze, szabadításom nem késik.” (Ézs 46,12–13a)', 'Ézs 46,12–13a', 'https://szentiras.hu/RUF/Ézs 46,12–13a', '„Mert szívvel hiszünk, hogy megigazuljunk, és szájjal teszünk vallást, hogy üdvözüljünk.” (Róm 10,10)', 'Róm 10,10', 'https://szentiras.hu/RUF/Róm 10,10', 'admin');
INSERT INTO readings (reading_year, reading_month, reading_day, firstreading, firstreading_short, firstreading_link, secondreading, secondreading_short, secondreading_link, created_by) VALUES ('2024', '06', '26', '„Emlékezzetek csodatetteire, amelyeket véghezvitt, csodáira és döntéseire!” (Zsolt 105,5)', 'Zsolt 105,5', 'https://szentiras.hu/RUF/Zsolt 105,5', '„Mindenért hálát adjatok, mert ez Isten akarata Jézus Krisztus által a ti javatokra.” (1Thessz 5,18)', '1Thessz 5,18', 'https://szentiras.hu/RUF/1Thessz 5,18', 'admin');
INSERT INTO readings (reading_year, reading_month, reading_day, firstreading, firstreading_short, firstreading_link, secondreading, secondreading_short, secondreading_link, created_by) VALUES ('2024', '06', '27', '„Harsogjon hangod, mint a kürt! Mondd meg népemnek, hogy mi a bűne, Jákób házának, hogy mi a vétke!” (Ézs 58,1b)', 'Ézs 58,1b', 'https://szentiras.hu/RUF/Ézs 58,1b', '„Ettől fogva kezdte Jézus hirdetni: Térjetek meg, mert elközelített a mennyek országa!” (Mt 4,17)', 'Mt 4,17', 'https://szentiras.hu/RUF/Mt 4,17', 'admin');
INSERT INTO readings (reading_year, reading_month, reading_day, firstreading, firstreading_short, firstreading_link, secondreading, secondreading_short, secondreading_link, created_by) VALUES ('2024', '06', '28', '„[Az Úr] megsegített, ezért vidám a szívem, és énekelve adok neki hálát.” (Zsolt 28,7b)', 'Zsolt 28,7b', 'https://szentiras.hu/RUF/Zsolt 28,7b', '„[Jézus] által vigyük Isten elé a hálaadás áldozatát mindenkor, azaz nevéről vallást tévő ajkaink gyümölcsét.” (Zsid 13,15)', 'Zsid 13,15', 'https://szentiras.hu/RUF/Zsid 13,15', 'admin');
INSERT INTO readings (reading_year, reading_month, reading_day, firstreading, firstreading_short, firstreading_link, secondreading, secondreading_short, secondreading_link, created_by) VALUES ('2024', '06', '29', '„Gondolj Teremtődre ifjúságod idején, míg el nem jönnek a rossz napok, és el nem érkeznek azok az évek, melyekről ezt mondod: nem szeretem őket!” (Préd 12,1)', 'Préd 12,1', 'https://szentiras.hu/RUF/Préd 12,1', '„Útra kelek, elmegyek apámhoz, és azt mondom neki: Atyám, vétkeztem az ég ellen és teellened. Nem vagyok többé méltó arra, hogy fiadnak nevezzenek, tégy engem olyanná, mint béreseid közül egy.” (Lk 15,18–19)', 'Lk 15,18–19', 'https://szentiras.hu/RUF/Lk 15,18–19', 'admin');
INSERT INTO readings (reading_year, reading_month, reading_day, firstreading, firstreading_short, firstreading_link, secondreading, secondreading_short, secondreading_link, created_by) VALUES ('2024', '06', '30', '„Egy szívet adok majd nekik, és új lelket adok beléjük, eltávolítom testükből a kőszívet, és hússzívet adok nekik.” (Ez 11,19)', 'Ez 11,19', 'https://szentiras.hu/RUF/Ez 11,19', '„Mert nem a félelem lelkét adta nekünk Isten, hanem az erő, a szeretet és a józanság lelkét.” (2Tim 1,7)', '2Tim 1,7', 'https://szentiras.hu/RUF/2Tim 1,7', 'admin');
INSERT INTO readings (reading_year, reading_month, reading_day, firstreading, firstreading_short, firstreading_link, secondreading, secondreading_short, secondreading_link, created_by) VALUES ('2024', '07', '1', '„Adjatok hálát az Úrnak, mert jó, mert örökké tart szeretete!” (Zsolt 106,1b)', 'Zsolt 106,1b', 'https://szentiras.hu/RUF/Zsolt 106,1b', '„Amit pedig szóltok vagy tesztek, mind az Úr Jézus nevében tegyétek, hálát adva az Atya Istennek őáltala.” (Kol 3,17)', 'Kol 3,17', 'https://szentiras.hu/RUF/Kol 3,17', 'admin');
INSERT INTO readings (reading_year, reading_month, reading_day, firstreading, firstreading_short, firstreading_link, secondreading, secondreading_short, secondreading_link, created_by) VALUES ('2024', '07', '2', '„De akik szeretnek, legyenek olyanok, mint a felkelő nap ereje teljében!” (Bír 5,31b)', 'Bír 5,31b', 'https://szentiras.hu/RUF/Bír 5,31b', 'Jézus mondja: „Ti vagytok a világ világossága. Nem rejthető el a hegyen épült város. […] Úgy ragyogjon a ti világosságotok az emberek előtt, hogy lássák jó cselekedeteiteket, és dicsőítsék a ti mennyei Atyátokat.” (Mt 5,14.16)', 'Mt 5,14.16', 'https://szentiras.hu/RUF/Mt 5,14.16', 'admin');
INSERT INTO readings (reading_year, reading_month, reading_day, firstreading, firstreading_short, firstreading_link, secondreading, secondreading_short, secondreading_link, created_by) VALUES ('2024', '07', '3', '„Szíveteket szaggassátok meg, ne a ruhátokat, úgy térjetek meg Istenetekhez, az Úrhoz!” (Jóel 2,13a)', 'Jóel 2,13a', 'https://szentiras.hu/RUF/Jóel 2,13a', '„Mert áron vétettetek meg: dicsőítsétek tehát Istent testetekben!” (1Kor 6,20)', '1Kor 6,20', 'https://szentiras.hu/RUF/1Kor 6,20', 'admin');
INSERT INTO readings (reading_year, reading_month, reading_day, firstreading, firstreading_short, firstreading_link, secondreading, secondreading_short, secondreading_link, created_by) VALUES ('2024', '07', '4', '„Boldog az, akinek gondja van a nincstelenre, ha bajba kerül, megmenti az Úr.” (Zsolt 41,2)', 'Zsolt 41,2', 'https://szentiras.hu/RUF/Zsolt 41,2', '„A jótékonyságról és az adakozásról pedig el ne feledkezzetek, mert ilyen áldozatokban gyönyörködik Isten.” (Zsid 13,16)', 'Zsid 13,16', 'https://szentiras.hu/RUF/Zsid 13,16', 'admin');
INSERT INTO readings (reading_year, reading_month, reading_day, firstreading, firstreading_short, firstreading_link, secondreading, secondreading_short, secondreading_link, created_by) VALUES ('2024', '07', '5', '„Akik félitek az Urat, az Úrban bízzatok!” (Zsolt 115,11a)', 'Zsolt 115,11a', 'https://szentiras.hu/RUF/Zsolt 115,11a', '„Nézzétek meg az égi madarakat: nem vetnek, nem is aratnak, csűrbe sem gyűjtenek, és a ti mennyei Atyátok táplálja őket.” (Mt 6,26a)', 'Mt 6,26a', 'https://szentiras.hu/RUF/Mt 6,26a', 'admin');
INSERT INTO readings (reading_year, reading_month, reading_day, firstreading, firstreading_short, firstreading_link, secondreading, secondreading_short, secondreading_link, created_by) VALUES ('2024', '07', '6', '„Aki a Felséges rejtekében lakik, a Mindenható árnyékában pihen, az ezt mondhatja az Úrnak: Oltalmam és váram, Istenem, akiben bízom!” (Zsolt 91,1–2)', 'Zsolt 91,1–2', 'https://szentiras.hu/RUF/Zsolt 91,1–2', '„De mivel Isten mind e mai napig megsegített, itt állok, és bizonyságot teszek kicsinyeknek és nagyoknak […].” (ApCsel 26,22a)', 'ApCsel 26,22a', 'https://szentiras.hu/RUF/ApCsel 26,22a', 'admin');
INSERT INTO readings (reading_year, reading_month, reading_day, firstreading, firstreading_short, firstreading_link, secondreading, secondreading_short, secondreading_link, created_by) VALUES ('2024', '07', '7', '„Ha pedig a bűnös megtér, és nem követi el többé a bűneit, hanem törvény és igazság szerint él, akkor megmenti az életét.” (Ez 18,27)', 'Ez 18,27', 'https://szentiras.hu/RUF/Ez 18,27', '„Mert mit használ az embernek, ha az egész világot megnyeri, lelkében pedig kárt vall?” (Mt 16,26a)', 'Mt 16,26a', 'https://szentiras.hu/RUF/Mt 16,26a', 'admin');
INSERT INTO readings (reading_year, reading_month, reading_day, firstreading, firstreading_short, firstreading_link, secondreading, secondreading_short, secondreading_link, created_by) VALUES ('2024', '07', '8', '„Ti a saját szemetekkel láttátok az Úrnak valamennyi nagy tettét, amelyet véghezvitt.” (5Móz 11,7)', '5Móz 11,7', 'https://szentiras.hu/RUF/5Móz 11,7', '„Mert nem kitalált meséket követve ismertettük meg veletek a mi Urunk Jézus Krisztus hatalmát és megjelenését, hanem úgy, hogy szemtanúi voltunk isteni fenségének.” (2Pt 1,16)', '2Pt 1,16', 'https://szentiras.hu/RUF/2Pt 1,16', 'admin');
INSERT INTO readings (reading_year, reading_month, reading_day, firstreading, firstreading_short, firstreading_link, secondreading, secondreading_short, secondreading_link, created_by) VALUES ('2024', '07', '9', '„Legyen kegyelmes hozzánk az Isten, áldjon meg bennünket, és ragyogtassa ránk orcáját!” (Zsolt 67,2)', 'Zsolt 67,2', 'https://szentiras.hu/RUF/Zsolt 67,2', '„Ekkor [Jézus] átölelte és kezét rájuk téve megáldotta őket [a kisgyermekeket].” (Mk 10,16)', 'Mk 10,16', 'https://szentiras.hu/RUF/Mk 10,16', 'admin');
INSERT INTO readings (reading_year, reading_month, reading_day, firstreading, firstreading_short, firstreading_link, secondreading, secondreading_short, secondreading_link, created_by) VALUES ('2024', '07', '10', '„Ezután is csak én leszek! Nincs, aki kezemből kiragadjon, ha én cselekszem, ki másíthatja meg?” (Ézs 43,13)', 'Ézs 43,13', 'https://szentiras.hu/RUF/Ézs 43,13', '„Azért senki ne dicsekedjék emberekkel, mert minden a tietek; ti viszont Krisztuséi vagytok, Krisztus pedig Istené.” (1Kor 3,21.23)', '1Kor 3,21.23', 'https://szentiras.hu/RUF/1Kor 3,21.23', 'admin');
INSERT INTO readings (reading_year, reading_month, reading_day, firstreading, firstreading_short, firstreading_link, secondreading, secondreading_short, secondreading_link, created_by) VALUES ('2024', '07', '11', '„Akkor ahelyett, hogy ezt mondanák nekik: Nem vagytok népem! – ezt fogják mondani: Az élő Isten fiai vagytok!” (Hós 2,1b)', 'Hós 2,1b', 'https://szentiras.hu/RUF/Hós 2,1b', '„Úgyhogy már nem vagy szolga, hanem fiú, ha pedig fiú, akkor Isten akaratából örökös is.” (Gal 4,7)', 'Gal 4,7', 'https://szentiras.hu/RUF/Gal 4,7', 'admin');
INSERT INTO readings (reading_year, reading_month, reading_day, firstreading, firstreading_short, firstreading_link, secondreading, secondreading_short, secondreading_link, created_by) VALUES ('2024', '07', '12', '„Egyetlen szó sem veszett el azokból az ígéretekből, amelyeket [az Úr] Mózes, az ő szolgája által megígért.” (1Kir 8,56b)', '1Kir 8,56b', 'https://szentiras.hu/RUF/1Kir 8,56b', '„És [Jézus] Mózestől meg valamennyi prófétától kezdve elmagyarázta nekik mindazt, ami az Írásokban róla szólt.” (Lk 24,27)', 'Lk 24,27', 'https://szentiras.hu/RUF/Lk 24,27', 'admin');
INSERT INTO readings (reading_year, reading_month, reading_day, firstreading, firstreading_short, firstreading_link, secondreading, secondreading_short, secondreading_link, created_by) VALUES ('2024', '07', '13', '„Ki hívja elő örök idők óta a nemzedékeket? Én, az Úr vagyok az első, és én mindvégig ugyanaz maradok.” (Ézs 41,4b–c)', 'Ézs 41,4b–c', 'https://szentiras.hu/RUF/Ézs 41,4b–c', '„Ne félj! Én vagyok az első és az utolsó.” (Jel 1,17b)', 'Jel 1,17b', 'https://szentiras.hu/RUF/Jel 1,17b', 'admin');
INSERT INTO readings (reading_year, reading_month, reading_day, firstreading, firstreading_short, firstreading_link, secondreading, secondreading_short, secondreading_link, created_by) VALUES ('2024', '07', '14', '„Az Úré a föld és ami betölti, a földkerekség és a rajta lakók.” (Zsolt 24,1)', 'Zsolt 24,1', 'https://szentiras.hu/RUF/Zsolt 24,1', '„Ezért tehát nem vagytok többé idegenek és jövevények, hanem polgártársai a szenteknek és háza népe Istennek.” (Ef 2,19)', 'Ef 2,19', 'https://szentiras.hu/RUF/Ef 2,19', 'admin');
INSERT INTO readings (reading_year, reading_month, reading_day, firstreading, firstreading_short, firstreading_link, secondreading, secondreading_short, secondreading_link, created_by) VALUES ('2024', '07', '15', '„A Seregek Ura velünk van, Jákób Istene a mi várunk.” (Zsolt 46,8)', 'Zsolt 46,8', 'https://szentiras.hu/RUF/Zsolt 46,8', '„Amikor Jézus kiszállt, és meglátta a nagy sokaságot, megszánta őket, mert olyanok voltak, mint a pásztor nélkül való juhok, és kezdte őket sok mindenre tanítani.” (Mk 6,34)', 'Mk 6,34', 'https://szentiras.hu/RUF/Mk 6,34', 'admin');
INSERT INTO readings (reading_year, reading_month, reading_day, firstreading, firstreading_short, firstreading_link, secondreading, secondreading_short, secondreading_link, created_by) VALUES ('2024', '07', '16', '„Miért mondod ezt, Jákób, miért beszélsz így, Izráel: Rejtve van sorsom az Úr előtt, nem kerül ügyem Isten elé.” (Ézs 40,27)', 'Ézs 40,27', 'https://szentiras.hu/RUF/Ézs 40,27', '„Akkor ő [Jézus] így szólt hozzájuk [az emmausi tanítványokhoz]: Ó, ti balgák! Milyen rest a szívetek, hogy mindazt elhiggyétek, amit megmondtak a próféták! Hát nem ezt kellett-e elszenvednie a Krisztusnak, és így megdicsőülnie?” (Lk 24,25–26)', 'Lk 24,25–26', 'https://szentiras.hu/RUF/Lk 24,25–26', 'admin');
INSERT INTO readings (reading_year, reading_month, reading_day, firstreading, firstreading_short, firstreading_link, secondreading, secondreading_short, secondreading_link, created_by) VALUES ('2024', '07', '17', '„Hanem ilyen lesz az a szövetség, amelyet Izráel házával fogok kötni, ha eljön az ideje – így szól az Úr –: Törvényemet beléjük helyezem, szívükbe írom be. Én Istenük leszek, ők pedig az én népem lesznek.” (Jer 31,33)', 'Jer 31,33', 'https://szentiras.hu/RUF/Jer 31,33', '„Akiké [az izráelitáké] a fiúság és a dicsőség, a szövetségek és a törvényadás, az istentisztelet és az ígéretek.” (Róm 9,4)', 'Róm 9,4', 'https://szentiras.hu/RUF/Róm 9,4', 'admin');
INSERT INTO readings (reading_year, reading_month, reading_day, firstreading, firstreading_short, firstreading_link, secondreading, secondreading_short, secondreading_link, created_by) VALUES ('2024', '07', '18', '„Így szól az Úr: Oltalmazom, mert ismeri nevemet.” (Zsolt 91,14b)', 'Zsolt 91,14b', 'https://szentiras.hu/RUF/Zsolt 91,14b', '„Minden gondotokat őreá vessétek, mert neki gondja van rátok.” (1Pt 5,7)', '1Pt 5,7', 'https://szentiras.hu/RUF/1Pt 5,7', 'admin');
INSERT INTO readings (reading_year, reading_month, reading_day, firstreading, firstreading_short, firstreading_link, secondreading, secondreading_short, secondreading_link, created_by) VALUES ('2024', '07', '19', '„Mert én új eget és új földet teremtek, a régire nem is emlékeznek, senkinek sem jut eszébe.” (Ézs 65,17)', 'Ézs 65,17', 'https://szentiras.hu/RUF/Ézs 65,17', 'János írja: „És láttam új eget és új földet, mert az első ég és az első föld elmúlt, és a tenger sincs többé.” (Jel 21,1)', 'Jel 21,1', 'https://szentiras.hu/RUF/Jel 21,1', 'admin');
INSERT INTO readings (reading_year, reading_month, reading_day, firstreading, firstreading_short, firstreading_link, secondreading, secondreading_short, secondreading_link, created_by) VALUES ('2024', '07', '20', '„Tudd meg, és lásd meg, milyen gonosz és keserves dolog, hogy elhagytad Istenedet, az Urat, és hogy nem félsz engem!” (Jer 2,19b)', 'Jer 2,19b', 'https://szentiras.hu/RUF/Jer 2,19b', '„Miután [a fiatalabb fiú] elköltötte mindenét, nagy éhínség támadt azon a vidéken, úgyhogy nélkülözni kezdett. Ekkor elment, és elszegődött annak a vidéknek egyik polgárához, aki kiküldte őt a földjeire disznókat legeltetni.” (Lk 15,14–15)', 'Lk 15,14–15', 'https://szentiras.hu/RUF/Lk 15,14–15', 'admin');
INSERT INTO readings (reading_year, reading_month, reading_day, firstreading, firstreading_short, firstreading_link, secondreading, secondreading_short, secondreading_link, created_by) VALUES ('2024', '07', '21', '„Jön már a hegyeken az örömhírhozó, aki békességet hirdet!” (Náh 2,1a)', 'Náh 2,1a', 'https://szentiras.hu/RUF/Náh 2,1a', '„És [Krisztus] eljött, békességet hirdetett nektek, a távoliaknak, és békességet a közelieknek.” (Ef 2,17)', 'Ef 2,17', 'https://szentiras.hu/RUF/Ef 2,17', 'admin');
INSERT INTO readings (reading_year, reading_month, reading_day, firstreading, firstreading_short, firstreading_link, secondreading, secondreading_short, secondreading_link, created_by) VALUES ('2024', '07', '22', '„Harcolni fognak ellened, de nem bírnak veled, mert én veled leszek – így szól az Úr –, és majd megmentelek!” (Jer 1,19)', 'Jer 1,19', 'https://szentiras.hu/RUF/Jer 1,19', 'Jézus így imádkozik tanítványaiért: „Én őértük könyörgök: nem a világért könyörgök, hanem azokért, akiket nekem adtál, mert a tieid.” (Jn 17,9)', 'Jn 17,9', 'https://szentiras.hu/RUF/Jn 17,9', 'admin');
INSERT INTO readings (reading_year, reading_month, reading_day, firstreading, firstreading_short, firstreading_link, secondreading, secondreading_short, secondreading_link, created_by) VALUES ('2024', '07', '23', '„[Uram,] csak a te intelmeidben gyönyörködöm, azok az én tanácsadóim.” (Zsolt 119,24)', 'Zsolt 119,24', 'https://szentiras.hu/RUF/Zsolt 119,24', 'Jézus mondja: „Ne gondoljátok, hogy azért jöttem, hogy érvénytelenné tegyem a törvényt vagy a próféták tanítását. Nem azért jöttem, hogy érvénytelenné tegyem, hanem hogy betöltsem azokat.” (Mt 5,17)', 'Mt 5,17', 'https://szentiras.hu/RUF/Mt 5,17', 'admin');
INSERT INTO readings (reading_year, reading_month, reading_day, firstreading, firstreading_short, firstreading_link, secondreading, secondreading_short, secondreading_link, created_by) VALUES ('2024', '07', '24', '„[Az Úr] elküldött, hogy örömhírt vigyek a szegényeknek […]. Hirdetem az Úr kegyelmének esztendejét, […] vigasztalok minden gyászolót.” (Ézs 61,1b–2)', 'Ézs 61,1b–2', 'https://szentiras.hu/RUF/Ézs 61,1b–2', '„Áldott az Isten, a mi Urunk Jézus Krisztus Atyja, az irgalom Atyja és minden vigasztalás Istene, aki megvigasztal minket minden nyomorúságunkban, hogy mi is megvigasztalhassunk másokat minden nyomorúságban azzal a vigasztalással, amellyel Isten vigasztal minket.” (2Kor 1,3–4)', '2Kor 1,3–4', 'https://szentiras.hu/RUF/2Kor 1,3–4', 'admin');
INSERT INTO readings (reading_year, reading_month, reading_day, firstreading, firstreading_short, firstreading_link, secondreading, secondreading_short, secondreading_link, created_by) VALUES ('2024', '07', '25', 'Isten azt mondta: „Szivárványívemet helyezem a felhőkre, az lesz a jele a szövetségnek, amelyet most a földdel megkötök.” (1Móz 9,13)', '1Móz 9,13', 'https://szentiras.hu/RUF/1Móz 9,13', '„A mostani egek és a föld pedig ugyanezen szó által megkímélve megmaradtak, hogy tűznek tartassanak fenn az ítéletnek és az istentelen emberek pusztulásának napjára.” (2Pt 3,7)', '2Pt 3,7', 'https://szentiras.hu/RUF/2Pt 3,7', 'admin');
INSERT INTO readings (reading_year, reading_month, reading_day, firstreading, firstreading_short, firstreading_link, secondreading, secondreading_short, secondreading_link, created_by) VALUES ('2024', '07', '26', '„Ha [Isten] lerombol valamit, nincs, ki felépítse; ha valakit bezár, nincs, ki kiengedné.” (Jób 12,14)', 'Jób 12,14', 'https://szentiras.hu/RUF/Jób 12,14', '„Jézus pedig ezt mondta neki: […] Minden lehetséges annak, aki hisz. A fiú apja azonnal felkiáltott: Hiszek! Segíts a hitetlenségemen!” (Mk 9,23–24)', 'Mk 9,23–24', 'https://szentiras.hu/RUF/Mk 9,23–24', 'admin');
INSERT INTO readings (reading_year, reading_month, reading_day, firstreading, firstreading_short, firstreading_link, secondreading, secondreading_short, secondreading_link, created_by) VALUES ('2024', '07', '27', '„Nem marad örökre elfelejtve a szegény, nem vész el végleg az elesettek reménye.” (Zsolt 9,19)', 'Zsolt 9,19', 'https://szentiras.hu/RUF/Zsolt 9,19', 'Jézus mondja: „Boldogok vagytok, ti szegények, mert tiétek az Isten országa.” (Lk 6,20)', 'Lk 6,20', 'https://szentiras.hu/RUF/Lk 6,20', 'admin');
INSERT INTO readings (reading_year, reading_month, reading_day, firstreading, firstreading_short, firstreading_link, secondreading, secondreading_short, secondreading_link, created_by) VALUES ('2024', '07', '28', '„Velem van az Úr, nem félek, ember mit árthat nekem?” (Zsolt 118,6)', 'Zsolt 118,6', 'https://szentiras.hu/RUF/Zsolt 118,6', '„Ki választana el minket Krisztus szeretetétől? Nyomorúság, vagy szorongattatás, vagy üldözés, vagy éhezés, vagy mezítelenség, vagy veszedelem, vagy fegyver?” (Róm 8,35)', 'Róm 8,35', 'https://szentiras.hu/RUF/Róm 8,35', 'admin');
INSERT INTO readings (reading_year, reading_month, reading_day, firstreading, firstreading_short, firstreading_link, secondreading, secondreading_short, secondreading_link, created_by) VALUES ('2024', '07', '29', '„[Az Úr, a mi Istenünk] fordítsa oda magához szívünket, hogy mindenben az ő útjain járjunk, és megtartsuk parancsolatait, rendelkezéseit és törvényeit, amelyeket őseinknek adott.” (1Kir 8,58)', '1Kir 8,58', 'https://szentiras.hu/RUF/1Kir 8,58', '„És senki sem mondhatja: »Jézus Úr«, csakis a Szentlélek által.” (1Kor 12,3b)', '1Kor 12,3b', 'https://szentiras.hu/RUF/1Kor 12,3b', 'admin');
INSERT INTO readings (reading_year, reading_month, reading_day, firstreading, firstreading_short, firstreading_link, secondreading, secondreading_short, secondreading_link, created_by) VALUES ('2024', '07', '30', '„Mert vizet fakasztok a pusztában és folyókat a sivatagban, hogy inni adjak választott népemnek.” (Ézs 43,20b)', 'Ézs 43,20b', 'https://szentiras.hu/RUF/Ézs 43,20b', 'Jézus ezt mondta az asszonynak: „De aki abból a vízből iszik, amelyet én adok neki, soha többé meg nem szomjazik, mert örök életre buzgó víz forrásává lesz benne.” (Jn 4,14)', 'Jn 4,14', 'https://szentiras.hu/RUF/Jn 4,14', 'admin');
INSERT INTO readings (reading_year, reading_month, reading_day, firstreading, firstreading_short, firstreading_link, secondreading, secondreading_short, secondreading_link, created_by) VALUES ('2024', '07', '31', '„Akkor majd megtudjátok, hogy én vagyok az Úr. Az én nevemért, nem pedig a ti gonosz életetek és romlott tetteitek szerint bánok így veletek, Izráel háza! […].” (Ez 20,44)', 'Ez 20,44', 'https://szentiras.hu/RUF/Ez 20,44', '„Isten azonban a maga szeretetét mutatta meg irántunk, mert Krisztus már akkor meghalt értünk, amikor még bűnösök voltunk.” (Róm 5,8)', 'Róm 5,8', 'https://szentiras.hu/RUF/Róm 5,8', 'admin');
INSERT INTO readings (reading_year, reading_month, reading_day, firstreading, firstreading_short, firstreading_link, secondreading, secondreading_short, secondreading_link, created_by) VALUES ('2024', '08', '1', '„Az Úr napja nagy és igen félelmetes; ki fogja kibírni azt!? De még most is így szól az Úr: Térjetek meg hozzám teljes szívvel […].” (Jóel 2,11c–12a)', 'Jóel 2,11c–12a', 'https://szentiras.hu/RUF/Jóel 2,11c–12a', '„Miután Jánost fogságba vetették, elment Jézus Galileába, és így hirdette az Isten evangéliumát: Beteljesedett az idő, és elközelített az Isten országa. Térjetek meg, és higgyetek az evangéliumban!” (Mk 1,14–15)', 'Mk 1,14–15', 'https://szentiras.hu/RUF/Mk 1,14–15', 'admin');
INSERT INTO readings (reading_year, reading_month, reading_day, firstreading, firstreading_short, firstreading_link, secondreading, secondreading_short, secondreading_link, created_by) VALUES ('2024', '08', '2', '„[Isten, aki] megrendítetted, megrepesztetted a földet. Gyógyítsd be sebeit, mert megindult.” (Zsolt 60,4)', 'Zsolt 60,4', 'https://szentiras.hu/RUF/Zsolt 60,4', '„Ekkor [a tanítványok] felébresztették [Jézust], és így szóltak hozzá: Mester, nem törődsz azzal, hogy elveszünk? Ő pedig felkelt, ráparancsolt a szélre, és azt mondta a tengernek: Hallgass el, némulj meg! És elült a vihar, és nagy csendesség lett.” (Mk 4,38b–39)', 'Mk 4,38b–39', 'https://szentiras.hu/RUF/Mk 4,38b–39', 'admin');
INSERT INTO readings (reading_year, reading_month, reading_day, firstreading, firstreading_short, firstreading_link, secondreading, secondreading_short, secondreading_link, created_by) VALUES ('2024', '08', '3', '„Benned bíznak, akik ismerik nevedet, nem hagyod el, Uram, azokat, akik hozzád folyamodnak.” (Zsolt 9,11)', 'Zsolt 9,11', 'https://szentiras.hu/RUF/Zsolt 9,11', 'Jézus mondja: „Aki énhozzám jön, azt én nem küldöm el.” (Jn 6,37b)', 'Jn 6,37b', 'https://szentiras.hu/RUF/Jn 6,37b', 'admin');
INSERT INTO readings (reading_year, reading_month, reading_day, firstreading, firstreading_short, firstreading_link, secondreading, secondreading_short, secondreading_link, created_by) VALUES ('2024', '08', '4', '„Amit csak akar az Úr, megteszi az égen és a földön és a mélységes tengerekben.” (Zsolt 135,6)', 'Zsolt 135,6', 'https://szentiras.hu/RUF/Zsolt 135,6', '„[Legyetek] a szolgálatkészségben fáradhatatlanok, a Lélekben buzgók: az Úrnak szolgáljatok.” (Róm 12,11)', 'Róm 12,11', 'https://szentiras.hu/RUF/Róm 12,11', 'admin');
INSERT INTO readings (reading_year, reading_month, reading_day, firstreading, firstreading_short, firstreading_link, secondreading, secondreading_short, secondreading_link, created_by) VALUES ('2024', '08', '5', '„Az Úr jár előttetek, és Izráel Istene lesz mögöttetek is.” (Ézs 52,12)', 'Ézs 52,12', 'https://szentiras.hu/RUF/Ézs 52,12', '„Amikor [Jézus] továbbment, meglátta Lévit, Alfeus fiát, aki a vámszedőhelyen ült, és így szólt hozzá: Kövess engem! Az pedig felkelt, és követte őt.” (Mk 2,14)', 'Mk 2,14', 'https://szentiras.hu/RUF/Mk 2,14', 'admin');
INSERT INTO readings (reading_year, reading_month, reading_day, firstreading, firstreading_short, firstreading_link, secondreading, secondreading_short, secondreading_link, created_by) VALUES ('2024', '08', '6', '„Fekvőhelyemen is rád gondolok, minden őrváltáskor rólad elmélkedem.” (Zsolt 63,7)', 'Zsolt 63,7', 'https://szentiras.hu/RUF/Zsolt 63,7', '„Mert Isten nem haragra rendelt minket, hanem hogy elnyerjük az üdvösséget a mi Urunk Jézus Krisztus által, aki meghalt értünk, hogy akár ébren vagyunk, akár alszunk, vele együtt éljünk.” (1Thessz 5,9–10)', '1Thessz 5,9–10', 'https://szentiras.hu/RUF/1Thessz 5,9–10', 'admin');
INSERT INTO readings (reading_year, reading_month, reading_day, firstreading, firstreading_short, firstreading_link, secondreading, secondreading_short, secondreading_link, created_by) VALUES ('2024', '08', '7', '„Az Úr félelme bölcsességre int, és aki tisztességet akar, előbb legyen alázatos!” (Péld 15,33)', 'Péld 15,33', 'https://szentiras.hu/RUF/Péld 15,33', '„Mivel tehát a világ a saját bölcsessége útján nem ismerte meg Istent a maga bölcsességében, tetszett Istennek, hogy az igehirdetés bolondsága által üdvözítse a hívőket.” (1Kor 1,21)', '1Kor 1,21', 'https://szentiras.hu/RUF/1Kor 1,21', 'admin');
INSERT INTO readings (reading_year, reading_month, reading_day, firstreading, firstreading_short, firstreading_link, secondreading, secondreading_short, secondreading_link, created_by) VALUES ('2024', '08', '8', '„Az Úr Józseffel volt, és eredményessé tette az Úr, amihez hozzáfogott.” (1Móz 39,23)', '1Móz 39,23', 'https://szentiras.hu/RUF/1Móz 39,23', '„A Lélek gyümölcse pedig: szeretet, öröm, békesség, türelem, szívesség, jóság, hűség, szelídség, önmegtartóztatás.” (Gal 5,22–23a)', 'Gal 5,22–23a', 'https://szentiras.hu/RUF/Gal 5,22–23a', 'admin');
INSERT INTO readings (reading_year, reading_month, reading_day, firstreading, firstreading_short, firstreading_link, secondreading, secondreading_short, secondreading_link, created_by) VALUES ('2024', '08', '9', '„Mentsd meg azokat, akiket halálra visznek, és ne fordulj el azoktól, akiket a vesztőhelyre hurcolnak! Ha azt mondanád, hogy erről nem tudunk, az, aki a szíveket vizsgálja, beléd lát, és aki lelkedet őrzi, ismer; ő megfizet az embernek cselekedete szerint.” (Péld 24,11–12)', 'Péld 24,11–12', 'https://szentiras.hu/RUF/Péld 24,11–12', '„És így ha szenved az egyik tag, vele együtt szenved valamennyi, ha dicsőségben részesül az egyik tag, vele együtt örül valamennyi.” (1Kor 12,26)', '1Kor 12,26', 'https://szentiras.hu/RUF/1Kor 12,26', 'admin');
INSERT INTO readings (reading_year, reading_month, reading_day, firstreading, firstreading_short, firstreading_link, secondreading, secondreading_short, secondreading_link, created_by) VALUES ('2024', '08', '10', '„Mert én, az Úr, szeretem az igazságot, gyűlölöm a rablást és az álnokságot.” (Ézs 61,8a)', 'Ézs 61,8a', 'https://szentiras.hu/RUF/Ézs 61,8a', '„És senki ne károsítsa meg és ne csalja meg testvérét semmiféle ügyben. Mert bosszút áll az Úr mindezekért […].” (1Thessz 4,6)', '1Thessz 4,6', 'https://szentiras.hu/RUF/1Thessz 4,6', 'admin');
INSERT INTO readings (reading_year, reading_month, reading_day, firstreading, firstreading_short, firstreading_link, secondreading, secondreading_short, secondreading_link, created_by) VALUES ('2024', '08', '11', '„A népek szeme láttára mutatom meg rajtatok, hogy szent vagyok. Akkor majd megtudjátok, hogy én vagyok az Úr, amikor beviszlek benneteket Izráel földjére, abba az országba, amelyről fölemelt kézzel tettem esküt, hogy őseiteknek adom.” (Ez 20,41b–42)', 'Ez 20,41b–42', 'https://szentiras.hu/RUF/Ez 20,41b–42', '„Hit által engedelmeskedett Ábrahám, amikor elhívta Isten, hogy induljon el arra a helyre, amelyet örökségül fog kapni. És elindult, nem tudva, hova megy.” (Zsid 11,8)', 'Zsid 11,8', 'https://szentiras.hu/RUF/Zsid 11,8', 'admin');
INSERT INTO readings (reading_year, reading_month, reading_day, firstreading, firstreading_short, firstreading_link, secondreading, secondreading_short, secondreading_link, created_by) VALUES ('2024', '08', '12', '„Segítségedben reménykedem, Uram, és teljesítem parancsolataidat.” (Zsolt 119,166)', 'Zsolt 119,166', 'https://szentiras.hu/RUF/Zsolt 119,166', '„Így szól az, aki ezekről bizonyságot tesz: Bizony, hamar eljövök.” (Jel 22,20a)', 'Jel 22,20a', 'https://szentiras.hu/RUF/Jel 22,20a', 'admin');
INSERT INTO readings (reading_year, reading_month, reading_day, firstreading, firstreading_short, firstreading_link, secondreading, secondreading_short, secondreading_link, created_by) VALUES ('2024', '08', '13', '„Énekeljetek új éneket az Úrnak, énekelj az Úrnak, te egész föld!” (Zsolt 96,1)', 'Zsolt 96,1', 'https://szentiras.hu/RUF/Zsolt 96,1', '„A tanítványok egész sokasága […] dicsérni kezdte Istent mindazokért a csodákért, amelyeket láttak, és ezt kiáltották: Áldott a király, aki az Úr nevében jön! A mennyben békesség, és dicsőség a magasságban!” (Lk 19,37b–38)', 'Lk 19,37b–38', 'https://szentiras.hu/RUF/Lk 19,37b–38', 'admin');
INSERT INTO readings (reading_year, reading_month, reading_day, firstreading, firstreading_short, firstreading_link, secondreading, secondreading_short, secondreading_link, created_by) VALUES ('2024', '08', '14', '„Mert megáldotta kezed minden munkáját Istened, az Úr, tudta, hogy itt jársz e nagy pusztában.” (5Móz 2,7a)', '5Móz 2,7a', 'https://szentiras.hu/RUF/5Móz 2,7a', '„Ezért tehát a lankadt kezeket és a megroskadt térdeket erősítsétek meg.” (Zsid 12,12)', 'Zsid 12,12', 'https://szentiras.hu/RUF/Zsid 12,12', 'admin');
INSERT INTO readings (reading_year, reading_month, reading_day, firstreading, firstreading_short, firstreading_link, secondreading, secondreading_short, secondreading_link, created_by) VALUES ('2024', '08', '15', '„Én, az Úr vagyok a ti Istenetek, aki kihoztalak benneteket Egyiptomból, hogy ne legyetek többé azoknak a szolgái. Összetörtem igátok jármát, hogy fölegyenesedve járhassatok.” (3Móz 26,13)', '3Móz 26,13', 'https://szentiras.hu/RUF/3Móz 26,13', '„Többé nem mondalak titeket szolgáknak, mert a szolga nem tudja, mit tesz az ura. Titeket azonban barátaimnak mondalak […].” (Jn 15,15)', 'Jn 15,15', 'https://szentiras.hu/RUF/Jn 15,15', 'admin');
INSERT INTO readings (reading_year, reading_month, reading_day, firstreading, firstreading_short, firstreading_link, secondreading, secondreading_short, secondreading_link, created_by) VALUES ('2024', '08', '16', '„Tiszteld apádat és anyádat […]!” (2Móz 20,12)', '2Móz 20,12', 'https://szentiras.hu/RUF/2Móz 20,12', '„Ezért fogadjátok be egymást, ahogyan Krisztus is befogadott titeket Isten dicsőségére.” (Róm 15,7)', 'Róm 15,7', 'https://szentiras.hu/RUF/Róm 15,7', 'admin');
INSERT INTO readings (reading_year, reading_month, reading_day, firstreading, firstreading_short, firstreading_link, secondreading, secondreading_short, secondreading_link, created_by) VALUES ('2024', '08', '17', 'Dávid így imádkozott: „A te ígéreted és szándékod szerint vitted véghez mindezeket a hatalmas dolgokat, hogy megismertesd azokat szolgáddal.” (2Sám 7,21)', '2Sám 7,21', 'https://szentiras.hu/RUF/2Sám 7,21', '„Ha pedig Krisztuséi vagytok, akkor Ábrahám utódai vagytok, és ígéret szerint örökösök.” (Gal 3,29)', 'Gal 3,29', 'https://szentiras.hu/RUF/Gal 3,29', 'admin');
INSERT INTO readings (reading_year, reading_month, reading_day, firstreading, firstreading_short, firstreading_link, secondreading, secondreading_short, secondreading_link, created_by) VALUES ('2024', '08', '18', '„Kevéssel tetted őt [az embert] kisebbé Istennél, dicsőséggel és méltósággal koronáztad meg.” (Zsolt 8,6)', 'Zsolt 8,6', 'https://szentiras.hu/RUF/Zsolt 8,6', '„Az ő isteni hatalma megajándékozott minket mindazzal, ami az életre és a kegyességre való, azáltal, hogy megismertük őt, aki saját dicsőségével és erejével hívott el minket.” (2Pt 1,3)', '2Pt 1,3', 'https://szentiras.hu/RUF/2Pt 1,3', 'admin');
INSERT INTO readings (reading_year, reading_month, reading_day, firstreading, firstreading_short, firstreading_link, secondreading, secondreading_short, secondreading_link, created_by) VALUES ('2024', '08', '19', '„Tégy le a haragról, hagyd a heveskedést, ne légy indulatos, mert az csak rosszra visz!” (Zsolt 37,8)', 'Zsolt 37,8', 'https://szentiras.hu/RUF/Zsolt 37,8', '„Vigyázzatok, hogy senki se fizessen a rosszért rosszal, hanem törekedjetek mindenkor a jóra egymás és mindenki iránt.” (1Thessz 5,15)', '1Thessz 5,15', 'https://szentiras.hu/RUF/1Thessz 5,15', 'admin');
INSERT INTO readings (reading_year, reading_month, reading_day, firstreading, firstreading_short, firstreading_link, secondreading, secondreading_short, secondreading_link, created_by) VALUES ('2024', '08', '20', '„Az ember [Ábrahám szolgája] közben némán figyelte, hogy megtudja, szerencséssé tette-e útját az Úr, vagy sem.” (1Móz 24,21)', '1Móz 24,21', 'https://szentiras.hu/RUF/1Móz 24,21', '„Egyszer, amikor az Úrnak szolgáltak, és böjtöltek, ezt mondta a Szentlélek: Válasszátok ki nekem Barnabást és Sault arra a munkára, amelyre elhívtam őket. Akkor böjtölés, imádkozás és kézrátétel után elbocsátották őket.” (ApCsel 13,2–3)', 'ApCsel 13,2–3', 'https://szentiras.hu/RUF/ApCsel 13,2–3', 'admin');
INSERT INTO readings (reading_year, reading_month, reading_day, firstreading, firstreading_short, firstreading_link, secondreading, secondreading_short, secondreading_link, created_by) VALUES ('2024', '08', '21', '„Emeljük föl szívünket és kezünket Istenhez, aki a mennyekben van.” (JSir 3,41)', 'JSir 3,41', 'https://szentiras.hu/RUF/JSir 3,41', '„Szenved-e valaki közöttetek? Imádkozzék! Öröme van-e valakinek? Énekeljen dicséretet!” (Jak 5,13)', 'Jak 5,13', 'https://szentiras.hu/RUF/Jak 5,13', 'admin');
INSERT INTO readings (reading_year, reading_month, reading_day, firstreading, firstreading_short, firstreading_link, secondreading, secondreading_short, secondreading_link, created_by) VALUES ('2024', '08', '22', '„Erre így szólt az Úr: Ha megtérsz, megengedem, hogy újból szolgálatomba állj.” (Jer 15,19a)', 'Jer 15,19a', 'https://szentiras.hu/RUF/Jer 15,19a', 'Jézus mondja: „Aki tehát vallást tesz rólam az emberek előtt, arról majd én is vallást teszek mennyei Atyám előtt” (Mt 10,32)', 'Mt 10,32', 'https://szentiras.hu/RUF/Mt 10,32', 'admin');
INSERT INTO readings (reading_year, reading_month, reading_day, firstreading, firstreading_short, firstreading_link, secondreading, secondreading_short, secondreading_link, created_by) VALUES ('2024', '08', '23', '„Mikor azt gondoltam, hogy roskad a lábam, szereteted, Uram, támogatott engem.” (Zsolt 94,18)', 'Zsolt 94,18', 'https://szentiras.hu/RUF/Zsolt 94,18', '„Elég neked az én kegyelmem, mert az én erőm erőtlenség által ér célhoz.” (2Kor 12,9a)', '2Kor 12,9a', 'https://szentiras.hu/RUF/2Kor 12,9a', 'admin');
INSERT INTO readings (reading_year, reading_month, reading_day, firstreading, firstreading_short, firstreading_link, secondreading, secondreading_short, secondreading_link, created_by) VALUES ('2024', '08', '24', '„Nézd! Elvettem a bűnödet, és díszes ruhába öltöztetlek téged.” (Zak 3,4b)', 'Zak 3,4b', 'https://szentiras.hu/RUF/Zak 3,4b', '„Az apa viszont ezt mondta szolgáinak: Hozzátok ki hamar a legszebb ruhát, és adjátok rá […], mert ez az én fiam meghalt és feltámadott, elveszett és megtaláltatott.” (Lk 15,22a.24a)', 'Lk 15,22a.24a', 'https://szentiras.hu/RUF/Lk 15,22a.24a', 'admin');
INSERT INTO readings (reading_year, reading_month, reading_day, firstreading, firstreading_short, firstreading_link, secondreading, secondreading_short, secondreading_link, created_by) VALUES ('2024', '08', '25', '„[Uram,] hálát adok neked, hogy meghallgattál, és megszabadítottál.” (Zsolt 118,21)', 'Zsolt 118,21', 'https://szentiras.hu/RUF/Zsolt 118,21', '„Ez jó és kedves a mi üdvözítő Istenünk színe előtt, aki azt akarja, hogy minden ember üdvözüljön, és eljusson az igazság megismerésére.” (1Tim 2,3–4)', '1Tim 2,3–4', 'https://szentiras.hu/RUF/1Tim 2,3–4', 'admin');
INSERT INTO readings (reading_year, reading_month, reading_day, firstreading, firstreading_short, firstreading_link, secondreading, secondreading_short, secondreading_link, created_by) VALUES ('2024', '08', '26', '„Hiszen megmentettél engem a haláltól, lábamat az elbukástól, hogy járhassak Isten színe előtt az élet világosságában.” (Zsolt 56,14)', 'Zsolt 56,14', 'https://szentiras.hu/RUF/Zsolt 56,14', '„Majd megfogva a gyermek kezét, [Jézus] ezt mondta neki: Talitha kúmi!, ami azt jelenti: Leányka, neked mondom, kelj fel! A leányka pedig azonnal felkelt és járt […].” (Mk 5,41–42)', 'Mk 5,41–42', 'https://szentiras.hu/RUF/Mk 5,41–42', 'admin');
INSERT INTO readings (reading_year, reading_month, reading_day, firstreading, firstreading_short, firstreading_link, secondreading, secondreading_short, secondreading_link, created_by) VALUES ('2024', '08', '27', '„Az Úr rendelkezései helyesek, megörvendeztetik a szívet.” (Zsolt 19,9a)', 'Zsolt 19,9a', 'https://szentiras.hu/RUF/Zsolt 19,9a', 'Jézus mondja: „De még boldogabbak azok, akik hallgatják az Isten beszédét, és megtartják.” (Lk 11,28)', 'Lk 11,28', 'https://szentiras.hu/RUF/Lk 11,28', 'admin');
INSERT INTO readings (reading_year, reading_month, reading_day, firstreading, firstreading_short, firstreading_link, secondreading, secondreading_short, secondreading_link, created_by) VALUES ('2024', '08', '28', '„Most azért keressétek szívvel-lélekkel Isteneteket, az Urat!” (1Krón 22,19a)', '1Krón 22,19a', 'https://szentiras.hu/RUF/1Krón 22,19a', '„Fülöp találkozott Nátánaéllel, és így szólt hozzá: Megtaláltuk azt, akiről Mózes írt a törvényben, akiről a próféták is írtak: a názáreti Jézust, József fiát.” (Jn 1,45)', 'Jn 1,45', 'https://szentiras.hu/RUF/Jn 1,45', 'admin');
INSERT INTO readings (reading_year, reading_month, reading_day, firstreading, firstreading_short, firstreading_link, secondreading, secondreading_short, secondreading_link, created_by) VALUES ('2024', '08', '29', '„Ki szerez eledelt a hollónak, amikor fiókái Istenhez kiáltanak, és kóvályognak, mert nincs mit enniük?” (Jób 38,41)', 'Jób 38,41', 'https://szentiras.hu/RUF/Jób 38,41', '„Úgyhogy az sem számít, aki ültet, az sem, aki öntöz, hanem csak Isten, aki a növekedést adja.” (1Kor 3,7)', '1Kor 3,7', 'https://szentiras.hu/RUF/1Kor 3,7', 'admin');
INSERT INTO readings (reading_year, reading_month, reading_day, firstreading, firstreading_short, firstreading_link, secondreading, secondreading_short, secondreading_link, created_by) VALUES ('2024', '08', '30', '„De az Úr terve örökké megmarad, szívének szándéka nemzedékről nemzedékre.” (Zsolt 33,11)', 'Zsolt 33,11', 'https://szentiras.hu/RUF/Zsolt 33,11', '„Őbenne [Krisztusban] lettünk örököseivé is, mivel eleve elrendeltettünk erre annak kijelentett végzése szerint, aki mindent saját akarata és elhatározása szerint cselekszik.” (Ef 1,11)', 'Ef 1,11', 'https://szentiras.hu/RUF/Ef 1,11', 'admin');
INSERT INTO readings (reading_year, reading_month, reading_day, firstreading, firstreading_short, firstreading_link, secondreading, secondreading_short, secondreading_link, created_by) VALUES ('2024', '08', '31', '„Könyörületes leszek hozzájuk, amilyen könyörületes az ember a fiához, aki tiszteli őt.” (Mal 3,17b)', 'Mal 3,17b', 'https://szentiras.hu/RUF/Mal 3,17b', 'Jézus mondja: „Boldogok, akik békét teremtenek, mert ők Isten fiainak neveztetnek.” (Mt 5,9)', 'Mt 5,9', 'https://szentiras.hu/RUF/Mt 5,9', 'admin');
INSERT INTO readings (reading_year, reading_month, reading_day, firstreading, firstreading_short, firstreading_link, secondreading, secondreading_short, secondreading_link, created_by) VALUES ('2024', '09', '1', '„Uram, te voltál hajlékunk nemzedékről nemzedékre. Mielőtt hegyek születtek, mielőtt a föld és a világ létrejött, öröktől fogva mindörökké vagy te, ó, Isten!” (Zsolt 90,1b–2)', 'Zsolt 90,1b–2', 'https://szentiras.hu/RUF/Zsolt 90,1b–2', '„Jézus így válaszolt nekik [tanítványainak]: Higgyetek Istenben!” (Mk 11,22)', 'Mk 11,22', 'https://szentiras.hu/RUF/Mk 11,22', 'admin');
INSERT INTO readings (reading_year, reading_month, reading_day, firstreading, firstreading_short, firstreading_link, secondreading, secondreading_short, secondreading_link, created_by) VALUES ('2024', '09', '2', '„[Az Úr] meggyógyítja a megtört szívűeket, és bekötözi sebeiket.” (Zsolt 147,3)', 'Zsolt 147,3', 'https://szentiras.hu/RUF/Zsolt 147,3', '„…az ő sebei által gyógyultatok meg.” (1Pt 2,24b)', '1Pt 2,24b', 'https://szentiras.hu/RUF/1Pt 2,24b', 'admin');
INSERT INTO readings (reading_year, reading_month, reading_day, firstreading, firstreading_short, firstreading_link, secondreading, secondreading_short, secondreading_link, created_by) VALUES ('2024', '09', '3', '„Minden oldalról körülfogtál, kezedet rajtam tartod.” (Zsolt 139,5)', 'Zsolt 139,5', 'https://szentiras.hu/RUF/Zsolt 139,5', '„Hiszen [Isten] nincs messzire egyikünktől sem, mert őbenne élünk, mozgunk és vagyunk.” (ApCsel 17,27b–28a)', 'ApCsel 17,27b–28a', 'https://szentiras.hu/RUF/ApCsel 17,27b–28a', 'admin');
INSERT INTO readings (reading_year, reading_month, reading_day, firstreading, firstreading_short, firstreading_link, secondreading, secondreading_short, secondreading_link, created_by) VALUES ('2024', '09', '4', '„De egyszer csak egy angyal érintette meg [Illést], és ezt mondta neki: Kelj föl, egyél! Ő fölkelt, evett és ivott, majd annak az ételnek az erejével ment negyven nap és negyven éjjel az Isten hegyéig, a Hórebig.” (1Kir 19,5b.8)', '1Kir 19,5b.8', 'https://szentiras.hu/RUF/1Kir 19,5b.8', '„Ne aggódjatok tehát, és ne kérdezgessétek: Mit együnk? – vagy: Mit igyunk? – vagy: Mit öltsünk magunkra?” (Mt 6,31)', 'Mt 6,31', 'https://szentiras.hu/RUF/Mt 6,31', 'admin');
INSERT INTO readings (reading_year, reading_month, reading_day, firstreading, firstreading_short, firstreading_link, secondreading, secondreading_short, secondreading_link, created_by) VALUES ('2024', '09', '5', '„Segítsd meg népedet, áldd meg örökségedet, légy pásztora, és gondozd örökké!” (Zsolt 28,9)', 'Zsolt 28,9', 'https://szentiras.hu/RUF/Zsolt 28,9', '„Amikor [Jézus] látta a sokaságot, megszánta őket, mert elgyötörtek és elveszettek voltak, mint a juhok pásztor nélkül.” (Mt 9,36)', 'Mt 9,36', 'https://szentiras.hu/RUF/Mt 9,36', 'admin');
INSERT INTO readings (reading_year, reading_month, reading_day, firstreading, firstreading_short, firstreading_link, secondreading, secondreading_short, secondreading_link, created_by) VALUES ('2024', '09', '6', '„Mert nem a magunk igaz tetteiben, hanem a te nagy irgalmadban bízva visszük eléd könyörgéseinket.” (Dán 9,18c)', 'Dán 9,18c', 'https://szentiras.hu/RUF/Dán 9,18c', '„Hiszen kegyelemből van üdvösségetek hit által, és ez nem tőletek van: Isten ajándéka; nem cselekedetekért, hogy senki se dicsekedjék.” (Ef 3,8–9)', 'Ef 3,8–9', 'https://szentiras.hu/RUF/Ef 3,8–9', 'admin');
INSERT INTO readings (reading_year, reading_month, reading_day, firstreading, firstreading_short, firstreading_link, secondreading, secondreading_short, secondreading_link, created_by) VALUES ('2024', '09', '7', '„Ezt mondta Isten [Salamonnak]: Kérj valamit, én megadom neked! Salamon ezt felelte: Adj azért a te szolgádnak engedelmes szívet, hogy tudja kormányozni népedet, különbséget téve a jó és a rossz között.” (1Kir 3,5–6a.9a)', '1Kir 3,5–6a.9a', 'https://szentiras.hu/RUF/1Kir 3,5–6a.9a', '„Majd magával vitte az ördög egy igen magas hegyre, megmutatta neki a világ minden országát és azok dicsőségét, és ezt mondta neki: Mindezt neked adom, ha leborulva imádsz engem. Ekkor így szólt hozzá Jézus: Távozz tőlem, Sátán, mert meg van írva: »Az Urat, a te Istenedet imádd, és csak neki szolgálj!«” (Mt 4,8–10)', 'Mt 4,8–10', 'https://szentiras.hu/RUF/Mt 4,8–10', 'admin');
INSERT INTO readings (reading_year, reading_month, reading_day, firstreading, firstreading_short, firstreading_link, secondreading, secondreading_short, secondreading_link, created_by) VALUES ('2024', '09', '8', '„Taníts engem helyes értelemre és ismeretre, mert hittel fogadtam parancsolataidat.” (Zsolt 119,66)', 'Zsolt 119,66', 'https://szentiras.hu/RUF/Zsolt 119,66', '„Az ostoba és éretlen vitatkozások elől térj ki, tudva, hogy ezek viszálykodást szülnek.” (2Tim 2,23)', '2Tim 2,23', 'https://szentiras.hu/RUF/2Tim 2,23', 'admin');
INSERT INTO readings (reading_year, reading_month, reading_day, firstreading, firstreading_short, firstreading_link, secondreading, secondreading_short, secondreading_link, created_by) VALUES ('2024', '09', '9', '„Boldog ember az, aki az Úrba veti bizodalmát […].” (Zsolt 40,5)', 'Zsolt 40,5', 'https://szentiras.hu/RUF/Zsolt 40,5', '„Ő [András], mihelyt találkozott testvérével, Simonnal, ezt mondta neki: Megtaláltuk a Messiást […].” (Jn 1,41)', 'Jn 1,41', 'https://szentiras.hu/RUF/Jn 1,41', 'admin');
INSERT INTO readings (reading_year, reading_month, reading_day, firstreading, firstreading_short, firstreading_link, secondreading, secondreading_short, secondreading_link, created_by) VALUES ('2024', '09', '10', '„Dicsérjétek az Urat! Hálát adok az Úrnak teljes szívből a becsületes emberek körében és közösségében.” (Zsolt 111,1)', 'Zsolt 111,1', 'https://szentiras.hu/RUF/Zsolt 111,1', '„Dicsérjétek Istenünket, ti szolgái mindnyájan, akik félitek őt, kicsinyek és nagyok.” (Jel 19,5b)', 'Jel 19,5b', 'https://szentiras.hu/RUF/Jel 19,5b', 'admin');
INSERT INTO readings (reading_year, reading_month, reading_day, firstreading, firstreading_short, firstreading_link, secondreading, secondreading_short, secondreading_link, created_by) VALUES ('2024', '09', '11', '„Tiszta szívet teremts bennem, Istenem, és az erős lelket újítsd meg bennem!” (Zsolt 51,12)', 'Zsolt 51,12', 'https://szentiras.hu/RUF/Zsolt 51,12', '„A parancs célja pedig a tiszta szívből, jó lelkiismeretből és képmutatás nélküli hitből fakadó szeretet.” (1Tim 1,5)', '1Tim 1,5', 'https://szentiras.hu/RUF/1Tim 1,5', 'admin');
INSERT INTO readings (reading_year, reading_month, reading_day, firstreading, firstreading_short, firstreading_link, secondreading, secondreading_short, secondreading_link, created_by) VALUES ('2024', '09', '12', '„Szolgáltassatok igazságot az elesettnek és szűkölködőnek!” (Zsolt 82,3)', 'Zsolt 82,3', 'https://szentiras.hu/RUF/Zsolt 82,3', 'Titeket pedig az Úr gyarapítson és gazdagítson a szeretetben egymás iránt és mindenki iránt, „ahogyan mi is szeretünk titeket.” (1Thessz 3,12)', '1Thessz 3,12', 'https://szentiras.hu/RUF/1Thessz 3,12', 'admin');
INSERT INTO readings (reading_year, reading_month, reading_day, firstreading, firstreading_short, firstreading_link, secondreading, secondreading_short, secondreading_link, created_by) VALUES ('2024', '09', '13', '„Tégy csodát híveddel, mert te megszabadítod azokat, akik jobbodhoz menekülnek támadóik elől.” (Zsolt 17,7)', 'Zsolt 17,7', 'https://szentiras.hu/RUF/Zsolt 17,7', '„Boldogok vagytok, ha gyaláznak titeket Krisztus nevéért, mert a dicsőség Lelke, Isten Lelke megnyugszik rajtatok.” (1Pt 4,14)', '1Pt 4,14', 'https://szentiras.hu/RUF/1Pt 4,14', 'admin');
INSERT INTO readings (reading_year, reading_month, reading_day, firstreading, firstreading_short, firstreading_link, secondreading, secondreading_short, secondreading_link, created_by) VALUES ('2024', '09', '14', '„Ha megtelik szívem aggodalommal, vigasztalásod felüdíti lelkemet.” (Zsolt 94,19)', 'Zsolt 94,19', 'https://szentiras.hu/RUF/Zsolt 94,19', 'Jézus mondta: „Atyám, ha akarod, vedd el tőlem ezt a poharat; mindazáltal ne az én akaratom legyen meg, hanem a tied. Ekkor angyal jelent meg neki a mennyből, hogy erősítse őt.” (Lk 22,42–43)', 'Lk 22,42–43', 'https://szentiras.hu/RUF/Lk 22,42–43', 'admin');
INSERT INTO readings (reading_year, reading_month, reading_day, firstreading, firstreading_short, firstreading_link, secondreading, secondreading_short, secondreading_link, created_by) VALUES ('2024', '09', '15', '„[Amikor] ehetsz […] jóllakásig, akkor vigyázz: ne feledkezz meg az Úrról, aki kihozott téged Egyiptom földjéről, a szolgaság házából!” (5Móz 6,11b–12)', '5Móz 6,11b–12', 'https://szentiras.hu/RUF/5Móz 6,11b–12', '„Jézus pedig így válaszolt nekik: Bizony, bizony, mondom nektek, nem Mózes adta nektek a mennyei kenyeret, hanem az én Atyám adja nektek az igazi mennyei kenyeret.” (Jn 6,32)', 'Jn 6,32', 'https://szentiras.hu/RUF/Jn 6,32', 'admin');
INSERT INTO readings (reading_year, reading_month, reading_day, firstreading, firstreading_short, firstreading_link, secondreading, secondreading_short, secondreading_link, created_by) VALUES ('2024', '09', '16', '„De az én Uram, az Úr megsegít engem, ezért nem maradok gyalázatban.” (Ézs 50,7a)', 'Ézs 50,7a', 'https://szentiras.hu/RUF/Ézs 50,7a', '„Amikor azonban [Péter] az erős szélre figyelt, megijedt, és amint süllyedni kezdett, felkiáltott: Uram, ments meg!” (Mt 14,30)', 'Mt 14,30', 'https://szentiras.hu/RUF/Mt 14,30', 'admin');
INSERT INTO readings (reading_year, reading_month, reading_day, firstreading, firstreading_short, firstreading_link, secondreading, secondreading_short, secondreading_link, created_by) VALUES ('2024', '09', '17', '„Megkoronázod az évet javaiddal, és nyomaidon bőség fakad.” (Zsolt 65,12)', 'Zsolt 65,12', 'https://szentiras.hu/RUF/Zsolt 65,12', '„[Isten] jótevőtök volt, a mennyből esőt adott nektek és termést hozó időket, bőven adott nektek eledelt és szívbéli örömet.” (ApCsel 14,17)', 'ApCsel 14,17', 'https://szentiras.hu/RUF/ApCsel 14,17', 'admin');
INSERT INTO readings (reading_year, reading_month, reading_day, firstreading, firstreading_short, firstreading_link, secondreading, secondreading_short, secondreading_link, created_by) VALUES ('2024', '09', '18', '„Csillag jön fel Jákóbból, jogar támad Izráelből.” (4Móz 24,17b)', '4Móz 24,17b', 'https://szentiras.hu/RUF/4Móz 24,17b', '„Én, Jézus, küldtem el angyalomat, hogy ezekről bizonyságot tegyen nektek a gyülekezetek előtt. Én vagyok Dávid gyökere és új hajtása, a fényes hajnalcsillag.” (Jel 22,16)', 'Jel 22,16', 'https://szentiras.hu/RUF/Jel 22,16', 'admin');
INSERT INTO readings (reading_year, reading_month, reading_day, firstreading, firstreading_short, firstreading_link, secondreading, secondreading_short, secondreading_link, created_by) VALUES ('2024', '09', '19', '„[Uram,] ne szállj perbe szolgáddal, hiszen egy élő sem igaz előtted!” (Zsolt 143,2)', 'Zsolt 143,2', 'https://szentiras.hu/RUF/Zsolt 143,2', '„Az asszonynak pedig ezt mondta [Jézus]: Megbocsáttattak a te bűneid. […] A te hited megtartott téged, menj el békességgel!” (Lk 7,48.50b)', 'Lk 7,48.50b', 'https://szentiras.hu/RUF/Lk 7,48.50b', 'admin');
INSERT INTO readings (reading_year, reading_month, reading_day, firstreading, firstreading_short, firstreading_link, secondreading, secondreading_short, secondreading_link, created_by) VALUES ('2024', '09', '20', '„[Isten] egymaga feszítette ki az eget, lépdel a tenger hullámhegyein. Ő alkotta a Nagy Medvét és a Kaszáscsillagot, a Fiastyúkot és a Dél csillagait.” (Jób 9,8–9)', 'Jób 9,8–9', 'https://szentiras.hu/RUF/Jób 9,8–9', '„Nekünk mégis egyetlen Istenünk az Atya, akitől van a mindenség, mi is őérte, és egyetlen Urunk Jézus Krisztus, aki által van a mindenség, mi is őáltala.” (1Kor 8,6)', '1Kor 8,6', 'https://szentiras.hu/RUF/1Kor 8,6', 'admin');
INSERT INTO readings (reading_year, reading_month, reading_day, firstreading, firstreading_short, firstreading_link, secondreading, secondreading_short, secondreading_link, created_by) VALUES ('2024', '09', '21', '„Az örvendező szív megszépíti az arcot, a bánatos szív pedig összetöri a lelket.” (Péld 15,13)', 'Péld 15,13', 'https://szentiras.hu/RUF/Péld 15,13', '„Örvendezve haladt tovább az útján.” (ApCsel 8,39)', 'ApCsel 8,39', 'https://szentiras.hu/RUF/ApCsel 8,39', 'admin');
INSERT INTO readings (reading_year, reading_month, reading_day, firstreading, firstreading_short, firstreading_link, secondreading, secondreading_short, secondreading_link, created_by) VALUES ('2024', '09', '22', '„Ez lesz a békesség!” (Mik 5,4a)', 'Mik 5,4a', 'https://szentiras.hu/RUF/Mik 5,4a', '„Ne fizessetek a gonoszért gonosszal, vagy a gyalázkodásért gyalázkodással, hanem ellenkezőleg: mondjatok áldást, hiszen arra hívattatok el, hogy áldást örököljetek.” (1Pt 3,9)', '1Pt 3,9', 'https://szentiras.hu/RUF/1Pt 3,9', 'admin');
INSERT INTO readings (reading_year, reading_month, reading_day, firstreading, firstreading_short, firstreading_link, secondreading, secondreading_short, secondreading_link, created_by) VALUES ('2024', '09', '23', '„Megmutatta szabadító erejét az Úr, a népek szeme előtt nyilvánvalóvá tette igazságát.” (Zsolt 98,2)', 'Zsolt 98,2', 'https://szentiras.hu/RUF/Zsolt 98,2', '„Boldogok, akik éheznek és szomjaznak az igazságra, mert ők megelégíttetnek.” (Mt 5,6)', 'Mt 5,6', 'https://szentiras.hu/RUF/Mt 5,6', 'admin');
INSERT INTO readings (reading_year, reading_month, reading_day, firstreading, firstreading_short, firstreading_link, secondreading, secondreading_short, secondreading_link, created_by) VALUES ('2024', '09', '24', '„Mert ahogyan a föld növényeket hajt, és a kert veteményeket sarjaszt, úgy sarjaszt majd igazságot az én Uram, az Úr is, és öröméneket minden nép hallatára.” (Ézs 61,11)', 'Ézs 61,11', 'https://szentiras.hu/RUF/Ézs 61,11', '„Hasonló [az Isten országa] a mustármaghoz, amelyet fogott egy ember, és elvetett a kertjében, az pedig felnövekedett, és fává lett, úgyhogy az égi madarak fészket raktak ágai között.” (Lk 13,19)', 'Lk 13,19', 'https://szentiras.hu/RUF/Lk 13,19', 'admin');
INSERT INTO readings (reading_year, reading_month, reading_day, firstreading, firstreading_short, firstreading_link, secondreading, secondreading_short, secondreading_link, created_by) VALUES ('2024', '09', '25', '„Én vagyok az Úr, minden élőnek az Istene. Van-e számomra lehetetlen?” (Jer 32,27)', 'Jer 32,27', 'https://szentiras.hu/RUF/Jer 32,27', '„És amikor [a tanítványok] meglátták őt, leborultak előtte, némelyek azonban kételkedtek. Jézus pedig hozzájuk lépett, és így szólt: Nekem adatott minden hatalom mennyen és földön.” (Mt 28,17–18)', 'Mt 28,17–18', 'https://szentiras.hu/RUF/Mt 28,17–18', 'admin');
INSERT INTO readings (reading_year, reading_month, reading_day, firstreading, firstreading_short, firstreading_link, secondreading, secondreading_short, secondreading_link, created_by) VALUES ('2024', '09', '26', '„Abban telik kedvem, Istenem, hogy akaratodat teljesítsem, törvényed szívemben van.” (Zsolt 40,9)', 'Zsolt 40,9', 'https://szentiras.hu/RUF/Zsolt 40,9', '„Jézus így válaszolt [a törvénytudónak]: »Szeresd az Urat, a te Istenedet teljes szívedből, teljes lelkedből és teljes elmédből.” Ez az első és a nagy parancsolat. A második hasonló ehhez: „Szeresd felebarátodat, mint magadat.«” (Mt 22,37–39)', 'Mt 22,37–39', 'https://szentiras.hu/RUF/Mt 22,37–39', 'admin');
INSERT INTO readings (reading_year, reading_month, reading_day, firstreading, firstreading_short, firstreading_link, secondreading, secondreading_short, secondreading_link, created_by) VALUES ('2024', '09', '27', '„Ezért örvendjetek és vigadjatok mindörökké annak, amit majd teremtek!” (Ézs 65,18a)', 'Ézs 65,18a', 'https://szentiras.hu/RUF/Ézs 65,18a', '„Mert Isten minden teremtménye jó, és semmi sem elvetendő, ha hálaadással élnek vele, mert Isten igéje és a könyörgés megszenteli.” (1Tim 4,4–5)', '1Tim 4,4–5', 'https://szentiras.hu/RUF/1Tim 4,4–5', 'admin');
INSERT INTO readings (reading_year, reading_month, reading_day, firstreading, firstreading_short, firstreading_link, secondreading, secondreading_short, secondreading_link, created_by) VALUES ('2024', '09', '28', '„Fogadd kedvesen számnak mondásait és szívem gondolatait, Uram, kősziklám és megváltóm!” (Zsolt 19,15)', 'Zsolt 19,15', 'https://szentiras.hu/RUF/Zsolt 19,15', '„Mária pedig mindezeket a beszédeket megjegyezte, és szívében forgatta.” (Lk 2,19)', 'Lk 2,19', 'https://szentiras.hu/RUF/Lk 2,19', 'admin');
INSERT INTO readings (reading_year, reading_month, reading_day, firstreading, firstreading_short, firstreading_link, secondreading, secondreading_short, secondreading_link, created_by) VALUES ('2024', '09', '29', '„Szeressétek [a jövevényt], mint magatokat…” (3Móz 19,34b)', '3Móz 19,34b', 'https://szentiras.hu/RUF/3Móz 19,34b', '„Itt már nincs többé görög és zsidó, körülmetéltség és körülmetéletlenség, barbár és szkíta, szolga és szabad, hanem minden és mindenekben Krisztus.” (Kol 3,11)', 'Kol 3,11', 'https://szentiras.hu/RUF/Kol 3,11', 'admin');
INSERT INTO readings (reading_year, reading_month, reading_day, firstreading, firstreading_short, firstreading_link, secondreading, secondreading_short, secondreading_link, created_by) VALUES ('2024', '09', '30', '„Odakiáltott Abnér Jóábnak: Hát szüntelenül vágnia kell a kardnak? Nem tudod, hogy keserves vége lesz ennek?” (2Sám 2,26a)', '2Sám 2,26a', 'https://szentiras.hu/RUF/2Sám 2,26a', 'Jézus ezt mondta: „Tedd vissza kardodat a helyére, mert akik kardot fognak, kard által vesznek el.” (Mt 26,52)', 'Mt 26,52', 'https://szentiras.hu/RUF/Mt 26,52', 'admin');
INSERT INTO readings (reading_year, reading_month, reading_day, firstreading, firstreading_short, firstreading_link, secondreading, secondreading_short, secondreading_link, created_by) VALUES ('2024', '10', '1', '„Ezért magasztallak, Uram, a népek között, zsoltárt éneklek nevednek.” (Zsolt 18,50)', 'Zsolt 18,50', 'https://szentiras.hu/RUF/Zsolt 18,50', '„Ki ne félne téged, Urunk, és ki ne dicsőítené a te nevedet, hiszen egyedül te vagy szent.” (Jel 15,4a)', 'Jel 15,4a', 'https://szentiras.hu/RUF/Jel 15,4a', 'admin');
INSERT INTO readings (reading_year, reading_month, reading_day, firstreading, firstreading_short, firstreading_link, secondreading, secondreading_short, secondreading_link, created_by) VALUES ('2024', '10', '2', '„Nem éheznek és nem szomjaznak majd, nem bántja őket a nap heve, mert az terelgeti őket, aki könyörült rajtuk, az vezeti őket forrásvizekhez.” (Ézs 49,10)', 'Ézs 49,10', 'https://szentiras.hu/RUF/Ézs 49,10', '„Uram, add nekem azt a vizet, hogy ne szomjazzam meg, és ne kelljen idejárnom meríteni.” (Jn 4,15)', 'Jn 4,15', 'https://szentiras.hu/RUF/Jn 4,15', 'admin');
INSERT INTO readings (reading_year, reading_month, reading_day, firstreading, firstreading_short, firstreading_link, secondreading, secondreading_short, secondreading_link, created_by) VALUES ('2024', '10', '3', '„De valóra váltom szolgáim beszédét, és követeim tervét megvalósítom. Én mondom azt Jeruzsálemnek: Lakjanak tebenned! – és Júda városainak: Épüljetek újjá!” (Ézs 44,26)', 'Ézs 44,26', 'https://szentiras.hu/RUF/Ézs 44,26', '„Ezért egészen bizonyosnak tartjuk a prófétai beszédet, amelyre jól teszitek, ha figyeltek, mint sötét helyen világító lámpásra, amíg felvirrad a nap, és felkel a hajnalcsillag szívetekben.” (2Pt 1,19)', '2Pt 1,19', 'https://szentiras.hu/RUF/2Pt 1,19', 'admin');
INSERT INTO readings (reading_year, reading_month, reading_day, firstreading, firstreading_short, firstreading_link, secondreading, secondreading_short, secondreading_link, created_by) VALUES ('2024', '10', '4', '„Sóvárogva tekintek ígéretedre: Mikor vigasztalsz meg engem?” (Zsolt 119,82)', 'Zsolt 119,82', 'https://szentiras.hu/RUF/Zsolt 119,82', '„Járuljunk tehát bizalommal a kegyelem trónusához, hogy irgalmat nyerjünk, és kegyelmet találjunk, amikor segítségre van szükségünk.” (Zsid 4,16)', 'Zsid 4,16', 'https://szentiras.hu/RUF/Zsid 4,16', 'admin');
INSERT INTO readings (reading_year, reading_month, reading_day, firstreading, firstreading_short, firstreading_link, secondreading, secondreading_short, secondreading_link, created_by) VALUES ('2024', '10', '5', '„A te nevedért kérünk, ne utálj meg minket, ne engedd, hogy meggyalázzák dicső trónodat!” (Jer 14,21a)', 'Jer 14,21a', 'https://szentiras.hu/RUF/Jer 14,21a', '„Isten nem vetette el az ő népét, amelyet eleve kiválasztott.” (Róm 11,2a)', 'Róm 11,2a', 'https://szentiras.hu/RUF/Róm 11,2a', 'admin');
INSERT INTO readings (reading_year, reading_month, reading_day, firstreading, firstreading_short, firstreading_link, secondreading, secondreading_short, secondreading_link, created_by) VALUES ('2024', '10', '6', '„A népek olyanok előtte, mint egy vízcsepp a vödörben, annyit érnek, mint egy porszem a mérlegserpenyőn […].” (Ézs 40,15a)', 'Ézs 40,15a', 'https://szentiras.hu/RUF/Ézs 40,15a', '„Vagy Isten kizárólag a zsidóké? Nem a népeké is? Bizony, a népeké is.” (Róm 3,29)', 'Róm 3,29', 'https://szentiras.hu/RUF/Róm 3,29', 'admin');
INSERT INTO readings (reading_year, reading_month, reading_day, firstreading, firstreading_short, firstreading_link, secondreading, secondreading_short, secondreading_link, created_by) VALUES ('2024', '10', '7', '„Ha bűneink ellenünk szólnak is, tégy jót velünk, Uram, a te nevedért!” (Jer 14,7a)', 'Jer 14,7a', 'https://szentiras.hu/RUF/Jer 14,7a', '„Mert ha az embereknek megbocsátjátok vétkeiket, nektek is megbocsát mennyei Atyátok.” (Mt 6,14)', 'Mt 6,14', 'https://szentiras.hu/RUF/Mt 6,14', 'admin');
INSERT INTO readings (reading_year, reading_month, reading_day, firstreading, firstreading_short, firstreading_link, secondreading, secondreading_short, secondreading_link, created_by) VALUES ('2024', '10', '8', '„Most azért az Urat féljétek, és őt szolgáljátok hűen és feddhetetlenül! Távolítsátok el azokat az isteneket, amelyeket atyáitok szolgáltak […], és az Urat szolgáljátok!” (Józs 24,14)', 'Józs 24,14', 'https://szentiras.hu/RUF/Józs 24,14', '„Amikor pedig még nem ismertétek Istent, olyan isteneknek szolgáltatok, amelyek valójában nem azok. Most azonban, miután megismertétek Istent, vagy még inkább: Isten ismert meg titeket, hogyan lehetséges az, hogy ismét visszatértek az erőtlen és szegény elemekhez, akiknek ismét szolgálni akartok?” (Gal 4,8–9)', 'Gal 4,8–9', 'https://szentiras.hu/RUF/Gal 4,8–9', 'admin');
INSERT INTO readings (reading_year, reading_month, reading_day, firstreading, firstreading_short, firstreading_link, secondreading, secondreading_short, secondreading_link, created_by) VALUES ('2024', '10', '9', '„Kezed alkotott és megerősített, tégy értelmessé, hogy megtanuljam parancsolataidat!” (Zsolt 119,73)', 'Zsolt 119,73', 'https://szentiras.hu/RUF/Zsolt 119,73', 'Pál írja: „Ezért tehát, testvéreim, álljatok szilárdan, és ragaszkodjatok azokhoz a hagyományokhoz, amelyeket akár beszédünkből, akár levelünkből tanultatok.” (2Thessz 2,15)', '2Thessz 2,15', 'https://szentiras.hu/RUF/2Thessz 2,15', 'admin');
INSERT INTO readings (reading_year, reading_month, reading_day, firstreading, firstreading_short, firstreading_link, secondreading, secondreading_short, secondreading_link, created_by) VALUES ('2024', '10', '10', '„Kiszáradtak a csontjaink, és elveszett a reménységünk, végünk van. Azért prófétálj, és ezt mondd nekik: Így szól az én Uram, az Úr: Íme, felnyitom sírjaitokat, és kihozlak sírjaitokból, én népem, és beviszlek benneteket Izráel földjére.” (Ez 37,11b–12)', 'Ez 37,11b–12', 'https://szentiras.hu/RUF/Ez 37,11b–12', '„Így van a halottak feltámadása is. Elvettetik romlandóságban, feltámasztatik romolhatatlanságban. Elvettetik földi test, feltámasztatik lelki test.” (1Kor 15,42.44a)', '1Kor 15,42.44a', 'https://szentiras.hu/RUF/1Kor 15,42.44a', 'admin');
INSERT INTO readings (reading_year, reading_month, reading_day, firstreading, firstreading_short, firstreading_link, secondreading, secondreading_short, secondreading_link, created_by) VALUES ('2024', '10', '11', '„De nálad van a bocsánat, ezért félnek téged.” (Zsolt 130,4)', 'Zsolt 130,4', 'https://szentiras.hu/RUF/Zsolt 130,4', '„Jézus pedig így könyörgött: Atyám, bocsáss meg nekik, mert nem tudják, mit cselekszenek!” (Lk 23,34a)', 'Lk 23,34a', 'https://szentiras.hu/RUF/Lk 23,34a', 'admin');
INSERT INTO readings (reading_year, reading_month, reading_day, firstreading, firstreading_short, firstreading_link, secondreading, secondreading_short, secondreading_link, created_by) VALUES ('2024', '10', '12', '„Erről beszélt az Úr, amikor azt mondta: A hozzám közel állókon mutatom meg, hogy szent vagyok, és az egész nép előtt, hogy dicsőséges vagyok!” (3Móz 10,3a)', '3Móz 10,3a', 'https://szentiras.hu/RUF/3Móz 10,3a', '„Mivel tehát megigazultunk hitből, békességünk van Istennel a mi Urunk Jézus Krisztus által.” (Róm 5,1)', 'Róm 5,1', 'https://szentiras.hu/RUF/Róm 5,1', 'admin');
INSERT INTO readings (reading_year, reading_month, reading_day, firstreading, firstreading_short, firstreading_link, secondreading, secondreading_short, secondreading_link, created_by) VALUES ('2024', '10', '13', 'József így szólt testvéreihez: „Ti rosszat terveztetek ellenem, de Isten terve jóra fordította azt […]” (1Móz 50,20a)', '1Móz 50,20a', 'https://szentiras.hu/RUF/1Móz 50,20a', '„Áldjátok azokat, akik átkoznak, és imádkozzatok azokért, akik bántalmaznak titeket!” (Lk 6,28)', 'Lk 6,28', 'https://szentiras.hu/RUF/Lk 6,28', 'admin');
INSERT INTO readings (reading_year, reading_month, reading_day, firstreading, firstreading_short, firstreading_link, secondreading, secondreading_short, secondreading_link, created_by) VALUES ('2024', '10', '14', '„Minden lélek dicsérje az Urat! Dicsérjétek az Urat!” (Zsolt 150,6)', 'Zsolt 150,6', 'https://szentiras.hu/RUF/Zsolt 150,6', '„Az egyedül üdvözítő Istennek a mi Urunk Jézus Krisztus által: dicsőség, fenség, erő és hatalom öröktől fogva, most és mindörökké.” (Júd 25)', 'Júd 25', 'https://szentiras.hu/RUF/Júd 25', 'admin');
INSERT INTO readings (reading_year, reading_month, reading_day, firstreading, firstreading_short, firstreading_link, secondreading, secondreading_short, secondreading_link, created_by) VALUES ('2024', '10', '15', '„Maga a menny Istene vezet bennünket sikerre, és mi az ő szolgáiként kezdjük el az építést.” (Neh 2,20a)', 'Neh 2,20a', 'https://szentiras.hu/RUF/Neh 2,20a', '„Maga pedig a mi Urunk Jézus Krisztus és Isten, a mi Atyánk, aki szeretett minket, és kegyelméből örök vigasztalással és jó reménységgel ajándékozott meg, vigasztalja meg a ti szíveteket, és erősítsen meg titeket minden jó cselekedetben és beszédben.” (2Thessz 2,16–17)', '2Thessz 2,16–17', 'https://szentiras.hu/RUF/2Thessz 2,16–17', 'admin');
INSERT INTO readings (reading_year, reading_month, reading_day, firstreading, firstreading_short, firstreading_link, secondreading, secondreading_short, secondreading_link, created_by) VALUES ('2024', '10', '16', '„Jobb egy tányér főzelék ott, ahol szeretet van, mint a hizlalt ökör, ahol gyűlölet van.” (Péld 15,17)', 'Péld 15,17', 'https://szentiras.hu/RUF/Péld 15,17', '„[Jézus] észrevett egy szegény özvegyasszonyt is, aki két fillért dobott abba [a perselybe], és így szólt: Bizony mondom nektek, hogy ez a szegény özvegyasszony mindenkinél többet dobott a perselybe. Azok ugyanis mind a feleslegükből dobtak az áldozati ajándékokhoz, ő azonban szegénységéből mindent beledobott, amije volt, az egész vagyonát.” (Lk 21,2–4)', 'Lk 21,2–4', 'https://szentiras.hu/RUF/Lk 21,2–4', 'admin');
INSERT INTO readings (reading_year, reading_month, reading_day, firstreading, firstreading_short, firstreading_link, secondreading, secondreading_short, secondreading_link, created_by) VALUES ('2024', '10', '17', '„Nem hagy akkor cserben, és nem hagy elpusztulni, mert irgalmas Isten az Úr, a te Istened.” (5Móz 4,31a)', '5Móz 4,31a', 'https://szentiras.hu/RUF/5Móz 4,31a', '„Az iránta való bizalmunk pedig azt jelenti, hogy ha bármit kérünk az ő akarata szerint, meghallgat minket.” (1Jn 5,14)', '1Jn 5,14', 'https://szentiras.hu/RUF/1Jn 5,14', 'admin');
INSERT INTO readings (reading_year, reading_month, reading_day, firstreading, firstreading_short, firstreading_link, secondreading, secondreading_short, secondreading_link, created_by) VALUES ('2024', '10', '18', '„Megvetett volt, és emberektől elhagyatott, fájdalmak férfia, betegség ismerője.” (Ézs 53,3a)', 'Ézs 53,3a', 'https://szentiras.hu/RUF/Ézs 53,3a', '„[Az Emberfiát] átadják a pogányoknak, kigúnyolják, meggyalázzák, leköpik, és miután megostorozták, megölik, de a harmadik napon feltámad.” (Lk 18,32–33)', 'Lk 18,32–33', 'https://szentiras.hu/RUF/Lk 18,32–33', 'admin');
INSERT INTO readings (reading_year, reading_month, reading_day, firstreading, firstreading_short, firstreading_link, secondreading, secondreading_short, secondreading_link, created_by) VALUES ('2024', '10', '19', '„Istenem, ne hagyj el késő vénségemben sem, míg csak hirdethetem hatalmadat, nagy tetteidet a jövő nemzedéknek.” (Zsolt 71,18)', 'Zsolt 71,18', 'https://szentiras.hu/RUF/Zsolt 71,18', '„Volt ott egy prófétanő is, Anna […], nyolcvannégy éves özvegyasszony volt. Nem távozott el a templomból, hanem böjtöléssel és imádkozással szolgált éjjel és nappal. Abban az órában is megjelent, hálát adott Istennek, és beszélt róla mindazoknak, akik várták Jeruzsálem megváltását.” (Lk 2,36–38)', 'Lk 2,36–38', 'https://szentiras.hu/RUF/Lk 2,36–38', 'admin');
INSERT INTO readings (reading_year, reading_month, reading_day, firstreading, firstreading_short, firstreading_link, secondreading, secondreading_short, secondreading_link, created_by) VALUES ('2024', '10', '20', '„Ezt mondta nekem [Jeremiásnak] az Úr: Én most a szádba adom igéimet!” (Jer 1,9)', 'Jer 1,9', 'https://szentiras.hu/RUF/Jer 1,9', 'Pál írja: „És én erőtlenség, félelem és nagy rettegés között jelentem meg nálatok. Beszédem és igehirdetésem sem az emberi bölcsesség megejtő szavaival hangzott hozzátok, hanem a Lélek bizonyító erejével, hogy hitetek ne emberek bölcsességén, hanem Isten erején nyugodjék.” (1Kor 2,3–5)', '1Kor 2,3–5', 'https://szentiras.hu/RUF/1Kor 2,3–5', 'admin');
INSERT INTO readings (reading_year, reading_month, reading_day, firstreading, firstreading_short, firstreading_link, secondreading, secondreading_short, secondreading_link, created_by) VALUES ('2024', '10', '21', '„[Népemet] magamnak formáltam, hogy hirdesse dicséretemet.” (Ézs 43,21)', 'Ézs 43,21', 'https://szentiras.hu/RUF/Ézs 43,21', '„Krisztus azonban mint Fiú hű a maga házához. Az ő háza mi vagyunk, ha a bizalmat és a reménység dicsekedését mindvégig szilárdan megtartjuk.” (Zsid 3,6)', 'Zsid 3,6', 'https://szentiras.hu/RUF/Zsid 3,6', 'admin');
INSERT INTO readings (reading_year, reading_month, reading_day, firstreading, firstreading_short, firstreading_link, secondreading, secondreading_short, secondreading_link, created_by) VALUES ('2024', '10', '22', '„Ti vagytok a tanúim – így szól az Úr –, és az én szolgám, akit kiválasztottam, hogy megismerjetek, higgyetek bennem […].” (Ézs 43,10a)', 'Ézs 43,10a', 'https://szentiras.hu/RUF/Ézs 43,10a', 'Jézus mondja: „Ellenben erőt kaptok, amikor eljön hozzátok a Szentlélek, és tanúim lesztek Jeruzsálemben, egész Júdeában és Samáriában, sőt a föld végső határáig.” (ApCsel 1,8)', 'ApCsel 1,8', 'https://szentiras.hu/RUF/ApCsel 1,8', 'admin');
INSERT INTO readings (reading_year, reading_month, reading_day, firstreading, firstreading_short, firstreading_link, secondreading, secondreading_short, secondreading_link, created_by) VALUES ('2024', '10', '23', '„Öregkoromban se vess el engem, ha elfogy az erőm, ne hagyj el!” (Zsolt 71,9)', 'Zsolt 71,9', 'https://szentiras.hu/RUF/Zsolt 71,9', '„Bizony, bizony, mondom neked: amikor fiatalabb voltál, felövezted magadat, és oda mentél, ahova akartál; de amikor megöregszel, kinyújtod a kezedet, más övez fel téged, és oda visz, ahova nem akarod.” (Jn 21,18)', 'Jn 21,18', 'https://szentiras.hu/RUF/Jn 21,18', 'admin');
INSERT INTO readings (reading_year, reading_month, reading_day, firstreading, firstreading_short, firstreading_link, secondreading, secondreading_short, secondreading_link, created_by) VALUES ('2024', '10', '24', '„Mert nálad van az élet forrása, a te világosságod által látunk világosságot.” (Zsolt 36,10)', 'Zsolt 36,10', 'https://szentiras.hu/RUF/Zsolt 36,10', '„Benne élet volt, és az élet volt az emberek világossága.” (Jn 1,4)', 'Jn 1,4', 'https://szentiras.hu/RUF/Jn 1,4', 'admin');
INSERT INTO readings (reading_year, reading_month, reading_day, firstreading, firstreading_short, firstreading_link, secondreading, secondreading_short, secondreading_link, created_by) VALUES ('2024', '10', '25', '„Ragaszkodj az intelemhez, ne térj el tőle, vigyázz rá, mert ez a te életed!” (Péld 4,13)', 'Péld 4,13', 'https://szentiras.hu/RUF/Péld 4,13', '„De te maradj meg abban, amit tanultál, és amiről megbizonyosodtál, tudván, kiktől tanultad, mivel gyermekségedtől ismered a szent írásokat, amelyek bölccsé tehetnek téged az üdvösségre a Krisztus Jézusba vetett hit által.” (2Tim 3,14–15)', '2Tim 3,14–15', 'https://szentiras.hu/RUF/2Tim 3,14–15', 'admin');
INSERT INTO readings (reading_year, reading_month, reading_day, firstreading, firstreading_short, firstreading_link, secondreading, secondreading_short, secondreading_link, created_by) VALUES ('2024', '10', '26', '„Az Úr az én pásztorom, nem szűkölködöm.” (Zsolt 23,1)', 'Zsolt 23,1', 'https://szentiras.hu/RUF/Zsolt 23,1', 'Jézus mondja: „Én vagyok a jó pásztor. A jó pásztor életét adja a juhokért.” (Jn 10,11)', 'Jn 10,11', 'https://szentiras.hu/RUF/Jn 10,11', 'admin');
INSERT INTO readings (reading_year, reading_month, reading_day, firstreading, firstreading_short, firstreading_link, secondreading, secondreading_short, secondreading_link, created_by) VALUES ('2024', '10', '27', '„Halld meg, Izráel: Az Úr a mi Istenünk, egyedül az Úr!” (5Móz 6,4)', '5Móz 6,4', 'https://szentiras.hu/RUF/5Móz 6,4', '„És különbségek vannak az isteni erő megnyilvánulásaiban is, de Isten, aki mindezt véghezviszi mindenkiben, ugyanaz.” (1Kor 12,6)', '1Kor 12,6', 'https://szentiras.hu/RUF/1Kor 12,6', 'admin');
INSERT INTO readings (reading_year, reading_month, reading_day, firstreading, firstreading_short, firstreading_link, secondreading, secondreading_short, secondreading_link, created_by) VALUES ('2024', '10', '28', '„Mert nem hagysz engem a holtak hazájában, nem engeded, hogy híved leszálljon a sírba.” (Zsolt 16,10)', 'Zsolt 16,10', 'https://szentiras.hu/RUF/Zsolt 16,10', '„Jézus ekkor ezt mondta neki [Mártának]: Én vagyok a feltámadás és az élet, aki hisz énbennem, ha meghal is, él; és aki él, és hisz énbennem, az nem hal meg soha. Hiszed-e ezt?” (Jn 11,25–26)', 'Jn 11,25–26', 'https://szentiras.hu/RUF/Jn 11,25–26', 'admin');
INSERT INTO readings (reading_year, reading_month, reading_day, firstreading, firstreading_short, firstreading_link, secondreading, secondreading_short, secondreading_link, created_by) VALUES ('2024', '10', '29', '„Csak az Úr ad bölcsességet, szájából ismeret és értelem származik.” (Péld 2,6)', 'Péld 2,6', 'https://szentiras.hu/RUF/Péld 2,6', 'Így szólt Jézus: „Magasztallak, Atyám, menny és föld Ura, mert elrejtetted ezeket a bölcsek és értelmesek elől, és felfedted az egyszerű embereknek.” (Mt 11,25)', 'Mt 11,25', 'https://szentiras.hu/RUF/Mt 11,25', 'admin');
INSERT INTO readings (reading_year, reading_month, reading_day, firstreading, firstreading_short, firstreading_link, secondreading, secondreading_short, secondreading_link, created_by) VALUES ('2024', '10', '30', '„Megnyitja az Úr előtted gazdag kincsesházát, az eget, és esőt ad földedre, amikor csak kell, és megáldja kezed minden munkáját.” (5Móz 28,12a)', '5Móz 28,12a', 'https://szentiras.hu/RUF/5Móz 28,12a', '„Ezért, szeretett testvéreim, legyetek szilárdak, rendíthetetlenek, buzgólkodjatok mindenkor az Úr munkájában, hiszen tudjátok, hogy fáradozásotok nem hiábavaló az Úrban.” (1Kor 15,58)', '1Kor 15,58', 'https://szentiras.hu/RUF/1Kor 15,58', 'admin');
INSERT INTO readings (reading_year, reading_month, reading_day, firstreading, firstreading_short, firstreading_link, secondreading, secondreading_short, secondreading_link, created_by) VALUES ('2024', '10', '31', '„Ezek a harci kocsikat, amazok a lovakat emlegetik dicsekedve, mi pedig Istenünknek, az Úrnak a nevét.” (Zsolt 20,8)', 'Zsolt 20,8', 'https://szentiras.hu/RUF/Zsolt 20,8', '„És nincsen üdvösség senki másban, mert nem is adatott az embereknek az ég alatt más név, amely által üdvözülhetnénk.” (ApCsel 4,12)', 'ApCsel 4,12', 'https://szentiras.hu/RUF/ApCsel 4,12', 'admin');
INSERT INTO readings (reading_year, reading_month, reading_day, firstreading, firstreading_short, firstreading_link, secondreading, secondreading_short, secondreading_link, created_by) VALUES ('2024', '11', '1', '„Kigyógyítom őket a hűtlenségből, szívből szeretni fogom őket […].” (Hós 14,5a)', 'Hós 14,5a', 'https://szentiras.hu/RUF/Hós 14,5a', '„És ne vígy minket kísértésbe, de szabadíts meg a gonosztól.” (Mt 6,13a)', 'Mt 6,13a', 'https://szentiras.hu/RUF/Mt 6,13a', 'admin');
INSERT INTO readings (reading_year, reading_month, reading_day, firstreading, firstreading_short, firstreading_link, secondreading, secondreading_short, secondreading_link, created_by) VALUES ('2024', '11', '2', '„Az Úr mindenkinek megfizet a maga igazsága és hűsége szerint […].” (1Sám 26,23)', '1Sám 26,23', 'https://szentiras.hu/RUF/1Sám 26,23', '„Egyáltalán ne ítéljetek azért addig, míg el nem jön az Úr. Ő majd megvilágítja a sötétség titkait, és nyilvánvalóvá teszi a szívek szándékait, és akkor mindenki Istentől kapja meg a dicséretet.” (1Kor 4,5)', '1Kor 4,5', 'https://szentiras.hu/RUF/1Kor 4,5', 'admin');
INSERT INTO readings (reading_year, reading_month, reading_day, firstreading, firstreading_short, firstreading_link, secondreading, secondreading_short, secondreading_link, created_by) VALUES ('2024', '11', '3', '„Ha gyarapszik is vagyonotok, ne bizakodjatok el!” (Zsolt 62,11b)', 'Zsolt 62,11b', 'https://szentiras.hu/RUF/Zsolt 62,11b', '„A gazdagok tegyenek jót, legyenek gazdagok a jó cselekedetekben, adakozzanak szívesen, javaikat osszák meg másokkal.” (1Tim 6,18)', '1Tim 6,18', 'https://szentiras.hu/RUF/1Tim 6,18', 'admin');
INSERT INTO readings (reading_year, reading_month, reading_day, firstreading, firstreading_short, firstreading_link, secondreading, secondreading_short, secondreading_link, created_by) VALUES ('2024', '11', '4', '„Halljátok meg mind, ti, népek, figyeljen a föld, és aki rajta él! Mert az én Uram, az Úr tanúskodik ellenetek […].” (Mik 1,2)', 'Mik 1,2', 'https://szentiras.hu/RUF/Mik 1,2', '„Ne tévelyegjetek: Istent nem lehet megcsúfolni. Hiszen amit vet az ember, azt fogja aratni is.” (Gal 6,7)', 'Gal 6,7', 'https://szentiras.hu/RUF/Gal 6,7', 'admin');
INSERT INTO readings (reading_year, reading_month, reading_day, firstreading, firstreading_short, firstreading_link, secondreading, secondreading_short, secondreading_link, created_by) VALUES ('2024', '11', '5', '„Egy szívet adok majd nekik, és új lelket adok beléjük, eltávolítom testükből a kőszívet, és hússzívet adok nekik, hogy rendelkezéseim szerint éljenek, törvényeimet megtartsák és teljesítsék.” (Ez 11,19–20)', 'Ez 11,19–20', 'https://szentiras.hu/RUF/Ez 11,19–20', '„Mikor jön el az Isten országa? […] Az Isten országa közöttetek van!” (Lk 17,20–21)', 'Lk 17,20–21', 'https://szentiras.hu/RUF/Lk 17,20–21', 'admin');
INSERT INTO readings (reading_year, reading_month, reading_day, firstreading, firstreading_short, firstreading_link, secondreading, secondreading_short, secondreading_link, created_by) VALUES ('2024', '11', '6', '„Boldogok, akik házadban laknak, szüntelenül dicsérhetnek téged!” (Zsolt 84,5)', 'Zsolt 84,5', 'https://szentiras.hu/RUF/Zsolt 84,5', '„Ugyanígy segít a Lélek is a mi erőtlenségünkön. Mert amiért imádkoznunk kell, nem tudjuk úgy kérni, ahogyan kell, de maga a Lélek esedezik értünk kimondhatatlan fohászkodásokkal.” (Róm 8,26)', 'Róm 8,26', 'https://szentiras.hu/RUF/Róm 8,26', 'admin');
INSERT INTO readings (reading_year, reading_month, reading_day, firstreading, firstreading_short, firstreading_link, secondreading, secondreading_short, secondreading_link, created_by) VALUES ('2024', '11', '7', '„Segíts meg bennünket, Urunk, Istenünk, mert rád támaszkodunk […].” (2Krón 14,10b)', '2Krón 14,10b', 'https://szentiras.hu/RUF/2Krón 14,10b', '„A reménység hitvallásához szilárdan ragaszkodjunk, mert hű az, aki ígéretet tett.” (Zsid 10,23b)', 'Zsid 10,23b', 'https://szentiras.hu/RUF/Zsid 10,23b', 'admin');
INSERT INTO readings (reading_year, reading_month, reading_day, firstreading, firstreading_short, firstreading_link, secondreading, secondreading_short, secondreading_link, created_by) VALUES ('2024', '11', '8', '„Én, aki igazat beszélek, és van erőm a szabadításhoz!” (Ézs 63,1b)', 'Ézs 63,1b', 'https://szentiras.hu/RUF/Ézs 63,1b', '„Vagy azért vagy irigy, mert én jó vagyok hozzájuk? Így lesznek az utolsókból elsők, és az elsőkből utolsók.” (Mt 20,15b–16)', 'Mt 20,15b–16', 'https://szentiras.hu/RUF/Mt 20,15b–16', 'admin');
INSERT INTO readings (reading_year, reading_month, reading_day, firstreading, firstreading_short, firstreading_link, secondreading, secondreading_short, secondreading_link, created_by) VALUES ('2024', '11', '9', '„Áldjátok az Urat, ti, teremtményei, mindenütt, ahol uralkodik! Áldjad, lelkem, az Urat!” (Zsolt 103,22)', 'Zsolt 103,22', 'https://szentiras.hu/RUF/Zsolt 103,22', '„Előre el is határozta, hogy fiaivá fogad minket Jézus Krisztus által, akarata és tetszése szerint, hogy magasztaljuk dicsőséges kegyelmét, amellyel megajándékozott minket szeretett Fiában.” (Ef 1,5–6)', 'Ef 1,5–6', 'https://szentiras.hu/RUF/Ef 1,5–6', 'admin');
INSERT INTO readings (reading_year, reading_month, reading_day, firstreading, firstreading_short, firstreading_link, secondreading, secondreading_short, secondreading_link, created_by) VALUES ('2024', '11', '10', '„Nagy örömöm telik az Úrban, víg örömre indít Istenem, mert az üdvösség ruhájába öltöztetett, az igazság palástját terítette rám […].” (Ézs 61,10a)', 'Ézs 61,10a', 'https://szentiras.hu/RUF/Ézs 61,10a', '„A világosság gyümölcse ugyanis csupa jóság, igazság és egyenesség.” (Ef 5,9)', 'Ef 5,9', 'https://szentiras.hu/RUF/Ef 5,9', 'admin');
INSERT INTO readings (reading_year, reading_month, reading_day, firstreading, firstreading_short, firstreading_link, secondreading, secondreading_short, secondreading_link, created_by) VALUES ('2024', '11', '11', '„Azután megtérnek Izráel fiai, keresni fogják Istenüket, az Urat […]: remegve fordulnak az Úrhoz, az ő jóságához az utolsó napokban.” (Hós 3,5)', 'Hós 3,5', 'https://szentiras.hu/RUF/Hós 3,5', '„Mert aki kér, mind kap, aki keres, talál, és a zörgetőnek megnyittatik.” (Mt 7,8)', 'Mt 7,8', 'https://szentiras.hu/RUF/Mt 7,8', 'admin');
INSERT INTO readings (reading_year, reading_month, reading_day, firstreading, firstreading_short, firstreading_link, secondreading, secondreading_short, secondreading_link, created_by) VALUES ('2024', '11', '12', '„A megtérés és a higgadtság segítene rajtatok, a béke és a bizalom erőt adna nektek!” (Ézs 30,15b)', 'Ézs 30,15b', 'https://szentiras.hu/RUF/Ézs 30,15b', '„Mert nem beszédben áll az Isten országa, hanem erőben.” (1Kor 4,20)', '1Kor 4,20', 'https://szentiras.hu/RUF/1Kor 4,20', 'admin');
INSERT INTO readings (reading_year, reading_month, reading_day, firstreading, firstreading_short, firstreading_link, secondreading, secondreading_short, secondreading_link, created_by) VALUES ('2024', '11', '13', '„A hősök íja összetörik, de akik a földre rogytak, az erő övét kötik magukra.” (1Sám 2,4)', '1Sám 2,4', 'https://szentiras.hu/RUF/1Sám 2,4', 'Pál írja: „Ezért Krisztusért örömöm telik erőtlenségekben, bántalmazásokban, nyomorúságokban, üldöztetésekben és szorongattatásokban, mert amikor erőtlen vagyok, akkor vagyok erős.” (2Kor 12,10)', '2Kor 12,10', 'https://szentiras.hu/RUF/2Kor 12,10', 'admin');
INSERT INTO readings (reading_year, reading_month, reading_day, firstreading, firstreading_short, firstreading_link, secondreading, secondreading_short, secondreading_link, created_by) VALUES ('2024', '11', '14', '„Íme, az Úré, a te Istenedé az ég és az egeknek egei, a föld és minden, ami rajta van.” (5Móz 10,14)', '5Móz 10,14', 'https://szentiras.hu/RUF/5Móz 10,14', '„Dicsőség a magasságban Istennek, és a földön békesség, és az emberekhez jóakarat.” (Lk 2,14)', 'Lk 2,14', 'https://szentiras.hu/RUF/Lk 2,14', 'admin');
INSERT INTO readings (reading_year, reading_month, reading_day, firstreading, firstreading_short, firstreading_link, secondreading, secondreading_short, secondreading_link, created_by) VALUES ('2024', '11', '15', '„A pusztában is láthattad, hogy egész úton, amelyen jártatok, úgy vitt téged Istened, az Úr, ahogy a fiát viszi az ember, míg el nem érkeztetek erre a helyre.” (5Móz 1,31)', '5Móz 1,31', 'https://szentiras.hu/RUF/5Móz 1,31', '„Erre ők [az emmausi tanítványok] is elbeszélték, ami az úton történt, és hogy miként ismerték fel őt [Jézust] a kenyér megtöréséről.” (Lk 24,35)', 'Lk 24,35', 'https://szentiras.hu/RUF/Lk 24,35', 'admin');
INSERT INTO readings (reading_year, reading_month, reading_day, firstreading, firstreading_short, firstreading_link, secondreading, secondreading_short, secondreading_link, created_by) VALUES ('2024', '11', '16', '„Ez a város [Jeruzsálem] hírnevet, örömöt, dicséretet és tisztességet szerez majd nekem a föld minden népe előtt, ha meghallják, hogy mennyi jót teszek velük.” (Jer 33,9a)', 'Jer 33,9a', 'https://szentiras.hu/RUF/Jer 33,9a', '„Az előtte haladó és az őt követő sokaság pedig ezt kiáltotta: Hozsánna Dávid Fiának! Áldott, aki jön az Úr nevében! Hozsánna a magasságban!” (Mt 21,9)', 'Mt 21,9', 'https://szentiras.hu/RUF/Mt 21,9', 'admin');
INSERT INTO readings (reading_year, reading_month, reading_day, firstreading, firstreading_short, firstreading_link, secondreading, secondreading_short, secondreading_link, created_by) VALUES ('2024', '11', '17', '„Mert csak én tudom, mi a tervem veletek – így szól az Úr –: jólétet és nem romlást tervezek, és reményteljes jövő az, amelyet nektek szánok.” (Jer 29,11)', 'Jer 29,11', 'https://szentiras.hu/RUF/Jer 29,11', '„A teremtett világ maga is meg fog szabadulni a romlandóság szolgaságából Isten gyermekeinek dicsőséges szabadságára.” (Róm 8,21)', 'Róm 8,21', 'https://szentiras.hu/RUF/Róm 8,21', 'admin');
INSERT INTO readings (reading_year, reading_month, reading_day, firstreading, firstreading_short, firstreading_link, secondreading, secondreading_short, secondreading_link, created_by) VALUES ('2024', '11', '18', '„Örülök és vigadok neked, zengem nevedet, ó, Felséges!” (Zsolt 9,3)', 'Zsolt 9,3', 'https://szentiras.hu/RUF/Zsolt 9,3', '„Örüljetek az Úrban mindenkor!” (Fil 4,4)', 'Fil 4,4', 'https://szentiras.hu/RUF/Fil 4,4', 'admin');
INSERT INTO readings (reading_year, reading_month, reading_day, firstreading, firstreading_short, firstreading_link, secondreading, secondreading_short, secondreading_link, created_by) VALUES ('2024', '11', '19', '„Lelkünk megmenekült, mint a madár a madarász tőréből. A tőr összetört, és mi megmenekültünk.” (Zsolt 124,7)', 'Zsolt 124,7', 'https://szentiras.hu/RUF/Zsolt 124,7', '„Azért jöttek, hogy hallgassák őt [Jézust], és meggyógyuljanak betegségeikből. Akiket tisztátalan lelkek gyötörtek, meggyógyultak.” (Lk 6,18)', 'Lk 6,18', 'https://szentiras.hu/RUF/Lk 6,18', 'admin');
INSERT INTO readings (reading_year, reading_month, reading_day, firstreading, firstreading_short, firstreading_link, secondreading, secondreading_short, secondreading_link, created_by) VALUES ('2024', '11', '20', '„Mert kegyelmes és irgalmas ő [az Úr], türelme hosszú, szeretete nagy, és visszavonhatja még a veszedelmet!” (Jóel 2,13)', 'Jóel 2,13', 'https://szentiras.hu/RUF/Jóel 2,13', '„De az [a vincellér] így válaszolt neki [a gazdának]: Uram, hagyd meg még ebben az évben, míg körülásom és megtrágyázom, hátha terem jövőre, ha pedig nem, akkor vágd ki!” (Lk 13,8–9)', 'Lk 13,8–9', 'https://szentiras.hu/RUF/Lk 13,8–9', 'admin');
INSERT INTO readings (reading_year, reading_month, reading_day, firstreading, firstreading_short, firstreading_link, secondreading, secondreading_short, secondreading_link, created_by) VALUES ('2024', '11', '21', '„Aki sötétségben jár, és nem ragyog rá fény, bízzon az Úr nevében, és támaszkodjon Istenére!” (Ézs 50,10b)', 'Ézs 50,10b', 'https://szentiras.hu/RUF/Ézs 50,10b', '„Új parancsolatot írok nektek: azt, ami igaz őbenne és tibennetek, hogy múlik a sötétség, és már fénylik az igazi világosság.” (1Jn 2,8)', '1Jn 2,8', 'https://szentiras.hu/RUF/1Jn 2,8', 'admin');
INSERT INTO readings (reading_year, reading_month, reading_day, firstreading, firstreading_short, firstreading_link, secondreading, secondreading_short, secondreading_link, created_by) VALUES ('2024', '11', '22', '„Taníts engem utaidra, Uram, hogy igazságod szerint járjak, és teljes szívvel féljem nevedet.” (Zsolt 86,11)', 'Zsolt 86,11', 'https://szentiras.hu/RUF/Zsolt 86,11', '„Megismeritek az igazságot, és az igazság megszabadít titeket.” (Jn 8,32)', 'Jn 8,32', 'https://szentiras.hu/RUF/Jn 8,32', 'admin');
INSERT INTO readings (reading_year, reading_month, reading_day, firstreading, firstreading_short, firstreading_link, secondreading, secondreading_short, secondreading_link, created_by) VALUES ('2024', '11', '23', '„…ne legyen [közöttetek] jós, se varázslást űző, jelmagyarázó vagy igéző! Ne legyen átokmondó, se szellemidéző, se jövendőmondó, se halottaktól tudakozódó! Mert utálatos az Úr előtt mindaz, aki ilyet cselekszik […].” (5Móz 18,10.12a)', '5Móz 18,10.12a', 'https://szentiras.hu/RUF/5Móz 18,10.12a', '„Legyetek valóban józanok, és ne vétkezzetek, mert némelyek nem ismerik Istent: megszégyenítésetekre mondom ezt.” (1Kor 15,34)', '1Kor 15,34', 'https://szentiras.hu/RUF/1Kor 15,34', 'admin');
INSERT INTO readings (reading_year, reading_month, reading_day, firstreading, firstreading_short, firstreading_link, secondreading, secondreading_short, secondreading_link, created_by) VALUES ('2024', '11', '24', '„Én népem, mit vétettem ellened, mivel bántottalak? Felelj nekem!” (Mik 6,3)', 'Mik 6,3', 'https://szentiras.hu/RUF/Mik 6,3', '„Vagy megveted jóságának, elnézésének és türelmének gazdagságát, és nem veszed tudomásul, hogy téged Isten jósága megtérésre ösztönöz?” (Róm 2,4)', 'Róm 2,4', 'https://szentiras.hu/RUF/Róm 2,4', 'admin');
INSERT INTO readings (reading_year, reading_month, reading_day, firstreading, firstreading_short, firstreading_link, secondreading, secondreading_short, secondreading_link, created_by) VALUES ('2024', '11', '25', '„Te magad okoztad ezt magadnak, mert elhagytad Istenedet, az Urat, amikor a helyes úton akart vezetni.” (Jer 2,17)', 'Jer 2,17', 'https://szentiras.hu/RUF/Jer 2,17', '„Mert ti, testvéreim, szabadságra vagytok elhívva; csak a szabadság nehogy ürügy legyen a testnek, hanem szeretetben szolgáljatok egymásnak.” (Gal 5,13)', 'Gal 5,13', 'https://szentiras.hu/RUF/Gal 5,13', 'admin');
INSERT INTO readings (reading_year, reading_month, reading_day, firstreading, firstreading_short, firstreading_link, secondreading, secondreading_short, secondreading_link, created_by) VALUES ('2024', '11', '26', '„Bizony, te rejtőzködő Isten vagy, Izráel Istene, szabadító!” (Ézs 45,15)', 'Ézs 45,15', 'https://szentiras.hu/RUF/Ézs 45,15', '„Nekem, minden szent között a legkisebbnek adatott az a kegyelem, hogy a pogányoknak hirdessem Krisztus mérhetetlen gazdagságát, és hogy feltárjam mindenki előtt, mi ama titok megvalósulásának a rendje, amely el volt rejtve öröktől fogva Istenben, mindenek teremtőjében.” (Ef 3,8–9)', 'Ef 3,8–9', 'https://szentiras.hu/RUF/Ef 3,8–9', 'admin');
INSERT INTO readings (reading_year, reading_month, reading_day, firstreading, firstreading_short, firstreading_link, secondreading, secondreading_short, secondreading_link, created_by) VALUES ('2024', '11', '27', '„Uram, te szerzel nekünk békességet, hiszen mindent te tettél, ami velünk történt.” (Ézs 26,12)', 'Ézs 26,12', 'https://szentiras.hu/RUF/Ézs 26,12', 'Pál írja: „Végül, testvéreim, örüljetek, állítsátok helyre a jó rendet magatok között, fogadjátok el az intést, jussatok egyetértésre, éljetek békességben, akkor a szeretet és a békesség Istene veletek lesz.” (2Kor 13,11)', '2Kor 13,11', 'https://szentiras.hu/RUF/2Kor 13,11', 'admin');
INSERT INTO readings (reading_year, reading_month, reading_day, firstreading, firstreading_short, firstreading_link, secondreading, secondreading_short, secondreading_link, created_by) VALUES ('2024', '11', '28', '„Titeket pedig az Úr papjainak neveznek, Istenünk szolgáinak mondanak.” (Ézs 61,6a)', 'Ézs 61,6a', 'https://szentiras.hu/RUF/Ézs 61,6a', '„Úgy tekintsen minket minden ember, mint Krisztus szolgáit és Isten titkainak sáfárait.” (1Kor 4,1)', '1Kor 4,1', 'https://szentiras.hu/RUF/1Kor 4,1', 'admin');
INSERT INTO readings (reading_year, reading_month, reading_day, firstreading, firstreading_short, firstreading_link, secondreading, secondreading_short, secondreading_link, created_by) VALUES ('2024', '11', '29', '„[Uram,] minden gazdagságnál nagyobb öröm, ha intelmeid szerint élhetek.” (Zsolt 119,14)', 'Zsolt 119,14', 'https://szentiras.hu/RUF/Zsolt 119,14', '„Ha parancsolataimat megtartjátok, megmaradtok a szeretetemben, ahogyan én mindig megtartottam az én Atyám parancsolatait, és megmaradok az ő szeretetében. Ezeket azért mondom nektek, hogy az én örömöm legyen bennetek, és örömötök teljes legyen.” (Jn 15,10–11)', 'Jn 15,10–11', 'https://szentiras.hu/RUF/Jn 15,10–11', 'admin');
INSERT INTO readings (reading_year, reading_month, reading_day, firstreading, firstreading_short, firstreading_link, secondreading, secondreading_short, secondreading_link, created_by) VALUES ('2024', '11', '30', '„Szeresd azért az Urat, a te Istenedet teljes szívedből, teljes lelkedből és teljes erődből!” (5Móz 6,5)', '5Móz 6,5', 'https://szentiras.hu/RUF/5Móz 6,5', '„A szeretet nem tesz rosszat a felebarátnak. A szeretet tehát a törvény betöltése.” (Róm 13,10)', 'Róm 13,10', 'https://szentiras.hu/RUF/Róm 13,10', 'admin');
INSERT INTO readings (reading_year, reading_month, reading_day, firstreading, firstreading_short, firstreading_link, secondreading, secondreading_short, secondreading_link, created_by) VALUES ('2024', '12', '1', '„Bárcsak állhatatosan járhatnék utadon, megtartva rendelkezéseidet!” (Zsolt 119,5)', 'Zsolt 119,5', 'https://szentiras.hu/RUF/Zsolt 119,5', 'Keresztelő János mondta: „Térjetek meg, mert elközelített a mennyek országa!” (Mt 3,2)', 'Mt 3,2', 'https://szentiras.hu/RUF/Mt 3,2', 'admin');
INSERT INTO readings (reading_year, reading_month, reading_day, firstreading, firstreading_short, firstreading_link, secondreading, secondreading_short, secondreading_link, created_by) VALUES ('2024', '12', '2', '„Bízzatok az Úrban mindenkor, mert az Úr a mi kősziklánk mindörökre!” (Ézs 26,4)', 'Ézs 26,4', 'https://szentiras.hu/RUF/Ézs 26,4', 'Jézus mondja: „Megmutatom nektek, kihez hasonló az, aki hozzám jön, hallja beszédeimet, és cselekszi azokat. Hasonló ahhoz a házépítő emberhez, aki mélyre ásott, és a kősziklára alapozott […].” (Lk 6,47–48a)', 'Lk 6,47–48a', 'https://szentiras.hu/RUF/Lk 6,47–48a', 'admin');
INSERT INTO readings (reading_year, reading_month, reading_day, firstreading, firstreading_short, firstreading_link, secondreading, secondreading_short, secondreading_link, created_by) VALUES ('2024', '12', '3', 'Sámuel ezt mondta Saulnak: „Akkor megszáll téged is az Úr lelke, velük együtt prófétai révületbe esel majd, és más emberré leszel.” (1Sám 10,6)', '1Sám 10,6', 'https://szentiras.hu/RUF/1Sám 10,6', '„Maga a Lélek tesz bizonyságot a mi lelkünkkel együtt arról, hogy Isten gyermekei vagyunk.” (Róm 8,16)', 'Róm 8,16', 'https://szentiras.hu/RUF/Róm 8,16', 'admin');
INSERT INTO readings (reading_year, reading_month, reading_day, firstreading, firstreading_short, firstreading_link, secondreading, secondreading_short, secondreading_link, created_by) VALUES ('2024', '12', '4', '„Bárcsak figyeltél volna parancsaimra! Akkor folyamként áradna rád a jólét, a tenger hullámaihoz hasonlóan az igazság.” (Ézs 48,18)', 'Ézs 48,18', 'https://szentiras.hu/RUF/Ézs 48,18', '„Akik békességet teremtenek, békességben vetnek, hogy az igazság gyümölcsét arassák.” (Jak 3,18)', 'Jak 3,18', 'https://szentiras.hu/RUF/Jak 3,18', 'admin');
INSERT INTO readings (reading_year, reading_month, reading_day, firstreading, firstreading_short, firstreading_link, secondreading, secondreading_short, secondreading_link, created_by) VALUES ('2024', '12', '5', '„Csak szeressétek az igazságot és a békességet!” (Zak 8,19b)', 'Zak 8,19b', 'https://szentiras.hu/RUF/Zak 8,19b', '„És Isten békessége, mely minden értelmet meghalad, meg fogja őrizni szíveteket és gondolataitokat Krisztus Jézusban.” (Fil 4,7)', 'Fil 4,7', 'https://szentiras.hu/RUF/Fil 4,7', 'admin');
INSERT INTO readings (reading_year, reading_month, reading_day, firstreading, firstreading_short, firstreading_link, secondreading, secondreading_short, secondreading_link, created_by) VALUES ('2024', '12', '6', '„Ó, Uram, Uram! Nagy hatalmaddal és kinyújtott karoddal te alkottad meg az eget és a földet; neked semmi sem lehetetlen!” (Jer 32,17)', 'Jer 32,17', 'https://szentiras.hu/RUF/Jer 32,17', '„Mert úgy szerette Isten a világot, hogy egyszülött Fiát adta, hogy aki hisz őbenne, el ne vesszen, hanem örök élete legyen.” (Jn 3,16)', 'Jn 3,16', 'https://szentiras.hu/RUF/Jn 3,16', 'admin');
INSERT INTO readings (reading_year, reading_month, reading_day, firstreading, firstreading_short, firstreading_link, secondreading, secondreading_short, secondreading_link, created_by) VALUES ('2024', '12', '7', '„De az Úr így szólt [Kainhoz]: Mit tettél? Testvéred kiontott vére hozzám kiált a földből!” (1Móz 4,10)', '1Móz 4,10', 'https://szentiras.hu/RUF/1Móz 4,10', '„Igaz az a beszéd, és teljes elfogadásra méltó, hogy Krisztus Jézus azért jött el a világba, hogy a bűnösöket üdvözítse, akik közül az első én vagyok.” (1Tim 1,15)', '1Tim 1,15', 'https://szentiras.hu/RUF/1Tim 1,15', 'admin');
INSERT INTO readings (reading_year, reading_month, reading_day, firstreading, firstreading_short, firstreading_link, secondreading, secondreading_short, secondreading_link, created_by) VALUES ('2024', '12', '8', '„Vétkeztünk őseinkkel együtt, bűnt, gonoszságot követtünk el.” (Zsolt 106,6)', 'Zsolt 106,6', 'https://szentiras.hu/RUF/Zsolt 106,6', '„Hiszen azt tartjuk, hogy hit által igazul meg az ember, a törvény cselekvésétől függetlenül.” (Róm 3,28)', 'Róm 3,28', 'https://szentiras.hu/RUF/Róm 3,28', 'admin');
INSERT INTO readings (reading_year, reading_month, reading_day, firstreading, firstreading_short, firstreading_link, secondreading, secondreading_short, secondreading_link, created_by) VALUES ('2024', '12', '9', '„Mind örülnek majd, akik hozzád menekülnek.” (Zsolt 5,12a)', 'Zsolt 5,12a', 'https://szentiras.hu/RUF/Zsolt 5,12a', '„Hanem úgy ajánljuk magunkat mindenben, mint Isten szolgái: […] mint szomorkodók, de mindig örvendezők, mint szegények, de sokakat gazdagítók, mint akiknek nincsen semmijük és akiké mégis minden.” (2Kor 6,4a.10)', '2Kor 6,4a.10', 'https://szentiras.hu/RUF/2Kor 6,4a.10', 'admin');
INSERT INTO readings (reading_year, reading_month, reading_day, firstreading, firstreading_short, firstreading_link, secondreading, secondreading_short, secondreading_link, created_by) VALUES ('2024', '12', '10', '„Nem megy le többé a napod, és a holdad sem fogy el, mert az Úr lesz örök világosságod […].” (Ézs 60,20)', 'Ézs 60,20', 'https://szentiras.hu/RUF/Ézs 60,20', '„Isten ugyanis, aki ezt mondta: »Sötétségből világosság ragyogjon fel«, ő gyújtott világosságot szívünkben, hogy felragyogjon előttünk Isten dicsőségének ismerete Krisztus arcán.” (2Kor 4,6)', '2Kor 4,6', 'https://szentiras.hu/RUF/2Kor 4,6', 'admin');
INSERT INTO readings (reading_year, reading_month, reading_day, firstreading, firstreading_short, firstreading_link, secondreading, secondreading_short, secondreading_link, created_by) VALUES ('2024', '12', '11', '„Az igazak várakozása örömre fordul, de a bűnösök reménysége semmivé lesz.” (Péld 10,28)', 'Péld 10,28', 'https://szentiras.hu/RUF/Péld 10,28', '„Ti pedig legyetek hasonlók az olyan emberekhez, akik várják, mikor tér vissza uruk a menyegzőről, hogy amikor megérkezik és zörget, azonnal ajtót nyithassanak neki.” (Lk 12,36)', 'Lk 12,36', 'https://szentiras.hu/RUF/Lk 12,36', 'admin');
INSERT INTO readings (reading_year, reading_month, reading_day, firstreading, firstreading_short, firstreading_link, secondreading, secondreading_short, secondreading_link, created_by) VALUES ('2024', '12', '12', '„[Isten] magasra emeli az alant levőket, a gyászolókat oltalmazza segítsége.” (Jób 5,11)', 'Jób 5,11', 'https://szentiras.hu/RUF/Jób 5,11', '„Azokat választotta ki Isten, akik a világ szemében erőtlenek, hogy megszégyenítse az erőseket” (1Kor 1,27b)', '1Kor 1,27b', 'https://szentiras.hu/RUF/1Kor 1,27b', 'admin');
INSERT INTO readings (reading_year, reading_month, reading_day, firstreading, firstreading_short, firstreading_link, secondreading, secondreading_short, secondreading_link, created_by) VALUES ('2024', '12', '13', '„Ekkor Mózes eltakarta az arcát, mert félt rátekinteni az Istenre.” (2Móz 3,6b)', '2Móz 3,6b', 'https://szentiras.hu/RUF/2Móz 3,6b', '„Ekkor Jézus odament, megérintette őket [a tanítványokat], és így szólt hozzájuk: Keljetek fel, és ne féljetek! Amikor pedig föltekintettek, senkit sem láttak, csak Jézust egyedül.” (Mt 17,7–8)', 'Mt 17,7–8', 'https://szentiras.hu/RUF/Mt 17,7–8', 'admin');
INSERT INTO readings (reading_year, reading_month, reading_day, firstreading, firstreading_short, firstreading_link, secondreading, secondreading_short, secondreading_link, created_by) VALUES ('2024', '12', '14', '„Miért vetetted meg az Úr szavát, miért tettél olyat, ami nem tetszik neki?!” (2Sám 12,9a)', '2Sám 12,9a', 'https://szentiras.hu/RUF/2Sám 12,9a', '„Én nyomorult ember! Kicsoda szabadít meg ebből a halálra ítélt testből? Hála legyen Istennek a mi Urunk Jézus Krisztus által!” (Róm 7,24–25a)', 'Róm 7,24–25a', 'https://szentiras.hu/RUF/Róm 7,24–25a', 'admin');
INSERT INTO readings (reading_year, reading_month, reading_day, firstreading, firstreading_short, firstreading_link, secondreading, secondreading_short, secondreading_link, created_by) VALUES ('2024', '12', '15', '„Ne bízzatok ilyen hazug szavakban: Az Úr temploma, az Úr temploma, az Úr temploma van itt! Mert csak ha igazán megjobbítjátok útjaitokat és tetteiteket […].” (Jer 7,4–5a)', 'Jer 7,4–5a', 'https://szentiras.hu/RUF/Jer 7,4–5a', '„Áldott az Úr, Izráel Istene, hogy meglátogatta és megváltotta az ő népét. […] hogy […] félelem nélkül szolgáljunk neki, szentségben és igazságban őelőtte életünk minden napján.” (Lk 1,68.74b–75)', 'Lk 1,68.74b–75', 'https://szentiras.hu/RUF/Lk 1,68.74b–75', 'admin');
INSERT INTO readings (reading_year, reading_month, reading_day, firstreading, firstreading_short, firstreading_link, secondreading, secondreading_short, secondreading_link, created_by) VALUES ('2024', '12', '16', '„Légy erős kősziklám, ahova mindig fölmehetek, melyet segítségemre rendeltél, mert te vagy az én sziklaváram.” (Zsolt 71,3)', 'Zsolt 71,3', 'https://szentiras.hu/RUF/Zsolt 71,3', '„[Jézus] egy kissé továbbment, arcra borult, és így imádkozott: Atyám, ha lehetséges, távozzék el tőlem ez a pohár; mindazáltal ne úgy legyen, ahogyan én akarom, hanem amint te.” (Mt 26,39)', 'Mt 26,39', 'https://szentiras.hu/RUF/Mt 26,39', 'admin');
INSERT INTO readings (reading_year, reading_month, reading_day, firstreading, firstreading_short, firstreading_link, secondreading, secondreading_short, secondreading_link, created_by) VALUES ('2024', '12', '17', '„Mert diadalra jut még a jog, és azt követi minden tiszta szívű ember.” (Zsolt 94,15)', 'Zsolt 94,15', 'https://szentiras.hu/RUF/Zsolt 94,15', 'Jézus mondja: „Vajon Isten nem szolgáltat-e igazságot választottainak, akik éjjel-nappal kiáltanak hozzá? És várakoztatja-e őket? Mondom nektek, hogy igazságot szolgáltat nekik hamarosan.” (Lk 18,7–8a)', 'Lk 18,7–8a', 'https://szentiras.hu/RUF/Lk 18,7–8a', 'admin');
INSERT INTO readings (reading_year, reading_month, reading_day, firstreading, firstreading_short, firstreading_link, secondreading, secondreading_short, secondreading_link, created_by) VALUES ('2024', '12', '18', '„Így szólt az Úr Abrámhoz látomásban: Ne félj, Abrám! Én vagyok a pajzsod: jutalmad igen bőséges.” (1Móz 15,1)', '1Móz 15,1', 'https://szentiras.hu/RUF/1Móz 15,1', '„És így, miután Ábrahám türelemmel várt, beteljesült az ígéret.” (Zsid 6,15)', 'Zsid 6,15', 'https://szentiras.hu/RUF/Zsid 6,15', 'admin');
INSERT INTO readings (reading_year, reading_month, reading_day, firstreading, firstreading_short, firstreading_link, secondreading, secondreading_short, secondreading_link, created_by) VALUES ('2024', '12', '19', 'Jákób azt mondta Józsefnek: „Eredj és nézd meg, hogy jól vannak-e testvéreid és a nyáj!” (1Móz 37,14a)', '1Móz 37,14a', 'https://szentiras.hu/RUF/1Móz 37,14a', '„Ügyeljünk arra, hogy egymást szeretetre és jó cselekedetre buzdítsuk.” (Zsid 10,24)', 'Zsid 10,24', 'https://szentiras.hu/RUF/Zsid 10,24', 'admin');
INSERT INTO readings (reading_year, reading_month, reading_day, firstreading, firstreading_short, firstreading_link, secondreading, secondreading_short, secondreading_link, created_by) VALUES ('2024', '12', '20', '„Izráelt megszabadítja az Úr örökké tartó szabadítással; nem ér benneteket szégyen és gyalázat soha, soha többé.” (Ézs 45,17)', 'Ézs 45,17', 'https://szentiras.hu/RUF/Ézs 45,17', 'Az angyal ezt mondta Józsefnek: „[Mária] fiút fog szülni, te pedig majd Jézusnak nevezed, mert ő fogja megszabadítani népét bűneiből.” (Mt 1,21)', 'Mt 1,21', 'https://szentiras.hu/RUF/Mt 1,21', 'admin');
INSERT INTO readings (reading_year, reading_month, reading_day, firstreading, firstreading_short, firstreading_link, secondreading, secondreading_short, secondreading_link, created_by) VALUES ('2024', '12', '21', '„Testem és lelkem ujjongva kiált az élő Istenhez.” (Zsolt 84,3b)', 'Zsolt 84,3b', 'https://szentiras.hu/RUF/Zsolt 84,3b', 'Erzsébet ezt mondta Máriának: „Hogyan lehetséges, hogy az én Uram anyja jön el énhozzám? Mert íme, amint meghallottam köszöntésed, ujjongva mozdult meg méhemben a magzat.” (Lk 1,43–44)', 'Lk 1,43–44', 'https://szentiras.hu/RUF/Lk 1,43–44', 'admin');
INSERT INTO readings (reading_year, reading_month, reading_day, firstreading, firstreading_short, firstreading_link, secondreading, secondreading_short, secondreading_link, created_by) VALUES ('2024', '12', '22', '„Mert te vagy, Uram, reménységem, te vagy, Uram, bizodalmam ifjúkorom óta.” (Zsolt 71,5)', 'Zsolt 71,5', 'https://szentiras.hu/RUF/Zsolt 71,5', '„Mert üdvösségünk reménységre szól. Viszont az a reménység, amelyet már látunk, nem is reménység, hiszen amit lát valaki, azt miért kellene remélnie? Ha pedig azt reméljük, amit nem látunk, akkor állhatatossággal várjuk.” (Róm 8,24–25)', 'Róm 8,24–25', 'https://szentiras.hu/RUF/Róm 8,24–25', 'admin');
INSERT INTO readings (reading_year, reading_month, reading_day, firstreading, firstreading_short, firstreading_link, secondreading, secondreading_short, secondreading_link, created_by) VALUES ('2024', '12', '23', '„Mert tudom, hogy hűtlen voltam, és vétkem mindig előttem van.” (Zsolt 51,5)', 'Zsolt 51,5', 'https://szentiras.hu/RUF/Zsolt 51,5', '„Ha megvalljuk bűneinket, hű és igaz ő: megbocsátja bűneinket, és megtisztít minket minden gonoszságtól.” (1Jn 1,9)', '1Jn 1,9', 'https://szentiras.hu/RUF/1Jn 1,9', 'admin');
INSERT INTO readings (reading_year, reading_month, reading_day, firstreading, firstreading_short, firstreading_link, secondreading, secondreading_short, secondreading_link, created_by) VALUES ('2024', '12', '24', '„Mindenki aszerint vigyen ajándékot, amilyen áldást kapott Istenedtől, az Úrtól, aki adja azt neked.” (5Móz 16,17)', '5Móz 16,17', 'https://szentiras.hu/RUF/5Móz 16,17', '„[A bölcsek] kinyitották kincsesládáikat, és ajándékokat adtak neki [a gyermeknek]: aranyat, tömjént és mirhát.” (Mt 2,11)', 'Mt 2,11', 'https://szentiras.hu/RUF/Mt 2,11', 'admin');
INSERT INTO readings (reading_year, reading_month, reading_day, firstreading, firstreading_short, firstreading_link, secondreading, secondreading_short, secondreading_link, created_by) VALUES ('2024', '12', '25', '„Népemmé fogadlak titeket, én pedig Istenetek leszek…” (2Móz 6,7)', '2Móz 6,7', 'https://szentiras.hu/RUF/2Móz 6,7', '„Lássátok meg, milyen nagy szeretetet tanúsított irántunk az Atya: Isten gyermekeinek neveznek minket, és azok is vagyunk.” (1Jn 3,1)', '1Jn 3,1', 'https://szentiras.hu/RUF/1Jn 3,1', 'admin');
INSERT INTO readings (reading_year, reading_month, reading_day, firstreading, firstreading_short, firstreading_link, secondreading, secondreading_short, secondreading_link, created_by) VALUES ('2024', '12', '26', '„Akkor majd megtudják, hogy én, az Úr, az ő Istenük, velük vagyok, és hogy ők, Izráel háza, az én népem – így szól az én Uram, az Úr.” (Ez 34,30)', 'Ez 34,30', 'https://szentiras.hu/RUF/Ez 34,30', '„Üdvözítő született ma nektek, aki az Úr Krisztus, a Dávid városában.” (Lk 2,11)', 'Lk 2,11', 'https://szentiras.hu/RUF/Lk 2,11', 'admin');
INSERT INTO readings (reading_year, reading_month, reading_day, firstreading, firstreading_short, firstreading_link, secondreading, secondreading_short, secondreading_link, created_by) VALUES ('2024', '12', '27', '„Látták a földön mindenütt Istenünk szabadítását.” (Zsolt 98,3b)', 'Zsolt 98,3b', 'https://szentiras.hu/RUF/Zsolt 98,3b', '„Várjuk a mi boldog reménységünket, a mi nagy Istenünk és üdvözítőnk, Jézus Krisztus dicsőségének megjelenését.” (Tit 2,13)', 'Tit 2,13', 'https://szentiras.hu/RUF/Tit 2,13', 'admin');
INSERT INTO readings (reading_year, reading_month, reading_day, firstreading, firstreading_short, firstreading_link, secondreading, secondreading_short, secondreading_link, created_by) VALUES ('2024', '12', '28', '„Aki könyörül a nincstelenen, az Úrnak ad kölcsön, mert ő megtéríti jótéteményét.” (Péld 19,17)', 'Péld 19,17', 'https://szentiras.hu/RUF/Péld 19,17', '„Adjatok, és adatik nektek: jó, megnyomott, megrázott, megtetézett mértékkel adnak öletekbe. Mert amilyen mértékkel ti mértek, olyan mértékkel mérnek viszonzásul nektek” (Lk 6,38)', 'Lk 6,38', 'https://szentiras.hu/RUF/Lk 6,38', 'admin');
INSERT INTO readings (reading_year, reading_month, reading_day, firstreading, firstreading_short, firstreading_link, secondreading, secondreading_short, secondreading_link, created_by) VALUES ('2024', '12', '29', '„Még a veréb is talál házat, és a fecske is fészket, ahova fiókáit helyezi oltáraidnál, Seregek Ura, királyom és Istenem!” (Zsolt 84,4)', 'Zsolt 84,4', 'https://szentiras.hu/RUF/Zsolt 84,4', '„A rókáknak barlangjuk van, és az égi madaraknak fészkük, de az Emberfiának nincs hova fejét lehajtania.” (Mt 8,20)', 'Mt 8,20', 'https://szentiras.hu/RUF/Mt 8,20', 'admin');
INSERT INTO readings (reading_year, reading_month, reading_day, firstreading, firstreading_short, firstreading_link, secondreading, secondreading_short, secondreading_link, created_by) VALUES ('2024', '12', '30', '„Nagy a mi Urunk, és igen erős, bölcsessége határtalan.” (Zsolt 147,5)', 'Zsolt 147,5', 'https://szentiras.hu/RUF/Zsolt 147,5', '„A gyermek pedig növekedett és erősödött, megtelt bölcsességgel, és Isten kegyelme volt rajta.” (Lk 2,40)', 'Lk 2,40', 'https://szentiras.hu/RUF/Lk 2,40', 'admin');
INSERT INTO readings (reading_year, reading_month, reading_day, firstreading, firstreading_short, firstreading_link, secondreading, secondreading_short, secondreading_link, created_by) VALUES ('2024', '12', '31', '„Bizony, itt van Isten, a mi Istenünk örökkön-örökké, ő vezet minket mindhalálig.” (Zsolt 48,15)', 'Zsolt 48,15', 'https://szentiras.hu/RUF/Zsolt 48,15', '„Jézus így válaszolt: Én vagyok az út, az igazság és az élet…” (Jn 14,6)', 'Jn 14,6', 'https://szentiras.hu/RUF/Jn 14,6', 'admin');
